INSERT INTO `namecard` (`id`, `name`, `description`, `source`, `type`) VALUES
(1, 'Genshin Impact - Un monde nouveau', '« Bienvenue en Teyvat ! »', 'Récompense de première connexion', 1),
(2, 'PB - Attrape-vent', 'Le plus difficile quand on écrit un journal de voyage, c\'est la première page.', 'Récompense de Pass de bataille', 2),
(3, 'Amber - Baron Lapinou', '« Baron Lapinou ! »', 'Niveau d\'affinité 10 avec Amber', 3),
(4, 'Klee - Chaud devant !', 'L\'explosif porte-bonheur de Klee. Est-ce une bombe rebondissante, ou Dodoco ? (Astuce : Regardez la queue...)', 'Niveau d\'affinité 10 avec Klee', 3),
(5, 'Diluc - Flambée mortelle', 'Maître Crepus a peint nombre d\'aigles de son vivant, mais jamais il n\'aurait pu imaginer peindre un jour un aigle brûlant d\'un tel feu.', 'Niveau d\'affinité 10 avec Diluc', 3),
(6, 'Razor - Affaire de loups', 'Le repas de Razor et des loups offre une scène... particulière. Mais jamais ils ne laisseront Klee ou le voyageur en être les spectateurs.', 'Niveau d\'affinité 10 avec Razor', 3),
(7, 'Venti - Ciel d\'azur', 'La douce couleur azur d\'un ciel sans nuages et le ton mélodieux de Fehling se complètent de façon parfaite.', 'Niveau d\'affinité 10 avec Venti', 3),
(8, 'Jean - Course du vent', 'Le pissenlit, aussi appelé « dent-de-lion », est l\'une des fleurs les plus sensibles au vent qui existe.', 'Niveau d\'affinité 10 avec Jean', 3),
(9, 'Barbara - Thérapeutique', 'Le chant de Barbara, doux et mélodieux, apaise les esprits et soigne les maux, à la manière de quelque magique source de bienveillance...', 'Niveau d\'affinité 10 avec Barbara', 3),
(10, 'Kaeya - Pavo Ocellus', 'Le paon arbore sur ses plumes des motifs faisant penser à des yeux qui jamais ne se ferment. Peut-on vraiment dire qu\'il manque un œil à Kaeya...?', 'Niveau d\'affinité 10 avec Kaeya', 3),
(11, 'Lisa - Tempus Fugit', 'Le temps est un vrai mystère, en particulier pour Lisa, qui le surveille s\'écouler dans son sablier en attendant la fin de sa journée de travail.', 'Niveau d\'affinité 10 avec Lisa', 3),
(12, 'Sucrose - Bio-alchimie', 'Si vous examinez avec soin une Hypostase Anémo avant qu\'elle ne disparaisse, vous découvrirez que tout cela lui donne un air plutôt perplexe.', 'Niveau d\'affinité 10 avec Sucrose', 3),
(13, 'Fischl - Corbeau de la nuit', 'Un corbeau noir comme la nuit, aux ailes capables de bloquer le soleil. « Qu\'en son honneur soit créé quelque apparat à son image ! »', 'Niveau d\'affinité 10 avec Fischl', 3),
(14, 'Noëlle - Protection', 'Noëlle est quelqu\'un de spécial, et ne laissera jamais aucun mal vous arriver.', 'Niveau d\'affinité 10 avec Noëlle', 3),
(15, 'Mona - Astromancie', 'Contemplant le reflet des étoiles sur la surface de l\'eau la jeune fille médite sur les joies et peines de la vie.', 'Niveau d\'affinité 10 avec Mona', 3),
(16, 'Bennett - Gratitude', 'Un signe de reconnaissance de la part de la Bande à Benny.', 'Niveau d\'affinité 10 avec Bennett', 3),
(17, 'Xiangling - Gooba', 'Petit test : essayez de prononcer aussi vite que possible la phrase suivante :« Gooba, bon bout de gars, gobe le bon goût avec bagou. » (Vous avez essayé, non...?)', 'Niveau d\'affinité 10 avec Xiangling', 3),
(18, 'Xingqiu - Bruine', 'Tombe la pluie, bris d\'arc-en-ciel...', 'Niveau d\'affinité 10 avec Xingqiu', 3),
(19, 'Qiqi - Conservation', 'Que mettre au frigo ? Les herbes de monsieur Baizhu, bien sûr... Le lait de coco... Ah, et n\'oublions pas Hu Tao tant qu\'on y est.', 'Niveau d\'affinité 10 avec Qiqi', 3),
(20, 'Keqing - Lame de foudre', 'C\'était juste une invention permettant d\'estimer les reliefs lors d\'inspections, à la base. Vraiment.', 'Niveau d\'affinité 10 avec Keqing', 3),
(21, 'Succès - Pérégrinations', 'Que tous les endroits visités à travers le continent ne vous fassent pas oublier la personne que vous étiez au départ...', 'Obtention de tous les succès de « Promenade dans le monde mortel - Partie I »', 4),
(22, 'Succès - Virtuose', 'Après avoir maîtrisé ces techniques, vous vous rendez compte qu\'il est possible de s\'en sortir sans combat, si ce n\'est pour une mission à accomplir.', 'Obtention de tous les succès de « L\'art de l\'aventure »', 4),
(23, 'Succès - Héroïque périple', 'Le périple du héros importe autant que sa destination.', 'Obtention de tous les succès de « Voyage du héros »', 4),
(24, 'Mondstadt - Chant du vent', 'Ah, le chant du vent et des oiseaux de Mondstadt...', 'Obtention de tous les succès de « Mondstadt : Cité du vent et de la chanson »', 4),
(25, 'Liyue - Paix de la roche', 'Liyue, ville aux tranquilles nuages et aux mille rochers...', 'Obtention de tous les succès de « Liyue : Port de pierre et des contrats »', 4),
(26, 'Succès - Prisme', 'La lumière du jour peut être fractionnée en une multitude de couleurs ; mais par commodité (ou fainéantise), il fut décidé de n\'en compter que sept. Peut-être en va-t-il de même pour les éléments ?', 'Obtention de tous les succès de « Expert élémentaire - Partie I »', 4),
(27, 'Succès - Archerie', 'Le tir à l\'arc, un art aussi efficace que mortel.', 'Obtention de tous les succès de « Dans le mille »', 4),
(28, 'Succès - Défi', 'Des défis se dresseront toujours devant vous, même quand vous souhaitez accomplir les tâches les plus simples. Ce n\'est jamais de refus avec vous...', 'Obtention de tous les succès de « Au défi ! - Partie I »', 4),
(29, 'Succès - Secrets profonds', 'Plus l\'on s\'enfonce dans les profondeurs du monde, et plus l\'on apprend de secrets.', 'Obtention de tous les succès de « Donjons et Profondeurs spiralées - Partie I »', 4),
(30, 'Succès - Brutaux des collines', 'Les Brutocollinus étaient à l\'origine appelés « gobelins des collines ». Une certaine jeune fille appelée Ella s\'obstinait-elle à leur donner le nom de « citoyens des collines ».', 'Obtention de tous les succès de « Olah ! - Partie I »', 4),
(31, 'Succès - Pays de la neige', 'Vous avez entendu nombre de choses au sujet du royaume de Snezhnaya. Comment l\'imaginez-vous ?', 'Obtention de tous les succès de « On ne pleure pas à Snezhnaya - Partie I »', 4),
(32, 'Succès - Suspense', 'Les conteurs aiment bien entretenir le suspense, mais vous connaissez déjà la suite de l\'histoire.', 'Obtention de tous les succès de « Nostalgie du Port de pierre - Partie I »', 4),
(33, 'Succès - Rencontre', '« Nos chemins se croiseront de nouveau, un jour. »', 'Obtention de tous les succès de « Rencontres dans un autre monde - Partie I »', 4),
(34, 'Ningguang - Phénix', 'On dit souvent : « Les phénix sont extrêmement rares dans le monde », mais plus sérieusement, une seule Ningguang suffit...', 'Niveau d\'affinité 10 avec Ningguang', 3),
(35, 'Beidou - Levée d\'ancre', '« Si vous vous perdez en pleine mer, cherchez l\'étoile polaire depuis la Grande Ourse dans le ciel nocturne pour retrouver le chemin de retour. »', 'Niveau d\'affinité 10 avec Beidou', 3),
(36, 'Chongyun - Lame de l\'esprit', 'Il s\'agissait juste au départ de créer une épée en pêcher. Mais une fois terminée, l\'arme obtenue s\'est avérée beaucoup plus efficace que le bois de pêcher pour procéder aux exorcismes, il faut le reconnaître.', 'Niveau d\'affinité 10 avec Chongyun', 3),
(37, 'Succès - Défi (2)', 'Et ce qui attend à la fin du défi est...', 'Obtention de tous les succès de « Au défi ! - Partie II »', 4),
(38, 'PB - Météorite', 'Tant que le désir habitera votre cœur, vous verrez là-haut l\'éclat sans lumière.', 'Récompense de Pass de bataille', 2),
(39, 'Diona - Miaou !', 'Tous ceux qui ont du sang de Katzlein présentent les mêmes caractéristiques félines. Peut-être le résultat d\'une ancienne blague ayant mal tourné ?', 'Niveau d\'affinité 10 avec Diona', 3),
(40, 'Zhongli - Chute de météore', 'Les étoiles chutent, la lumière faiblit.', 'Niveau d\'affinité 10 avec Zhongli', 3),
(41, 'Xinyan - Final', 'Xinyan doit attendre la fin de chaque concert pour pouvoir se plonger dans la contemplation de son instrument. Même silencieux, c\'est un plaisir pour les sens.', 'Niveau d\'affinité 10 avec Xinyan', 3),
(42, 'Tartaglia - Posture du démon', 'Ce thème porte les marques du jeune et fier guerrier qui promène son style de combat toujours changeant à travers le continent. Mais sa « Posture du démon » n\'est pas une technique de sa création : elle lui fut enseignée par une fille solitaire qui vit dans les plus sombres recoins de l\'univers...', 'Niveau d\'affinité 10 avec Tartaglia', 3),
(43, 'Mondstadt - Célébrité', 'Le nom du chevalier honoraire résonne dans chaque recoin de la cité.', 'Niv. de réputation 4 à Mondstadt', 5),
(44, 'Mondstadt - Vœux', 'Ceux qui ont à cœur d\'aider les autres seront aidés à leur tour.', 'Niv. de réputation 7 à Mondstadt', 5),
(45, 'Liyue - Navires', 'Les navires vont et viennent, innombrables.', 'Niv. de réputation 4 à Liyue', 5),
(46, 'Liyue - Dans les nuages', 'Les pics ornent la lune nuageuse.', 'Niv. de réputation 7 à Liyue', 5),
(47, 'Succès - Défi (3)', 'Enfin un défi de taille. Ce combat restera dans les annales.', 'Obtention de tous les succès de « Au défi ! - Partie III »', 4),
(48, 'Succès - Sommet enneigé', 'La route qui mène au sommet est semée d\'embûches. Ne laissez pas votre courage s\'amenuiser sur le chemin.', 'Obtention de tous les succès de « Visite à Dosdragon »', 4),
(49, 'Ganyu - Qilin', 'Respectez la nature.', 'Niveau d\'affinité 10 avec Ganyu', 3),
(50, 'Albedo - Radiance solaire', 'Quel privilège d\'assister à ce miracle.', 'Niveau d\'affinité 10 avec Albedo', 3),
(51, 'PB - Craie', 'Équipez-vous chaudement avant de poser le pied sur cette montagne.', 'Récompense de Pass de bataille', 2),
(52, 'Permagel - Arbre ancien', 'De l\'ancien arbre poussent de nouvelles branches.', 'Niv. d\'offrande 9 à l\'Arbre permagelé', 1),
(53, 'Célébration - Hypostase', 'Mon adversaire d\'aujourd\'hui semble avoir un tempérament artistique.', 'Événement « Symphonie Hypostase »', 6),
(54, 'Xiao - Masque', 'Il attend avec impatience le jour où il pourra porter le masque et danser non pas pour conquérir des démons mais au son de cette fameuse flûte, dans un océan de fleurs.', 'Niveau d\'affinité 10 avec Xiao', 3),
(55, 'Hu Tao - Pacificatrice', 'La directrice Hu Tao ne fait pas peur du tout. N\'est-ce pas ?', 'Niveau d\'affinité 10 avec Hu Tao', 3),
(56, 'PB - Mingxiao', 'Lorsque tous les souhaits se rassemblent, alors ils illuminent la nuit.', 'Récompense de Pass de bataille', 2),
(57, 'Célébration - Lanternes', 'Tous les ans au même jour, les lanternes s\'élèvent dans la nuit. Que la flamme ne s\'éteigne jamais et que l\'humanité soit pérenne.', 'Boutique d\'événement du Festival des lanternes', 6),
(58, 'Célébration - Échecs', 'Vous pensez tout savoir de la stratégie des mécanismes mais avez-vous réalisé que vous étiez vous-même un pion ?', 'Obtenu lors de l\'événement « Théâtre mécanique »', 6),
(59, 'Rosalia - Éternel labeur', 'La bienveillance et la gentillesse désintéressées sont des qualités qui, selon elles, se doivent d\'être compensées par un éternel labeur.', 'Niveau d\'affinité 10 avec Rosalia', 3),
(60, 'PB - Alizée', 'Personne ne voit le vent, jusqu\'à ce qu\'il vous souffle au visage. Il en va de même pour l\'amour. Personne ne le voit, jusqu\'au jour où on se retrouve avec un bouquet entre les mains.', 'Récompense de Pass de bataille', 2),
(61, 'Célébration - Festin', 'Excellent !', 'Événement « Cadeaux d\'un autre monde »', 6),
(62, 'Célébration - Écaille brillante', 'L\'univers abrite un serpent, dont les écailles scintillent précisément de ces couleurs.', 'Boutique d\'événement', 6),
(63, 'Célébration - Fruit de la sagesse', 'Quelle connaissance le fruit de la sagesse apporterait-il s\'il descendait sur terre ? Quoi qu\'il en soit, il aura bon goût entre les mains expertes de Diona.', 'Événement « Amplificateur d\'énergie »', 6),
(64, 'Célébration - Coucou !', 'On ne me voit pas, on ne me voit pas.', 'Événement « Poursuite du vent »', 6),
(65, 'Yanfei - Impartialité', 'La justice est signée d\'écarlate !', 'Niveau d\'affinité 10 avec Yanfei', 3),
(66, 'Eula - Sceau de glace', 'Un cœur froid n\'est qu\'une coquille pour se protéger.', 'Niveau d\'affinité 10 avec Eula', 3),
(67, 'PB - Autre monde', 'Combien de nuits avez-vous passées avec une pierre pour oreiller ?', 'Récompense de Pass de bataille', 2),
(68, 'Succès - Heure du thé', 'Les jours et les mois défilent dans la théière.', 'Obtention de tous les succès de « Plus qu\'un royaume - Partie I »', 4),
(69, 'Kazuha - Chasse en montagne d\'automne', '« Le chant du pin résonne pendant que les feuilles rouges frissonnent. »', 'Niveau d\'affinité 10 avec Kazuha', 3),
(70, 'PB - Été', 'C\'est l\'heure de partir à l\'aventure.', 'Récompense de Pass de bataille', 2),
(71, 'Succès - Maison de vacances', 'C\'est un vaste monde sous les combles.', 'Obtention de tous les succès de « Plus qu\'un royaume - Partie II »', 4),
(72, 'Célébration - Immensité', 'Imaginez la vaste étendue d\'une mer d\'étoiles. Imaginez un 1 suivi de centaines de zéros.', 'Boutique d\'événement', 6),
(73, 'Célébration - Badaboum', '« Il était une fois dans un pays très, très lointain un royaume connu sous le nom de la Grande ligue. Les gens qui vivaient là-bas pouvaient lancer toutes sortes de balles badaboum rapides et puissantes. »', 'Événement « Combadaboum »', 4),
(74, 'Célébration - Chant guerrier', 'Les bons présages doivent apparaître trois fois pour assurer la bonne fortune, le vrai triomphe n\'est remporté qu\'après trois victoires et il convient de s\'incliner trois fois après une prestation inoubliable.', 'Événement « Légende martiale du vent »', 6),
(75, 'Inazuma - Cerisier sacré', 'Partout où tombent les pétales de cerisier, repose la protection de l\'arbre sacré.', 'Niv.48 de faveur du cerisier sacré', 1),
(76, 'PB - Sanctuaire de Narukami', 'Le cerisier du Mont Yougou symbolise le Sanctuaire de Narukami.', 'Récompense de Pass de bataille', 2),
(77, 'Kamisato Ayaka - Éventail', 'Ayaka change constamment d\'éventail. Si vous voulez en donner en cadeau, évitez un éventail d\'été ou un éventail de lancer. Les éventails de danse ou pour le thé sont de bons choix cependant.', 'Niveau d\'affinité 10 avec Kamisato Ayaka', 3),
(78, 'Yoimiya - Étincelle dorée', 'Même des étincelles momentanées peuvent laisser des flammes inextinguibles dans le cœur de ceux qui regardent le ciel nocturne.', 'Niveau d\'affinité 10 avec Yoimiya', 3),
(79, 'Sayu - Daruma Muji-muji', 'Bien qu\'il soit plus petit que Sayu, il l\'aide à faire les choses qu\'elle ne veut pas faire.', 'Niveau d\'affinité 10 avec Sayu', 3),
(80, 'Inazuma - Emblème de Raiden', 'Ceci est l\'emblème de la Shogun Ogosho et la bannière de ses troupes, le Mitsudomoe Électro.', 'Niv. de réputation 7 à Inazuma', 5),
(81, 'Inazuma - Armoiries des Kamisato', 'Ce blason représente le Clan Kamisato de la Commission culturelle.', 'Niv. de réputation 4 à Inazuma', 5),
(82, 'Succès - Porte', 'Quelles rencontres se trouvent de l\'autre côté de cette porte ?', 'Obtention de tous les succès de « Rencontres dans un autre monde - Partie II »', 4),
(83, 'Succès - Traversée', 'Un voyageur connaîtra des milliers d\'histoires, et chacune d\'elle fera partie de son âme.', 'Obtention de tous les succès de « Promenade dans le monde mortel - Partie II »', 4),
(84, 'Succès - Dragon de pierre', 'Notre voyage au milieu des précipices.', 'Obtention de tous les succès de « Au défi ! - Partie IV »', 4),
(85, 'Succès - Pleine floraison', 'Rassemblez la terre des quatre coins de l\'univers, et d\'elle pourront pousser d\'innombrables fleurs.', 'Obtention de tous les succès de « Plus qu\'un royaume - Partie III »', 4),
(86, 'Inazuma - Insigne de Kujou', 'Cet insigne représente le Clan Kujou de la Commission administrative.', 'Obtention de tous les succès de « Inazuma : Archipel de la foudre éternelle - I »', 4),
(87, 'Célébration - Ingéniosité', 'Un vrai grand maître peut prévoir mille coups à l\'avance.', 'Événement « Théâtre mécanique - Scène des merveilles »', 6),
(88, 'Shogun Raiden - Révélation', 'Je ne suis pas seulement Ei, ni simplement la Shogun Raiden. Observez d\'un œil tempétueux et vous trouverez votre éternité.', 'Niveau d\'affinité 10 avec la Shogun Raiden', 3),
(89, 'Sangonomiya Kokomi - Profondeurs', 'Tous les habitants de l\'Île de Watatsumi connaissent cette légende : la demeure de leurs ancêtres s\'étend dans les profondeurs marines.', 'Niveau d\'affinité 10 avec Sangonomiya Kokomi', 3),
(90, 'Kujou Sara - Tengu', 'Kujou Sara a renoncé à l\'arrogance qui caractérise les Tengu, mais a acquis leur maîtrise de l\'épée et des pouvoirs surnaturels.', 'Niveau d\'affinité 10 avec Kujou Sara', 3),
(91, 'PB - Désilune', 'Que le clair de lune vous illumine.', 'Récompense de Pass de bataille', 2),
(92, 'Inazuma - Emblème de Sangonomiya', 'Cet emblème représente le Clan Sangonomiya dirigeant l\'Île de Watatsumi.', 'Obtention de tous les succès de « Inazuma : Archipel de la foudre éternelle - II »', 4),
(93, 'Succès - Clous', '« Si on ne peut pas manger ces poissons, alors pourquoi s\'embêter à accrocher ces clous aux hameçons ? » « Tu ne comprendrais pas si je t\'expliquais. »', 'Obtention de tous les succès de « Guide de pêche en Teyvat - Partie I »', 4),
(94, 'Célébration - Délice mélodieux', 'Étoiles et montagnes, écoutez attentivement ma musique !', '', 1),
(95, 'Célébration - Ikki', '« Je vais vous poser une question et vous me répondrez avec votre épée. »', 'Événement « Hyakunin Ikki »', 6),
(96, 'Thomas - Brassards ardents', 'Armure flamboyante, brassard enflammé.', 'Niveau d\'affinité 10 avec Thomas', 3),
(97, 'PB - Général céleste', '« Ce n\'est point pour punir le mal que tu as été créé. Mais quel est ton but... Je crains de ne pas le savoir non plus. »', 'Récompense de Pass de bataille', 2),
(98, 'Inazuma - Plume d\'aigle', 'Pirka chikappo ! Kapatcir kamuy !', 'Obtention de tous les succès de « Chroniques de la mer de brouillard »', 4),
(99, 'Gorou - Chien serein', 'Tant qu\'on croit au chemin sur lequel on s\'est engagé, alors tout ce qu\'on rencontre au cours de son périple est source de bonheur.', 'Niveau d\'affinité 10 avec Gorou', 3),
(100, 'Itto - Visage d\'Oni', 'Les vrais hommes savent mettre le chagrin et la colère derrière eux et sourient au monde !', 'Niveau d\'affinité 10 avec Itto', 3),
(101, 'PB - Lyratum', 'Seule cette fleur subsiste sur les hauteurs où tous les arbres ont flétri.', 'Récompense de Pass de bataille', 2),
(102, 'Shenhe - Peigne', 'Avoir des cheveux blancs à un jeune âge montre que vos préoccupations sont nombreuses. Les mots « peigner » et « répandre » se prononçant de manière similaire dans leur langue, les Liyuéens considèrent que se peigner les cheveux équivaut à répandre leurs inquiétudes.', 'Niveau d\'affinité 10 avec Shenhe', 3),
(103, 'Yun Jin - Rime', 'Maître Yun a deux sortes de professeurs : ceux qui critiquent sa performance sur scène et les livres de rimes qu\'elle consulte lors de la rédaction d\'une pièce.', 'Niveau d\'affinité 10 avec Yun Jin', 3),
(104, 'Inazuma - Tokoyo', '« Ô puissant soleil ! À quoi servent ta chaleur et ta lumière si tu ne peux pas obtenir tout ce que tu illumines ? »', 'Obtention de tous les succès de « À la lumière du jour »', 4),
(105, 'PB - Teinte fluide', 'Les couleurs vives qui passent se transforment en mille images', 'Récompense de Pass de bataille', 2),
(106, 'Célébration - Enivrement', 'Il y a beaucoup d\'histoires ici, mais pas une goutte d\'alcool. Paimon peut donc boire sans souci.', 'Événement « Une gorgée d\'un rêve d\'ivresse »', 6),
(107, 'Yae Miko - Kitsune rêveuse', 'Où peut-on apercevoir la forme de renard de Yae Miko ? Dans vos rêves, évidemment.', 'Niveau d\'affinité 10 avec Yae Miko', 3),
(108, 'PB - Éveil', 'Les Trois Royaumes tremblent, alors que le ciel et la terre s\'éveillent. Les sangocorex, eux aussi, se relèvent de leur sommeil éternel.', 'Récompense de Pass de bataille', 2),
(109, 'Kamisato Ayato - Ondulation', 'Sur la surface de l\'étang le camélia se pose, et mille ondulations à travers les eaux se composent', 'Niveau d\'affinité 10 avec Kamisato Ayato', 3),
(110, 'Lumen - Pierre de lumière', 'Que coule dans ce cristal azuréen sans nom et sans voix ? Les secrets des cieux ? Ou les murmures de l\'abîme ?', 'Niv.10 d\'Adjuvant de pierre de lumen', 1),
(111, 'Succès - Coup de tonnerre', 'Écoutez le son qui vient des nuages. C\'est une rage rancunière, et l\'expression d\'un souhait.', 'Obtention de tous les succès de « Au défi ! - Partie V »', 4),
(112, 'Succès - Gouffre', 'Là où autrefois les mineurs creusaient assidûment des tunnels ne reste que le silence. Seuls les cristaux émaillés et les moelles pierreuses, tous deux millénaires, dégagent encore un faible éclat.', 'Obtention de tous les succès de « Gouffre éclairé »', 4),
(113, 'PB - Irodori', 'Ceux qui partagent un loisir sont connus comme un groupe de fans. Dépêchez-vous d\'inventer ces histoires avant que la responsable éditoriale Kitsune n\'invente une règle sur les droits d\'auteur... Ou peut-être qu\'elle y a déjà pensé, mais que cela ne le dérange pas du tout ?', 'Récompense de Pass de bataille', 2),
(114, 'Yelan - Jet', '« Il n\'est pas facile de fabriquer des dés qui donnent des chances égales aux joueurs. Sur les dés de mauvaise facture, les faces ont des poids différents. »', 'Niveau d\'affinité 10 avec Yelan', 3),
(115, 'Kuki Shinobu - Tissage', '« Vous voyez ces anneaux d\'herbe à sanglots qu\'ils ont à Inazuma ? Vous savez, ceux qui permettent de chasser les mauvais esprits... Shinobu a pensé que puisque les créatures maléfiques ne franchissent pas ces cercles de leur propre chef, on pourrait les utiliser pour faire des objets. Ah, son talent de prêtresse, c\'est effrayant... »', 'Niveau d\'affinité 10 avec Kuki Shinobu', 3),
(116, 'PB - Destinée', '« Les âmes loyales de jadis se dispersèrent telles des oies sauvages s\'envolant vers l\'est et l\'ouest. Aujourd\'hui, j\'éclaire ces esprits héroïques avec ma lanterne afin qu\'ils ne perdent pas leur chemin. »', 'Récompense de Pass de bataille', 2),
(117, 'Shikanoin Heizou - Temari', 'Un temari constitué de nombreux fils de couleur entremêlés. La confection de cet objet a sans doute été compliquée... pour que Heizou s\'amuse à les démêler.', 'Niveau d\'affinité 10 avec Shikanoin Heizou', 3),
(118, 'PB - Azur marin', 'Vous vous réveillez de votre rêve après des années passées. L\'océan de cet été demeurera dans cette coquille souvenir.', 'Récompense de Pass de bataille', 2),
(119, 'Tighnari - Baigné de rosée', 'Tighnari entretient la fourrure de sa queue grâce à une huile essentielle à l\'extrait de fleurs soigneusement conçue par ses soins. Un parfum léger et apaisant s\'en dégage.', 'Niveau d\'affinité 10 avec Tighnari', 3),
(120, 'Collei - Vertu', '« C—C\'est Cuilein-Anbar ! Hum... »', 'Niveau d\'affinité 10 avec Collei', 3),
(121, 'Dori - Lampe magique', 'Si vous frottez la lampe qu\'elle vous a offerte et les moras que vous lui donnez, tous vos vœux se réaliseront... Pas vrai ?', 'Niveau d\'affinité 10 avec Dori', 3),
(122, 'PB - Fond des bois', 'Des roses s\'épanouissent dans la vallée sous la lune tranquille. C\'est un paradis en votre présence.', 'Récompense de Pass de bataille', 2),
(123, 'Succès - Errance', 'Chaque voyage et chaque souvenir est un miracle unique qui n\'appartient qu\'au voyageur.', 'Obtention de tous les succès de « Promenade dans le monde mortel - Partie III »', 4),
(124, 'Succès - Destin', 'Un jour, notre rencontre brillera telle une étoile de la réminiscence dans nos cœurs.', 'Obtention de tous les succès de « Rencontres dans un autre monde - Partie III »', 4),
(125, 'Sumeru - Bois scintillants', 'Quels rêves se trouvent-ils dans le sommeil des contes enfouis dans les profondeurs de la forêt ?', 'Obtention de tous les succès de « Sumeru : Selve de connaissances »', 4),
(126, 'Sumeru - Observance', 'Admirez tout ce que le monde a à vous offrir à travers la lumière des cieux.', 'Niv. de réputation 4 à Sumeru', 5),
(127, 'Sumeru - Rectitude', 'Les sages illuminés par la connaissance de la vérité ne seront pas souillés par le péché. À l\'instar du nilotpalotus, ils demeureront purs après vents et pluies.', 'Niv. de réputation 7 à Sumeru', 5),
(128, 'PB - Vent de retour', 'Les années passent, et seuls le souvenir et les vents de retour ne changent pas.', 'Récompense de Pass de bataille', 2),
(129, 'Cyno - Sérénité', 'Chaque fois que Cyno tombe dans un silence serein, il envisage un jugement de poids... Ou peut-être envisage-t-il simplement quelque chose d\'amusant...', 'Niveau d\'affinité 10 avec Cyno', 3),
(130, 'Candace - Héronne cendrée', 'Les vents brûlants du passé ébouriffent les plumes de la héronne cendrée, mais l\'oiseau n\'a d\'yeux que pour l\'étoile du matin aux premières lueurs de la journée.', 'Niveau d\'affinité 10 avec Candace', 3),
(131, 'Nilou - Danse du lotus', 'Le lotus qui fleurit au milieu de l\'eau pure n\'est rien d\'autre qu\'un éclair de lumière sous ses orteils.', 'Niveau d\'affinité 10 avec Nilou', 3),
(132, 'Succès - Sept lumières', 'N\'importe qui peut jouer un air qui n\'appartient qu\'à lui et qui, comme la rosée sur l\'herbe, peut refléter les sept lumières du ciel.', 'Obtention de tous les succès de « Expert élémentaire - Partie II »', 4),
(133, 'Sumeru - Marche-sable', 'Les sables avalent les traces des voyageurs, et ils attendent le jour où une rafale révélera à nouveau des souvenirs dorés.', 'Obtention de tous les succès de « Sumeru : Désert doré - I »', 4),
(134, 'Succès - Rêves reflétés', '« Un millénaire d\'obsession et de nostalgie tragique s\'est perdu depuis longtemps dans les tempêtes de sable de ces nuits flamboyantes. Mais l\'éternelle oasis verdoyante de ses rêves est, selon toute vraisemblance, aussi douce qu\'elle l\'était au premier jour... »', 'Obtention de tous les succès de « Au défi ! - Partie VI »', 4),
(135, 'Nahida - Croissant', 'La lune montante admire le soleil, car sa lumière illumine le monde, tandis que le soleil couchant envie la lune, car elle est toujours accompagnée par les plus beaux des rêves.', 'Niveau d\'affinité 10 avec Nahida', 3),
(136, 'Layla - Rêverie', '« Le ciel étoilé de tes rêves illuminera le chemin si le cristal de ton cœur reflète une lumière pure. »', 'Niveau d\'affinité 10 avec Layla', 3),
(137, 'PB - Fongisme', '« ... Voyageur, nul besoin d\'être triste. À l\'aube, un nouveau vent soufflera les spores qui transportent de merveilleux rêves vers tes terres d\'espoir... »', 'Récompense de Pass de bataille', 2),
(138, 'Nomade - Ballade d\'errance', 'Le passé se cache dans un monde où l\'éphémère règne et la neige fine épouse la forme des fleurs.', 'Niveau d\'affinité 10 avec le Nomade', 3),
(139, 'Faruzan - Secret scellé', '« B—Bon, il faut m\'appeler «madame » la prochaine fois que vous me posez une question ! »', 'Niveau d\'affinité 10 avec Faruzan', 3),
(140, 'Succès - Invocateur', '« Personne ne m\'arrive à la cheville dans ce village ! »', 'Obtention de tous les succès de « Invocation des Sept : JCC »', 4),
(141, 'PB - Rivières stellaires', 'Le nom du festin tomba dans l\'oubli, les trois rivières se dispersèrent dans les nuages de fumée et l\'automne demeura à la courbe du crépuscule.', 'Récompense de Pass de bataille', 2),
(142, 'Alhaitham - Érudition', '« Les seules choses auxquelles les chercheurs doivent se conformer sont les débats et la dialectique. Ce n\'est qu\'en remettant tout en question à tout moment que l\'on peut éviter de tomber dans les préjugés. »', 'Niveau d\'affinité 10 avec Alhaitham', 3),
(143, 'Yaoyao - Yuegui', '« Pouvez-vous deviner lequel est mon Yuegui ? Eh oui, ils le sont tous ! »', 'Niveau d\'affinité 10 avec Yaoyao', 3),
(144, 'Célébration - Nuage lenticulaire', 'Les nuages flottants se dispersaient vers les mille sommets à l\'aube.', 'Boutique d\'événement', 6),
(145, 'PB - Rime', 'Sous de magnifiques rayons écarlates éclairant le ciel nocturne, des carillons sont orchestrés pour célébrer l\'année écoulée. Les palais dorés sont ornés de rideaux de perles, les balustrades peintes en argent par le clair de lune et les fêtes sont remplies de chants et de danses joyeux. Dans les feux d\'artifice, une richesse d\'instruments joue à l\'unisson, harmonieusement.', 'Récompense de Pass de bataille', 2),
(146, 'Sumeru - Tempête de sable', 'Dans l\'interminable tempête de sable se cachent des rêves anciens...', 'Obtention de tous les succès de « Sumeru : Désert doré - II »', 4),
(147, 'Dehya - Feu purifiant', 'Ceux qui doivent être brûlés seront réduits en cendres dans le vent, afin que toutes les choses précieuses qui doivent être préservées puissent être révélées.', 'Niveau d\'affinité 10 avec Dehya', 3),
(148, 'Mika - Index', 'Chaque fois qu\'il tient la carte qu\'il a illustrée dans ses mains, le garçon a l\'impression de tenir une petite reproduction de ce monde.', 'Niveau d\'affinité 10 avec Mika', 3),
(149, 'PB - Débrouillardise', 'La rumeur dit que le véritable amour est comme un fantôme, quelque chose dont tout le monde parle mais que peu ont vu. Cela dit, puisque même les fantômes peuvent apparaître ici, peut-être que le véritable amour attend aussi au prochain coin de rue...', 'Récompense de Pass de bataille', 2),
(150, 'Baizhu - Soulagement', '« La sincérité engendre la longévité. »', 'Niveau d\'affinité 10 avec Baizhu', 3),
(151, 'Kaveh - Firmament', '« Lors de la conceptualisation d\'une architecture, il n\'y a rien de mal à imaginer que la structure surmontera le firmament et survivra aux étoiles. »', 'Niveau d\'affinité 10 avec Kaveh', 3),
(152, 'Succès - Myriade', '« Oh, myriade née de la khvarena, protégez cette terre en péril. Prospérité et décadence ne sont que des illusions du monde terrestre, issues de la présence des graines. »', 'Obtention de tous les succès de « Hamada bénie »', 4),
(153, 'Succès - Crépuscule éphémère', '« J\'oublierai les fleurs d\'antan, la lumière du jour, la pluie. Je vous demande de vous souvenir à ma place, dans ce monde naissant, et de vous accrocher à ce rêve perdu.»', 'Obtention de tous les succès de « Au défi ! - Partie VII »', 4),
(154, 'Sumeru - Grand rêve', '« Le temps de s\'épanouir n\'est pas encore venu. Cache-moi avec soin entre les brins d\'herbe et sous la hauteur des arbres, et laisse-moi m\'attarder dans ce lieu de rêve. »', 'Niv.48 de l\'arbre des rêves', 1),
(155, 'Sumeru - Amrita', 'L\'ancien Bassin d\'amrita est empreint de nostalgie des temps passés. Peut-être contient-il aussi l\'énergie qui créera l\'avenir...', 'Après 6 offrandes au Bassin d\'amrita', 1),
(156, 'PB - Lauriers', '« La connaissance sert de couronne de laurier du temps, et la réminiscence est liée au temps que nous avons partagé en tant qu\'un. »', 'Récompense de Pass de bataille', 2),
(157, 'Kirara - Neko-colis', '« Pour Kirara, les sourires des clients sont les meilleures récompenses possibles. »', 'Niveau d\'affinité 10 avec Kirara', 3),
(158, 'PB - Secret d\'invoqueur', 'N\'oubliez jamais le temps passé avec des jeux et de bons amis, et n\'oubliez jamais la sensation de ne pas s\'inquiéter de l\'avenir.', 'Récompense de Pass de bataille', 2),
(159, 'PB - Enluminure', 'Peut-être que la lumière onirique qui n\'existe que de l\'autre côté de ce verre transparent peut un tant soit peu éclairer le monde de ce côté.', 'Récompense de Pass de bataille', 2),
(160, 'Lyney - Simulacre', 'L\'un des principaux types de magie consiste à faire « disparaître » les choses, et Lyney a un arsenal de tours dans sa manche. Étant son acolyte, tant que vous êtes d\'accord pour fermer les yeux, tous vos problèmes peuvent également disparaître.', 'Niveau d\'affinité 10 avec Lyney', 3),
(161, 'Lynette - Intégrité', 'L\'un des principaux types de magie consiste à faire « apparaître » les choses. Bien que Lynette conserve généralement son énergie, son côté sérieux peut « apparaître » aussi soudainement que les colombes d\'un chapeau.', 'Niveau d\'affinité 10 avec Lynette', 3),
(162, 'Fréminet - Progrès', 'Alors que les appareils horlogoïdes ne font que tourner en rond, les poupées avancent.', 'Niveau d\'affinité 10 avec Fréminet', 3),
(163, 'Fontaine - Jugement', 'L\'insigne du nouvel Archon Hydro. Nul besoin de mention légale, il est entendu que personne n\'ose l\'utiliser sans autorisation.', 'Obtention de tous les succès de « Fontaine - Danse de la rosée blanche (1) »', 4),
(164, 'Succès - Chemins', '« Ô souverain tout-puissant, l\'Universitas Magistrorum a fourni les prédictions que vous avez demandées : les deux étoiles ont été capturées par la gravité du monde ; après une longue période orbitale, aujourd\'hui est le jour où leurs chemins s\'entrelaceront à nouveau. »', 'Obtention de tous les succès de « Rencontres dans un autre monde - Partie IV »', 4),
(165, 'Succès - Vogue', 'Le voyageur touche dix mille ruisseaux qui coulent tous de la même fontaine des eaux du monde, et tous retournent à la même mer.', 'Obtention de tous les succès de « Promenade dans le monde mortel - Partie IV »', 4),
(166, 'Fontaine - Technologie', 'Le symbole des modules d\'énergie utilisés par tous les Mékas horlogoïdes de Fontaine. Il y avait autrefois un grand débat sur la nécessité de l\'enregistrer en tant que marque, mais au final, le public a été urgé de ne pas l\'utiliser trop librement.', 'Niv. de réputation 4 à Fontaine', 5),
(167, 'Fontaine - Actualités', 'Le logo protégé de L\'Oiseau de vapeur. Si vous vous faites prendre en train de l\'utiliser sans autorisation préalable, nous ferons valoir nos droits et poursuivrons dans toute la mesure permise par la loi !', 'Niv. de réputation 8 à Fontaine', 5),
(168, 'PB - Source des eaux', 'Les garde-temps accompagnent le passage tranquille des années, tout comme les poissons accompagnent les tourbillons des eaux claires des profondeurs. Si tout ce qui est doit un jour se faner, pourquoi mes pensées sont-elles aussi claires que jamais ?', 'Récompense de Pass de bataille', 2),
(169, 'Neuvillette - Afflux', 'La surface de l\'eau peut sembler placide comme un joyau immaculé, mais elle peut être en train d\'attendre que le moment de jaillir vienne.', 'Niveau d\'affinité 10 avec Neuvillette', 3),
(170, 'Wriothesley - Détention', 'Maintenir l\'ordre à la Forteresse de Méropide n\'est pas une mince affaire. Ainsi, une main habile et parfois ferme est nécessaire... avec une petite pincée de cruauté légèrement regrettable.', 'Niveau d\'affinité 10 avec Wriothesley', 3),
(171, 'PB - Étoile filante', 'Faites un vœu avant de lancer votre fléchette. Si vous parvenez à la mettre dans le pot, qui sait, votre souhait pourrait bien se réaliser...', 'Récompense de Pass de bataille', 2),
(172, 'Célébration - Euphonie', 'La pluie passe et les arbres de la montagne chantent.', 'Événement « GENSHIN CONCERT »', 6),
(173, 'Succès - Perpétuité', '« Personne n\'entend le rêve qui s\'est brisé entre les conques et les engrenages. Seul l\'infatigable danseur mécanique interroge encore les échos des jours et des nuits passés à chaque pas élégant.»', 'Obtention de tous les succès de « Au défi ! - Partie VIII »', 4),
(174, 'Fontaine - Expiation', 'Le logo officiel de la Forteresse de Méropide. C\'est notre espoir le plus cher que tous ceux qui souhaitent honorer l\'ordre et vivre leur vie en paix ne soient jamais associés à l\'organisation que représente cet insigne.', 'Obtention de tous les succès de « Fontaine - Danse de la rosée blanche (2) »', 4),
(175, 'Furina', '', '', 3),
(176, 'Charlotte', NULL, NULL, 3),
(177, 'Fontaine - Exhaustivité', '« Les nuages du soir se dispersent dans le ciel et le ruisseau brille d\'or sous le soleil couchant. Mon ombre erre seule, semblable à une apparition solitaire au pied d\'une cascade. »', 'Obtention de tous les succès de « Fontaine - Danse de la rosée blanche (3) »', 4),
(178, 'PB - Valse tournoyante', 'Même si les mécanismes horlogoïdes s\'arrêtent de tourner, même si les marionnettes cessent d\'avancer... Même si nos souvenirs restent éternellement figés comme du corail... N\'oublions pas nos aventures et le temps que nous avons passé ensemble.', 'Récompense de Pass de bataille', 2);
