INSERT INTO `material` (`id`, `name`, `type`, `description`, `rarity`, `source`) VALUES
(1, 'EXP d\'aventure', 'Objet de déverrouillage de système', 'L\'EXP d\'aventure permet d\'augmenter le Niv. d\'aventure.', 3, 'Récompense'),
(2, 'EXP d\'affinité', 'Augmente l\'affinité', 'L\'EXP d\'affinité permet d\'augmenter le niveau d\'affinité.', 3, 'Récompense'),
(3, 'Résine originelle', 'Objet de calcul du résultat du défi', 'Peut être utilisée pour activer des Arbres pétrifiés et des Fleurs des lignes énergétiques et en obtenir ainsi les récompenses.\r\nLa légende raconte que les racines des anciens arbres pétrifiés et les Fleurs des lignes énergétiques s\'entrelacent dans les profondeurs de la terre. Leurs connexions dessinent les trajectoires que prennent les lignes énergétiques.', 3, NULL),
(4, 'Primo-gemme', 'Devise spéciale', 'Une gemme cristalline originelle qui brille grâce à la condensation des espoirs et des rêves des univers d\'autrefois.', 5, NULL),
(5, 'Mora', 'Devise commune', 'La devise commune de Teyvat. S\'il y a bien un mot que tout le monde comprend, c\'est celui-là.', 3, NULL),
(6, 'Cristal primaire', 'Devise spéciale', 'Un cristal infusé de l\'énergie qui créa l\'univers. Formé par l\'espoir le plus pur, il renferme des possibilités infinies et contient assez d\'énergie pour faire naître une nouvelle étoile.', 5, NULL),
(7, 'Pièces du royaume', 'Devise spéciale', 'Monnaie du royaume utilisée dans la Sérénithéière en échange de nombreux objets.', 3, NULL),
(8, 'Astérie libre', 'Coupon d\'échange avancé', 'Une monnaie qui peut être échangée contre des ressources précieuses dans la boutique.\r\nL\'étincelle d\'étoile du destin excédentaire. Peut-être qu\'elle peut illuminer d\'autres coins de l\'univers.', 5, 'Vœux'),
(9, 'Astrion libre', 'Coupon d\'échange commun', 'Une monnaie qui peut être échangée contre des ressources précieuses dans la boutique.\r\nLa poussière d\'étoile du destin. Peut-être qu\'elle peut créer de nouveaux destins en l\'accumulant.', 4, 'Vœux'),
(10, 'Pierre de la fatalité', 'Objet de vœux exclusif', 'Une graine du destin qui relie les rêves.\r\nLa lueur de la pierre de la fatalité peut faire s\'entrelacer les sorts qui ne devraient pas se croiser. C\'est grâce à cette lueur que les étoiles peuvent former l\'image de votre cœur.', 5, NULL),
(11, 'Pierre de la destinée', 'Objet de vœux', 'Une graine d\'espoir qui illumine le ciel étoilé.\r\nPeu importe la distance qui les sépare l\'un de l\'autre, ceux qui sont destinés à se rencontrer seront guidés par la lueur de la pierre de la fatalité et se retrouveront enfin sous le ciel étoilé.', 5, NULL),
(12, 'Sceau Anémo', 'Sceau des sept cités-états', 'Un insigne montrant la reconnaissance des vents de Mondstadt.\r\nS\'échange contre des objets spéciaux dans un lieu spécifique de Mondstadt.', 3, 'Exploration de Mondstadt'),
(13, 'Sceau Géo', 'Sceau des sept cités-états', 'Un insigne reconnu par le rocher de Liyue.\r\nS\'échange contre des objets spéciaux dans un lieu spécifique de Liyue.', 3, 'Exploration de Liyue'),
(14, 'Sceau Électro', 'Sceau des sept cités-états', 'Un sceau qui sert de signe de reconnaissance donné dans les îles tonitruantes d\'Inazuma.\r\nOffrez-les au cerisier sacré du Sanctuaire de Narukami pour augmenter le niveau de faveur du cerisier sacré.', 3, 'Exploration d\'Inazuma'),
(15, 'Sceau Dendro', 'Sceau des sept cités-états', 'Un insigne qui témoigne de la reconnaissance des arbres et des plantes de Sumeru.\r\nPrésentez-le devant l\'arbre des rêves situé au fond des bois pour recevoir la récompense de la forêt.', 3, 'Exploration de Sumeru'),
(16, 'Sceau Hydro', 'Sceau des sept cités-états', 'Un sceau qui sert de signe de reconnaissance des eaux à Fontaine.\r\nOffrez-les à la Fontaine de Lucine pour recevoir les guerdons de la source.', 3, 'Exploration de Fontaine'),
(17, 'Sceau Pyro', 'Sceau des sept cités-états', 'Marque de reconnaissance de la part de Mondstadt que vous pouvez échanger contre des objets rares chez certains marchands de la cité.', 3, 'Exploration de Natlan'),
(18, 'Sceau Cryo', 'Sceau des sept cités-états', 'Marque de reconnaissance de la part de Mondstadt que vous pouvez échanger contre des objets rares chez certains marchands de la cité.', 3, 'Exploration de Snezhnaya'),
(19, 'Champignon', 'Ingrédient', 'Un mycète qui pousse dans tous les endroits humides et à l\'abri du soleil.', 1, 'Dans la nature'),
(20, 'Fleur sucrante', 'Ingrédient', 'Une plante au parfum riche qui vous guidera à elle, même les yeux fermés.', 1, 'Dans la nature'),
(21, 'Carotte', 'Ingrédient', 'Un légume sain et nourrissant. Facile à cultiver et à récolter.', 1, 'Dans la nature'),
(22, 'Radis', 'Ingrédient', 'Un légume riche en fibres et en nutriments. Facile à cultiver et à récolter.', 1, 'Dans la nature'),
(23, 'Gueule-de-loup', 'Ingrédient', 'Une fleur comestible après cuisson. Assaisonnez vos plats avec, ils n\'en seront que meilleurs.', 1, 'Berges des rivières et des lacs'),
(24, 'Menthe', 'Ingrédient', 'Une plante dont les feuilles rafraîchissent le palais, même lors des étés les plus chauds.', 1, 'Dans la nature'),
(25, 'Brin de blé', 'Ingrédient', 'Céréale dorée par le soleil qui donne de l\'espoir et qui remplit la panse. Il s\'agit d\'un ingrédient de base dans une multitude de plats.', 1, 'Dans la nature'),
(26, 'Chou', 'Ingrédient', 'Un légume à feuilles. D\'après certains, il s\'agissait d\'une plante décorative autrefois. En tout cas, sa meilleure place est dans la casserole.', 1, 'Dans la nature'),
(27, 'Pomme de pin', 'Ingrédient', 'Une graine riche en huile. Ce n\'est pas dans l\'estomac d\'un écureuil qu\'elle deviendra un arbre, c\'est sûr.', 1, 'Dans la nature'),
(28, 'Baie à crochet', 'Produit de Mondstadt', 'Une baie à crochet qui s\'attache souvent aux poils des loups. Si vous la regardez, vous pouvez presque entendre les hurlements des loups dans les bois.', 1, 'Zone conseillée : Territoire des Loups'),
(29, 'Tombaie', 'Produit de Mondstadt', 'Une baie juteuse laissant un goût sucré et rafraîchissant en bouche. Dans le passé, ce fruit offrait quelque réconfort à ceux qui surveillaient les tempêtes.', 1, 'Zone conseillée : Montagnes du guet'),
(30, 'Cécilia', 'Produit de Mondstadt', 'Une belle fleur qui porte un nom aussi beau que son apparence, et que l\'on trouve seulement où soufflent les vents violents, et est aussi intangible que le cœur d\'un vagabond.', 1, 'Zone conseillée : Falaise Arrachétoile'),
(31, 'Chrysanthème à aubes', 'Produit de Mondstadt', 'Une plante qui aime le vent. Pour les habitants de Mondstadt qui vénèrent le vent et la liberté, les chrysanthèmes à aubes sont les « vents visibles ».', 1, 'Zone conseillée : Antre de Stormterror'),
(32, 'Champignon anémophile', 'Produit de Mondstadt', 'Un champignon qui grandit sous les caresses de la brise. Il prospère comme le vent qui souffle toujours, nourrissant la vie.', 1, 'Sous les corniches'),
(33, 'Fruit du lotus', 'Ingrédient', 'Une plante aquatique originaire de Liyue qui pousse par deux. L\'une d\'elle dégage un doux parfum floral, tandis que l\'autre porte un fruit amer. On dit que c\'est une bonne plante médicinale.', 1, 'Berges des rivières et des lacs'),
(34, 'Piment de Jueyun', 'Produit de Liyue', 'Une plante épicée originaire de Liyue. La sentir suffit à faire monter en flèche votre température interne et à vous assoiffer.', 1, 'Zone conseillée : Village de Qingce'),
(35, 'Jade noctiluque', 'Produit de Liyue', 'Un minerai rare qui brille dans l\'obscurité. On dit qu\'il s\'agit du fruit de la condensation des éléments fluctuants entre le ciel et la terre.', 1, 'Zone conseillée : Village Mingyun'),
(36, 'Fleur de soie', 'Produit de Liyue', 'Une plante qui fleurit à la manière des nuages arc-en-ciel de Liyue. Elle peut être utilisée pour fabriquer des tissus extrêmement lisses et soyeux.', 1, 'Zone conseillée : Port de Liyue'),
(37, 'Lys verni', 'Produit de Liyue', 'Une fleur qui pousse à Liyue depuis les temps anciens. Elle rassemble les souvenirs de la terre pour les libérer sous forme de parfum à sa fleuraison.', 1, 'Zone conseillée : Terrasse Yujing, Port de Liyue'),
(38, 'Fleur de Qingxin', 'Produit de Liyue', 'Une fleur blanche translucide qui ne pousse que sur les sommets des montagnes les plus hautes. Au lieu de grandir dans les plaines riches et humides, elle choisit de regarder au loin depuis les sommets des montagnes les plus solitaires.', 1, 'Zone conseillée : Forêt de pierre de Guyun'),
(39, 'Queue de cheval', 'Matériau', 'Une plante violette qui pousse parfois au milieu des herbes dans les marais, les dépassant en taille et produisant plus de graines, un peu à la façon de quelque noble parmi la plèbe.', 1, 'Zone conseillée : Marais Dihua'),
(40, 'Conque d\'étoile', 'Produit de Liyue', 'Un coquillage creux échoué sur la plage. Plaquez-le contre votre oreille et vous entendrez l\'appel de la mer.', 1, 'Plages de Liyue'),
(41, 'Muguet bleu', 'Produit de Liyue', 'Une petite fleur qui possède une forte vitalité. On dit qu\'elle fleurit vers le bas pour que son parfum dure plus longtemps.', 1, 'Montagnes de Liyue'),
(42, 'Baie', 'Ingrédient', 'Un petit fruit coloré qu\'on trouve un peu partout. Efficace lorsqu\'on a un petit coup de barre.', 1, 'Dans la nature'),
(43, 'Corolle de fleur de brume', 'Matériau', 'La partie supérieure d\'une fleur de brume prise dans la glace. Elle continue d\'émettre de l\'air froid après cueillette.', 1, 'Zone conseillée : Domaine de l\'Aurore'),
(44, 'Pistil de fleur enflammée', 'Matériau', 'Un pistil de fleur enflammée. Il émet de la chaleur même éteint.', 1, 'Zone conseillée : Temple des Mille Vents'),
(45, 'Électrocristal', 'Matériau', 'Un cristal qui puise son énergie électrique dans l\'air. Il peut engourdir votre main si vous le tenez trop longtemps.', 1, 'Zone conseillée : Cap de la Promesse'),
(46, 'Herbe à lampe', 'Produit de Mondstadt', 'Une plante sauvage qui produit de la lumière une fois la nuit tombée.', 1, 'Zone conseillée : Bois des Murmures'),
(47, 'Lys calla', 'Produit de Mondstadt', 'Une fleur qui pousse près des points d\'eaux. Ses pétales dodus ont un goût sucré et amer lorsqu\'ils sont cuisinés.', 1, 'Zone conseillée : Deauclaire'),
(48, 'Graines de pissenlit', 'Produit de Mondstadt', 'Des graines qui volent au gré du vent. Même dépourvues d\'aigrettes, elles désirent être emportées très loin.', 1, 'Zone conseillée : Portes de Mondstadt'),
(49, 'Cœur de lapis', 'Produit de Liyue', 'Un cristal précieux condensé par l\'élément Géo pur qui se développe souvent avec d\'autres minéraux. On l\'appelle aussi « cœur de rocher ».', 1, 'Zone conseillée : Mont Hulao'),
(50, 'Viande crue', 'Ingrédient', 'Un morceau de viande fraîche qui peut être cuisiné.', 1, 'Butin d\'animal'),
(51, 'Œuf d\'oiseau', 'Ingrédient', 'Cet œuf renfermait une infinité de possibilités, mais dans son état actuel, il constituera votre apport quotidien de protéines.', 1, 'Dans la nature'),
(52, 'Matsutake', 'Ingrédient', 'Un mycète qui pousse sur ou près des pins. On peut le trouver facilement grâce à son odeur particulière.', 1, 'Dans la nature'),
(53, 'Volaille', 'Ingrédient', 'Un morceau de viande fraîche qui peut être cuisiné.', 1, 'Butin d\'animal'),
(54, 'Ailes de papillon', 'Matériau', 'Une jolie paire d\'ailes colorées. La poudre scintillante qui en tombe est peut-être ce qui permet aux papillons de voler ?', 1, 'Dans la nature'),
(57, 'Crabe', 'Ingrédient', 'Un petit crustacé qui vit près de la mer. Sa chair tendre et délicieuse peut être utilisée en cuisine.', 1, 'Plages, berges des rivières et des lacs'),
(58, 'Œufs de crabe', 'Ingrédient', 'Obtenus sur des crabes femelles, ces œufs sont des ingrédients rares qui ne sont malheureusement pas au goût de tout le monde.', 1, 'Cuisine (préparation)'),
(59, 'Sel', 'Ingrédient', 'Une pincée suffit pour rehausser le goût de vos plats.', 1, 'Épiceries'),
(60, 'Oignon', 'Ingrédient', 'Un légume rond qui fait pleurer tout le monde, même les durs à cuire.', 1, 'Épiceries'),
(61, 'Poivre', 'Ingrédient', 'Cet assaisonnement pique légèrement vos papilles et fait des merveilles sur de la viande.', 1, 'Épiceries'),
(62, 'Lait', 'Ingrédient', 'Du lait de vache riche et frais. N\'en perdez pas une goutte, même pas celle qui coule sur votre menton.', 1, 'Épiceries'),
(63, 'Tomate', 'Ingrédient', 'Cette tomate charnue et juteuse possède un goût acidulé. On ne sait toujours pas si c\'est un fruit ou un légume.', 1, 'Épiceries'),
(64, 'Pomme de terre', 'Ingrédient', 'Un légume-racine dont on ne se lasse jamais grâce aux mille et une façons de le cuisiner.', 1, 'Épiceries'),
(65, 'Grenouille', 'Matériau', 'Un amphibien commun des environnements humides. Cet animal plein d\'énergie sécrète une substance revigorante lorsqu\'elle est ingérée par des humains.', 1, 'Cours d\'eau douce'),
(66, 'Abdomen luminescent', 'Matériau', 'Partie émettrice de lumière chez une luciole qui permet de séduire en période de reproduction. Son ingestion améliore la force chez les humains.', 1, 'Dans la nature'),
(67, 'Queue de lézard', 'Matériau', 'Une queue de lézard enroulée sur elle-même. Possède un goût amer, mais favorise efficacement le durcissement cutané.', 1, 'Butin de lézard'),
(68, 'Poisson', 'Ingrédient', 'Un filet de poisson frais. Bien préparé, on peut en faire un plat aussi riche que savoureux.', 1, 'Butin de poisson'),
(69, 'Noyau cristallin', 'Matériau', 'Noyau d\'un papillon cristallin. Il absorbe l\'énergie élémentaire présente dans l\'air.', 1, 'Butin de papillon cristallin'),
(70, 'Tofu', 'Ingrédient', 'Un produit de soja frais. Il a un goût délicat et une texture très douce.', 1, 'Épiceries'),
(71, 'Amande', 'Ingrédient', 'Une graine avec un arôme spécial qui peut donner une toute nouvelle saveur fraîche et différente aux plats.', 1, 'Épiceries'),
(72, 'Perle de loche', 'Matériau', 'Une perle brillante crachée par une loche qui a une vertu médicinale magique.\r\nLes minéraux et les éléments ingérés par la loche se condensent en perles possédant un faible pouvoir magique. En cas d\'urgence, la loche crache ces perles pour confondre les chasseurs et se protéger.', 1, 'Zone conseillée : Marais Dihua'),
(73, 'Pousse de bambou', 'Ingrédient', 'Une pousse de bambou qui vient tout juste de naître. Elle dégage un parfum particulier et ne peut qu\'être trouvée dans les forêts de bambou.', 1, 'Zone conseillée : Forêt de bambou du Village de Qingce'),
(74, 'Riz', 'Ingrédient', 'Une céréale commune en Teyvat. Son goût et sa texture sont appréciés des masses.', 1, 'Épiceries'),
(75, 'Crevette', 'Ingrédient', 'Une crevette des fonds marins, délicieuse et pleine de nutriments.', 1, 'Épiceries'),
(76, 'Viande congelée', 'Ingrédient', 'Morceau de viande aussi rare que délicieux.\r\nD\'après les annales, cette viande provient d\'un type de sanglier disparu depuis plusieurs siècles ; les conditions climatiques extrêmes des Monts Dosdragon ont permis sa conservation. Aux yeux des amateurs de bonne chère de Mondstadt, il s\'agit pour ainsi dire d\'un mets sans égal.', 1, 'Butin d\'animal'),
(77, 'Anguille', 'Ingrédient', 'Un morceau de chair d\'unagi qui une fois cuisiné offre une saveur inimitable.', 1, 'Zone conseillée : Île de Watatsumi'),
(78, 'Haricot glabre', 'Ingrédient', 'Un légume commun en Teyvat qui dégage un parfum léger. Il se cuisine de diverses manières et son utilisation est donc répandue dans le monde entier.', 1, 'Restaurants de Sumeru'),
(79, 'Viande mystérieuse', 'Ingrédient', 'Un morceau de viande fraîche qui émet une odeur mystérieuse. Quel goût peut-il bien avoir ?', 1, 'Butin d\'animal'),
(80, 'Grain de café', 'Ingrédient', 'Un fruit commun en Teyvat, dont l\'arôme est particulier. Il peut être utilisé pour faire des boissons après avoir été moulu.', 1, 'Café de Fontaine'),
(81, 'Jus fermenté', 'Ingrédient', 'Un jus fermenté au parfum fruité unique, souvent utilisé comme marinade.', 1, 'Source\r\nÉpiceries de Fontaine'),
(82, 'Sang sombre', 'Objet de quête', 'Une substance que vous avez obtenue après la défaite de Dvalin.\r\nElle possède la forme d\'un cristal imbibé du pouvoir des vents anciens et d\'un autre pouvoir inconnu qui habitait le corps du dragon.\r\nCe dernier était prisonnier d\'une cage toxique constituée de trois éléments :\r\nLe sang empoisonné qu\'il avait autrefois ingéré, les mensonges de l\'Ordre de l\'Abîme et la peur de l\'abandon.\r\nEn entendant les paroles d\'un certain barde, Dvalin versa des larmes qui brisèrent les caillots de sang impur qui avaient poussé sur son corps, et se libéra de l\'emprise maléfique à laquelle il était soumis.\r\nCet exploit fut possible grâce à un voyageur venant d\'une contrée lointaine...', 5, ''),
(83, 'Fragment de monolithe', 'Objet de quête', 'Fragment récupéré après avoir vaincu le Dieu du Vortex.\r\nDans le passé, ce type de fragment était utilisé pour contenir la puissance d\'Ortell.\r\n\r\nIl fut un temps où les habitants de ces terres étaient terrorisés par des vagues aussi hautes que des montagnes, incapables de trouver le sommeil dans la nuit soumise à l\'assaut des flots rageurs faisant trembler le monde. Ces peurs finirent par disparaître avec le temps et le retour de la paix.\r\nMais au fond, ceci importe peu. Car quand bien même l\'ancien dieu gisant dans les profondeurs se réveillerait à nouveau, il ne pourrait pas changer le cours de l\'histoire et lutter contre la naissance d\'une nouvelle ère. Peut-être est-ce justement là ce dont souhaite être le témoin celui qui vint à bout du Dieu du Vortex autrefois.', 5, ''),
(84, 'Dent étrange', 'Matériau', 'Une étrange dent recueillie aux Monts Dosdragon. Peut-être que quelqu\'un en sait plus à son sujet...', 1, NULL),
(85, 'Croc de dragon infusé de vitalité', 'Matériau', 'Un croc de dragon infusé de force vitale. Sa brillance et sa texture suffisent à éveiller l\'inquiétude de celui qui l\'observe...', 1, NULL),
(86, 'Masque funéraire', 'Objet de quête', 'Un trophée obtenu en vainquant Signora, la 8e Exécutrice des Fatui.\r\nLes membres de Fatui vivent souvent leurs vies à travers leurs masques. Après avoir rejoint l\'organisation, ils doivent abandonner leur passé et leur individualité, renonçant ainsi à leurs noms et à leurs visages.\r\n\r\nCependant, ces actions restent difficiles à accomplir.\r\nSeuls ceux qui portent en eux une obsession qui frôle ou transcende le délire sont disposés à rejoindre cette organisation qui défie l\'ordre divin, laissant un œil maléfique consumer les jours qu\'ils leur restent dans ce monde en échange d\'un immense pouvoir.\r\nÉvidemment, cela n\'exclut pas la présence de personnes ayant soif de pouvoir ou qui aiment tout simplement faire du mal à autrui chez les Fatui.\r\n\r\nRosalyne-Kruzchka Lohefalter mena jadis une vie simple et innocente.\r\nCependant, cette dernière prit fin lorsque ses yeux se remplirent d\'une rage folle. La glace froide de son œil maléfique éteignit les flammes qui la dévoraient, mais celles-ci resurgirent lorsqu\'un étranger éternel à la volonté aussi inébranlable que la sienne brisa le pouvoir du froid qui la protégeait...', 5, NULL),
(87, 'Morceau de fer', 'Minerai de forge', 'Un morceau de fer que les mains expertes d\'un bon forgeron peuvent transformer en objet.', 1, 'Dans la nature'),
(88, 'Morceau de fer blanc', 'Minerai de forge', 'Un morceau de fer blanc que les mains expertes d\'un bon forgeron peuvent transformer en objet.', 1, 'Zone conseillée : Territoire des Loups'),
(89, 'Morceau de cristal', 'Minerai de forge', 'Cristal brut. Seul un professionnel pourra faire ressortir sa vraie valeur.', 1, 'Zone conseillée : Antre de Stormterror'),
(90, 'Éclat de cristal magique', 'Minerai de forge', 'Ces éclats peuvent être raffinés en EXP d\'arme avec de la Résine originelle.\r\nLe travail de ces cristaux, formés sous l\'influence des lignes énergétiques, requiert un grand savoir-faire.', 1, 'Dans la nature'),
(91, 'Minerai d\'argétoile', 'Minerai de forge', 'Ce minerai précieux est le produit des lignes énergétiques et de l\'environnement particulier des Monts Dosdragon.', 1, 'Zone conseillée : Monts Dosdragon'),
(92, 'Morceau d\'améthyste', 'Minerai de forge', 'Un cristal non taillé qui ne peut être trouvé qu\'à Inazuma. Un certain savoir-faire professionnel fera ressortir sa véritable valeur.', 1, 'Zone conseillée : Île de Narukami'),
(93, 'Cristal de condensat', 'Minerai de forge', 'Un cristal brillant et clair, dont le noyau est à la fois dur et malléable.', 1, 'Zone conseillée : À la surface d\'Elynas'),
(94, 'Base d\'épée du nord', 'Matériau', 'Un matériau qui peut être forgé en épée à une main 4★.\r\nD\'après les philosophes, le marbre a le potentiel de donner naissance à une belle sculpture. Ce matériau possède également des possibilités infinies qui n\'attendent que vous pour être explorées.', 4, 'Récompense de Donjon de la conquête et du Loup du Nord à Mondstadt, Liyue et Inazuma'),
(95, 'Base d\'arc du nord', 'Matériau', 'Un matériau qui peut être forgé en arc 4★.\r\nD\'après les philosophes, le marbre a le potentiel de donner naissance à une belle sculpture. Ce matériau possède également des possibilités infinies qui n\'attendent que vous pour être explorées.', 4, 'Récompense de Donjon de la conquête et du Loup du Nord à Mondstadt, Liyue et Inazuma'),
(96, 'Base d\'épée à deux mains du nord', 'Matériau', 'Un matériau qui peut être forgé en épée à deux mains 4★.\r\nD\'après les philosophes, le marbre a le potentiel de donner naissance à une belle sculpture. Ce matériau possède également des possibilités infinies qui n\'attendent que vous pour être explorées.', 4, 'Récompense de Donjon de la conquête et du Loup du Nord à Mondstadt, Liyue et Inazuma'),
(97, 'Base de catalyseur du nord', 'Matériau', 'Un matériau qui peut être forgé en catalyseur 4★.\r\nD\'après les philosophes, le marbre a le potentiel de donner naissance à une belle sculpture. Ce matériau possède également des possibilités infinies qui n\'attendent que vous pour être explorées.', 4, 'Récompense de Donjon de la conquête et du Loup du Nord à Mondstadt, Liyue et Inazuma'),
(98, 'Base de lance du nord', 'Matériau', 'Un matériau qui peut être forgé en arme d\'hast 4★.\r\nD\'après les philosophes, le marbre a le potentiel de donner naissance à une belle sculpture. Ce matériau possède également des possibilités infinies qui n\'attendent que vous pour être explorées.', 4, 'Récompense de Donjon de la conquête et du Loup du Nord à Mondstadt, Liyue et Inazuma'),
(99, 'Base d\'épée du centre', 'Matériau', 'Un matériau qui peut être forgé en épée à une main 4★.\r\nD\'après les philosophes, toutes les choses du monde ont quatre raisons d\'être. Le but de ce matériau est de devenir une arme digne d\'accompagner un héros.', 4, 'Récompense de Donjon de la conquête à Sumeru et à Fontaine'),
(100, 'Base d\'arc du centre', 'Matériau', 'Un matériau qui peut être forgé en arc 4★.\r\nD\'après les philosophes, toutes les choses du monde ont quatre raisons d\'être. Le but de ce matériau est de devenir une arme digne d\'accompagner un héros.', 4, 'Récompense de Donjon de la conquête à Sumeru et à Fontaine'),
(101, 'Base d\'épée à deux mains du centre', 'Matériau', 'Un matériau qui peut être forgé en épée à deux mains 4★.\r\nD\'après les philosophes, toutes les choses du monde ont quatre raisons d\'être. Le but de ce matériau est de devenir une arme digne d\'accompagner un héros.', 4, 'Récompense de Donjon de la conquête à Sumeru et à Fontaine'),
(102, 'Base de catalyseur du centre', 'Matériau', 'Un matériau qui peut être forgé en catalyseur 4★.\r\nD\'après les philosophes, toutes les choses du monde ont quatre raisons d\'être. Le but de ce matériau est de devenir une arme digne d\'accompagner un héros.', 4, 'Récompense de Donjon de la conquête à Sumeru et à Fontaine'),
(103, 'Base de lance du centre', 'Matériau', 'Un matériau qui peut être forgé en arme d\'hast 4★.\r\nD\'après les philosophes, toutes les choses du monde ont quatre raisons d\'être. Le but de ce matériau est de devenir une arme digne d\'accompagner un héros.', 4, 'Récompense de Donjon de la conquête à Sumeru et à Fontaine'),
(104, 'Scarabuto', 'Produit d\'Inazuma', 'Un scarabée étrange qui peuple les zones riches en énergie Électro. Son tempérament docile et sédentaire ne pourrait pas contraster plus avec la forme féroce et démoniaque qui orne sa carapace.', 1, 'Zone conseillée : Île de Narukami'),
(105, 'Fleur de cerisier', 'Produit d\'Inazuma', 'Une fleur du cerisier sacré du Sanctuaire de Narukami, remplie d\'amour pour Inazuma.', 1, 'Zone conseillée : Île de Narukami'),
(106, 'Moelle cristalline', 'Produit d\'Inazuma', 'Un cristal qui contient une partie du pouvoir du Tatarigami. L\'ajout de ce matériau lors de la fusion augmentera considérablement la résistance et la dureté des métaux.', 1, 'Zone conseillée : Île de Yashiori'),
(107, 'Dendrobium sanglant', 'Produit d\'Inazuma', 'Une plante magnifique qui est également appelée « lycoris » par les poètes. On la croyait autrefois éteinte dans l\'archipel d\'Inazuma, avant de réapparaître sur les champs de bataille. On dit qu\'elle fleurit d\'un rouge d\'autant plus vif là où le sang a été versé.', 1, 'Zone conseillée : Plage de Nazuchi'),
(108, 'Herbe à sanglots', 'Produit d\'Inazuma', 'Même les jours sans vent, cette plante frissonnera au son du tonnerre. La partie qui ressemble à des pétales est en fait une extension de ses feuilles, destinée à protéger cette fleur si fragile.', 1, 'Zone conseillée : Tatarasuna'),
(109, 'Ganoderma marin', 'Produit d\'Inazuma', 'Une espèce qui ne pousse que dans certaines régions spéciales de l\'océan et des îles. On dirait une sorte de champignon, mais c\'est en fait le résultat de la sécrétion et de la prolifération de certains organismes à corps mou.', 1, 'Dans les eaux d\'Inazuma'),
(110, 'Perle de corail', 'Produit d\'Inazuma', 'Une perle précieuse qui se trouve dans le corail de l\'Île de Watatsumi. Elle possède une lueur qui rappelle celle de la lune.', 1, 'Zone conseillée : Île de Watatsumi'),
(111, 'Fruit d\'Amakumo', 'Produit d\'Inazuma', 'Le fruit de l\'herbe d\'Amakumo, qui pousse sur l\'Île de Seirai. En le mettant contre votre oreille, vous pouvez entendre comme un petit crépitement de courant électrique.', 1, 'Zone conseillée : Île de Seirai'),
(112, 'Champignon fluorescent', 'Produit d\'Inazuma', 'Un champignon qui brille comme une lumière dans la nuit. Il renferme un pouvoir incroyable.', 1, 'Zone conseillée : Île de Tsurumi'),
(113, 'Herbe marine', 'Ingrédient', 'Une plante qui pousse dans les eaux peu profondes, pouvant ajouter une saveur unique aux plats.', 1, 'Dans les eaux d\'Inazuma'),
(114, 'Melon lavande', 'Ingrédient', 'Un fruit à la couleur vive, dont la chair à l\'intérieur offre une sensation en bouche unique lorsqu\'elle est cuite.', 1, 'Dans la nature d\'Inazuma'),
(115, 'Champignon sacramental', 'Produit de Sumeru', 'Un champignon qui pousse en couches successives telle une mer de nuages et que l\'on trouve principalement sur les arbres au cœur de la forêt tropicale.', 1, 'Zone conseillée : Selve de Lokapala'),
(116, 'Padisachidée', 'Produit de Sumeru', 'Une plante pure et noble, très exigeante quant à l\'environnement dans lequel elle pousse. Son pistil peut être transformé en épices précieuses.', 1, 'Ville de Sumeru'),
(117, 'Nilotpalotus', 'Produit de Sumeru', 'Poussant dans les zones humides des forêts, ces plantes ne fleurissent que la nuit, et leurs fleurs projettent la même lueur que la lune.', 1, 'Cours d\'eau de Sumeru'),
(118, 'Fruit harra', 'Ingrédient', 'Poussant dans la forêt tropicale humide et chaude, ce fruit a une saveur épicée particulière. À Sumeru, il est un choix de prédilection pour fabriquer des épices, ce qui peut être réalisé en le réduisant à l\'état de poudre.', 1, 'Dans la nature'),
(119, 'Kalpalotus', 'Produit de Sumeru', 'La fleur produite par une liane poussant sur les falaises. On l\'appelle lotus uniquement parce qu\'elle en a l\'apparence, mais en dehors de cela, ces deux plantes ne partagent aucune similitude.', 1, 'Montagnes de Sumeru'),
(120, 'Pêche zaytun', 'Ingrédient', 'Le fruit d\'un arbuste forestier. Sa chair est ferme et sucrée. On dit qu\'il a un effet calmant sur l\'esprit.', 1, 'Dans la nature'),
(121, 'Rose sumérienne', 'Ingrédient', 'Vives et belles, ces fleurs d\'un violet pur dégagent un parfum agréable qui fera s\'arrêter toute personne passant devant elles. Leur nom peut porter à confusion, car bien qu\'elles soient des roses, elles sont semblables à nulle autre fleur de la même classification florale.', 1, 'Dans la nature'),
(122, 'Quandong', 'Produit de Sumeru', 'Un fruit qui pousse même dans les environnements désertiques les plus hostiles. Ces fruits pourpres vibrants sont rendus encore plus beaux sur un fond de sable jaune.', 1, 'Déserts de Sumeru'),
(123, 'Noix d\'ajilenakh', 'Ingrédient', 'Un fruit qui pousse sur les arbres à feuilles persistantes dans les oasis du désert. La chair sous sa peau dure est épaisse, sucrée et délicieuse, ce qui en fait une source de nourriture importante parmi les peuples du désert.', 1, 'Oasis des déserts de Sumeru'),
(124, 'Pupe graisseuse des sables', 'Produit de Sumeru', 'On ne trouve ces carapaces séchées que dans les profondeurs du désert où les anguilles des sables se reproduisent. Leur surface solide est destinée à protéger les corps larvaires des anguilles le temps qu\'elles s\'acclimatent à l\'environnement sec et chaud du désert.', 1, 'Zone conseillée : Tunnels des Wenuts'),
(125, 'Fleur funéraire', 'Produit de Sumeru', 'Une fleur écarlate pouvant fleurir même sur les anciens champs de bataille ou s\'épanouir dans les profondeurs du désert. Ses fleurs tombantes semblent porter le deuil de héros tombés depuis longtemps.', 1, 'Zone conseillée : Marais de l\'asipattravana'),
(126, 'Trishiraite', 'Produit de Sumeru', 'Un minerai splendide qui peut être trouvé dans les profondeurs des montagnes désolées, apparemment formé à partir de la condensation de l\'énergie élémentaire.', 1, 'Zone conseillée : Monts Temir'),
(127, 'Scarabée', 'Produit de Sumeru', 'Un coléoptère tenace qui trouve le repos dans le vaste océan de sable du désert. Le motif doré sur la coquille de ces coléoptères fait allusion à une relation profonde entre ces derniers et les ruines antiques qui parsèment le désert.', 1, 'Ruines des déserts de Sumeru'),
(128, 'Viparyas', 'Matériau', 'Des fleurs sans graines.\r\nQuand la terre se souviendra, les viparyas s\'épanouiront.', 1, 'Zone conseillée : Vanarana'),
(129, 'Lotus pluvieux', 'Matériau', 'Une plante qui pousse dans des milieux riches en élément Hydro. Cette plante se retrouve partout à Fontaine après ses longues saisons des pluies, d\'où son nom.', 1, 'Berges des rivières et des lacs'),
(130, 'Marcotte', 'Ingrédient', 'Une fleur rose avec un éclat semblable à une perle et un parfum riche. C\'est l\'un des ingrédients majeurs des parfums.', 1, 'Dans la nature'),
(131, 'Conque de béryl', 'Produit de Fontaine', 'Une structure en forme de conque qui dégage une faible lueur. Malgré son nom, ce n\'est pas une coquille ou un coquillage, mais quelque chose de condensé à partir d\'énergie élémentaire pure.', 1, 'Zone conseillée : Plaine Salacia'),
(132, 'Fleur romaritime', 'Produit de Fontaine', 'Une fleur bleue éthérée et élégante. Ses pistils tendres sont élastiques et absorbent l\'eau, ce qui en fait une matière première idéale pour diverses nécessités du quotidien.', 1, 'Zone conseillée : Plaine Salacia'),
(133, 'Maréalgue', 'Ingrédient', 'Une algue qui dérive dans l\'eau et se nourrit de substances produites par d\'autres plantes. Bien que souvent confondues avec le zooplancton, les maréalgues relèvent par nature de la catégorie du phytoplancton.', 1, 'Zones sous-marines de Fontaine'),
(134, 'Clochette de Lumidouce', 'Produit de Fontaine', 'Une fleur violette sereine et tranquille. Elle a un parfum léger, doux et durable et est souvent utilisée pour fabriquer des parfums rares.', 1, 'Nord de la Cour de Fontaine'),
(135, 'Rose arc-en-ciel', 'Produit de Fontaine', 'Une fleur rose délicate et tendre. Malgré leur nom, les roses arc-en-ciel ressemblent davantage aux lys.', 1, 'Zone conseillée : Région Béryl'),
(136, 'Sombrétoile', 'Produit de Fontaine', 'Un animal au corps mou qui émet une faible lumière. On en trouve souvent en train d\'escalader les murs des bâtiments près de l\'eau ou sous l\'eau.', 1, 'Zone conseillée : Région de Liffey'),
(137, 'Lys lacmineux', 'Produit de Fontaine', 'Une fleur bleue, claire et pure qui pousse là où des veines d\'eau se rejoignent. Elle possède un parfum frais et élégant, à la fois vif et doux.', 1, 'Zone conseillée : Forêt d\'Erinnyes'),
(138, 'Unité de sous-détection', 'Produit de Fontaine', 'Cette unité de détection, créée par l\'Institut de recherche de Fontaine en utilisant de nouveaux modèles de composants artificiels, était à l\'origine destinée au suivi des données environnementales.', 1, 'Zone conseillée : Région de l\'Institut de recherche d\'ingénierie cinétique de Fontaine'),
(139, 'Source des premières rosées', 'Produit de Fontaine', 'Une goutte de rosée limpide flottant dans les eaux de la Région mortée. Elle est différente des autres corps d\'eau pure, car il semblerait qu\'il s\'agisse d\'un regroupement d\'énergie élémentaire encore plus pure et primordiale.', 1, 'Zone conseillée : Région mortée'),
(140, 'Bois de bouleau', 'Matériau', 'Bois obtenu à partir de bouleau. Il est facile à travailler et produit des coupes lisses. Le mobilier fabriqué en utilisant ce bois est à la fois élégant et durable.\r\nPeut être utilisé pour fabriquer des décorations.', 1, 'Zone conseillée : Hauts du Mornevent'),
(141, 'Bois d\'exquisetier', 'Matériau', 'Bois obtenu à partir d\'exquisetier. Avec sa jolie couleur et sa fabrication unique, il peut avoir une myriade d\'utilisations après avoir subi les traitements appropriés.\r\nPeut être utilisé pour fabriquer des décorations.', 1, 'Zone conseillée : Sources de Jade'),
(142, 'Bois de pin', 'Matériau', 'Bois obtenu à partir de pin. Sa couleur et son grain n\'ont rien d\'extraordinaire, mais sa nature sans prétention est ce qui constitue son élégance. Ce bois est également très robuste et des mains expertes devraient en faire des meubles résistants aux mites et à la moisissure.\r\nPeut être utilisé pour fabriquer des décorations.', 1, 'Zone conseillée : Monts Dosdragon'),
(143, 'Bois d\'haréna', 'Matériau', 'Bois obtenu à partir d\'haréna. On dit que c\'est une plante exceptionnellement tenace, qui résiste au vent et à la pluie, avec une longue durée de vie.\r\nPeut être utilisé pour fabriquer des décorations.', 1, 'Zone conseillée : Forêt de jade'),
(144, 'Tige de bambou', 'Matériau', 'Une tige de bambou qui est plus légère et solide que son apparence le suggère. Elle dégage un doux parfum.\r\nPeut être utilisé pour fabriquer des décorations.', 1, 'Zone conseillée : Sources de Jade'),
(145, 'Bois de cèdre parfumé', 'Matériau', 'Bois obtenu à partir de cèdre. Aussi connu sous le nom de « cèdre parfumé » du fait des huiles aromatiques qu\'il contient.\r\nPeut être utilisé pour fabriquer des décorations.', 1, 'Zone conseillée : Vallée Tombétoile'),
(146, 'Bois d\'épicéa', 'Matériau', 'Bois obtenu à partir d\'épicéa. Largement utilisé en raison de sa qualité et de son grain.\r\nPeut être utilisé pour fabriquer des décorations.', 1, 'Zone conseillée : Colline sifflante'),
(147, 'Bois de rêve', 'Matériau', 'Bois obtenu à partir de cerisier. D\'une qualité exquise et gorgé d\'humidité, on dit qu\'il permet d\'avoir des rêves aussi doux que la brise chaude du printemps.\r\nPeut être utilisé pour fabriquer des décorations.', 1, 'Zone conseillée : Île de Narukami'),
(148, 'Bois d\'érable', 'Matériau', 'Bois obtenu à partir d\'érable d\'Inazuma, d\'une couleur et texture fines.\r\nPeut être utilisé pour fabriquer des décorations.', 1, 'Zone conseillée : Île de Narukami'),
(149, 'Bois de cèdre inazumien', 'Matériau', 'Bois obtenu à partir de cèdre d\'Inazuma. Il est doux, droit et dégage un léger parfum.\r\nPeut être utilisé pour fabriquer des décorations.', 1, 'Zone conseillée : Île de Narukami'),
(150, 'Bois de dulcinier', 'Matériau', 'Bois obtenu à partir de dulcinier. Il renferme la quantité parfaite d\'humidité et d\'huile, ce qui lui permet d\'avoir de nombreuses utilisations.\r\nPeut être utilisé pour fabriquer des décorations.', 1, 'Zone conseillée : Île de Yashiori'),
(151, 'Bois radiant', 'Matériau', 'Bois obtenu à partir de radiantier. On dit qu\'il est exceptionnellement tenace, qu\'il résiste au vent et à la pluie, et qu\'il a une longue durée de vie.\r\nPeut être utilisé pour fabriquer des décorations.', 1, 'Zone conseillée : Forêts de Sumeru'),
(152, 'Bois de karmaphalien', 'Matériau', 'Bois obtenu à partir de karmaphalien. En le travaillant, on peut facilement obtenir une surface lisse et agréable à la fois à l\'œil et au toucher sans utiliser de procédés trop sophistiqués.\r\nPeut être utilisé pour fabriquer des décorations.', 1, 'Zone conseillée : Forêts de Sumeru'),
(153, 'Bois d\'épiphanie', 'Matériau', 'Bois obtenu à partir d\'épiphanien. On le voit partout. D\'apparence ordinaire, il est notablement durable et facile à utiliser.\r\nPeut être utilisé pour fabriquer des décorations.', 1, 'Zone conseillée : Forêts de Sumeru'),
(154, 'Bois de palmier-dattier', 'Matériau', 'Bois obtenu à partir de palmier-dattier. Bien qu\'il n\'ait pas de texture spéciale ni n\'offre de sensation particulière au toucher, il retient très bien l\'eau et est donc facile à cultiver.\r\nPeut être utilisé pour fabriquer des décorations.', 1, 'Zone conseillée : Déserts de Sumeru'),
(155, 'Bois de tamaris Athel', 'Matériau', 'Bois obtenu à partir de tamaris Athel. Il a une texture particulière et résiste à la sécheresse et à l\'humidité, ce qui le rend très durable.\r\nPeut être utilisé pour fabriquer des décorations.', 1, 'Zone conseillée : Déserts de Sumeru'),
(156, 'Bois d\'hibiscus', 'Matériau', 'Bois obtenu à partir d\'hibiscutier. Avec son tronc dur, il convient à la construction des fondations et des charpentes des maisons. Le bois étant facile à traiter et à transformer, il faut donc relativement moins de travail manuel pour produire de beaux meubles.\r\nPeut être utilisé pour fabriquer des décorations.', 1, 'Zone conseillée : dans la nature de Fontaine'),
(157, 'Bois de tilleul', 'Matériau', 'Bois obtenu à partir de tilleul cordial. Grâce à sa texture lisse et sa couleur claire, peu de copeaux de bois sont produits même lors d\'un traitement fin et ce bois est souvent sculpté dans des objets artisanaux délicats ou utilisé pour fabriquer des pièces compactes de mécanismes en bois.\r\nPeut être utilisé pour fabriquer des décorations.', 1, 'Zone conseillée : dans la nature de Fontaine'),
(158, 'Bois de frêne', 'Matériau', 'Bois obtenu à partir de frêne. D\'une résistance extrêmement élevée, il est utilisé non seulement pour la fabrication de composants porteurs solides, mais également pour les plaques qui servent de renforts muraux et les boîtes de transport soumises à des chocs fréquents.\r\nPeut être utilisé pour fabriquer des décorations.', 1, 'Zone conseillée : dans la nature de Fontaine'),
(159, 'Bois de cyprès', 'Matériau', 'Bois obtenu à partir de cyprès. Son noyau a un rayon relativement grand, alors que son aubier est plus fin. Bien qu\'il ne soit pas solide ou fiable comme le métal, ce bois possède des avantages uniques pour les constructions de grande surface.\r\nPeut être utilisé pour fabriquer des décorations.', 1, 'Zone conseillée : dans la nature de Fontaine'),
(160, 'Tissu', 'Matériau', 'Une étoffe de qualité. Cet objet vous sera utile dans la fabrication de décorations.', 1, NULL),
(161, 'Teinture rouge', 'Matériau', 'Teinture de couleur rouge. Cet objet vous sera utile dans la fabrication de décorations.', 1, NULL),
(162, 'Teinture jaune', 'Matériau', 'Teinture de couleur jaune. Cet objet vous sera utile dans la fabrication de décorations.', 1, NULL),
(163, 'Teinture bleue', 'Matériau', 'Teinture de couleur bleue. Cet objet vous sera utile dans la fabrication de décorations.', 1, NULL),
(164, 'Astuces du voyageur', 'Matériau d\'EXP de personnage', 'Matériau d\'EXP de personnage. Confère 1 000 pts d\'EXP.\r\nCes astuces vous seront toujours utiles si vous ne vivez pas en Teyvat.', 2, 'Aventure et exploration'),
(165, 'Conseils de l\'aventurier', 'Matériau d\'EXP de personnage', 'Matériau d\'EXP de personnage. Confère 5 000 pts d\'EXP.\r\nCes conseils sont indispensables si vous vous aventurez dans des contrées inconnues.', 3, 'Aventure et exploration'),
(166, 'Leçons du héros', 'Matériau d\'EXP de personnage', 'Matériau d\'EXP de personnage. Confère 20 000 pts d\'EXP.\r\nCes leçons vous seront précieuses pour un pèlerinage de Teyvat à Celestia.', 4, 'Récompense de Fleur de la révélation'),
(167, 'Minerai de renforcement', 'Matériau de renforcement d\'arme', 'Matériau d\'EXP d\'arme. Confère 400 pts d\'EXP.\r\nC\'est un minéral raffiné qui a un potentiel illimité. Il peut être utilisé pour renforcer des armes.', 1, 'Forge'),
(168, 'Minerai de renforcement raffiné', 'Matériau de renforcement d\'arme', 'Matériau d\'EXP d\'arme. Confère 2 000 pts d\'EXP.\r\nPour une raison inconnue, les armes peuvent absorber ce minéral raffiné spécial pour devenir plus puissantes.', 2, 'Forge'),
(169, 'Minerai de renforcement mystique', 'Matériau de renforcement d\'arme', 'Matériau d\'EXP d\'arme. Confère 10 000 pts d\'EXP.\r\nLa légende dit que les souvenirs des batailles se sont condensés dans des minéraux raffinés. Si vous renforcez une arme avec ces matériaux, elle pourra avoir sa propre âme.', 3, 'Forge'),
(170, 'Éclat de diamant scintillant', 'Matériau d\'élévation de personnage', 'Matériau d\'élévation de personnage.\n« Bienvenue au monde »', 2, 'Récompense de Niv. d\'aventure'),
(171, 'Fragment de diamant scintillant', 'Matériau d\'élévation de personnage', 'Matériau d\'élévation de personnage.\r\n« Bienvenue au monde »', 3, 'Récompense de Niv. d\'aventure'),
(172, 'Morceau de diamant scintillant', 'Matériau d\'élévation de personnage', 'Matériau d\'élévation de personnage.\r\n« Bienvenue au monde »', 4, 'Récompense de Niv. d\'aventure'),
(173, 'Diamant scintillant', 'Matériau d\'élévation de personnage', 'Matériau d\'élévation de personnage.\r\n« Bienvenue au monde »', 5, 'Récompense de Niv. d\'aventure'),
(174, 'Éclat d\'agate agnidus', 'Matériau d\'élévation de personnage', 'Matériau d\'élévation de personnage.', 2, 'Butin d\'Hypostase Pyro'),
(175, 'Fragment d\'agate agnidus', 'Matériau d\'élévation de personnage', 'Matériau d\'élévation de personnage.\r\n« Aller en pèlerinage pour le désir, et se battre pour la réputation... »', 3, 'Butin d\'Hypostase Pyro (Niv.40+)'),
(176, 'Morceau d\'agate agnidus', 'Matériau d\'élévation de personnage', 'Matériau d\'élévation de personnage.\r\n« Aller en pèlerinage pour le désir, et se battre pour la réputation... »\r\n« Le rêve a tout brûlé en cendre à la fin. »', 4, 'Butin d\'Hypostase Pyro (Niv.60+)'),
(177, 'Pierre d\'agate agnidus', 'Matériau d\'élévation de personnage', 'Matériau d\'élévation de personnage.\r\n« Aller en pèlerinage pour le désir, et se battre pour la réputation... »\r\n« Si son vrai cœur originel peut être laissé dans les cendres, il a réussi à atteindre la vérité de ██. »', 5, 'Butin d\'Hypostase Pyro (Niv.75+)'),
(178, 'Éclat de lazurite varunada', 'Matériau d\'élévation de personnage', 'Matériau d\'élévation de personnage.', 2, 'Butin d\'Hypostase Hydro'),
(179, 'Fragment de lazurite varunada', 'Matériau d\'élévation de personnage', 'Matériau d\'élévation de personnage.\r\n« Mes idéaux sont purs. »', 3, 'Butin d\'Hypostase Hydro (Niv.40+)'),
(180, 'Morceau de lazurite varunada', 'Matériau d\'élévation de personnage', 'Matériau d\'élévation de personnage.\r\n« Mes idéaux sont purs. »\r\n« Je me dois de te corriger : personne ici n\'est impur aux yeux des dieux... »', 4, 'Butin d\'Hypostase Hydro (Niv.60+)'),
(181, 'Pierre de lazurite varunada', 'Matériau d\'élévation de personnage', 'Matériau d\'élévation de personnage.\r\n« Mes idéaux sont purs. »\r\n« Je me dois de te corriger : personne ici n\'est impur aux yeux des dieux, et seuls les lois et le tribunal peuvent déclarer un jugement. »\r\n« Ils peuvent même me juger. Louez donc ma grandeur et ma pureté. »', 5, 'Butin d\'Hypostase Hydro (Niv.75+)'),
(182, 'Éclat d\'émeraude nagadus', 'Matériau d\'élévation de personnage', 'Matériau d\'élévation de personnage.', 2, 'Butin d\'Hypostase Dendro'),
(183, 'Fragment d\'émeraude nagadus', 'Matériau d\'élévation de personnage', 'Matériau d\'élévation de personnage.\r\n« J\'ai fait un très long rêve... »', 3, 'Butin d\'Hypostase Dendro (Niv.40+)'),
(184, 'Morceau d\'émeraude nagadus', 'Matériau d\'élévation de personnage', 'Matériau d\'élévation de personnage.\r\n« J\'ai fait un très long rêve... »\r\n« Il y avait des gens qui se tenaient la main et dansaient en cercle : des sages et des simples d\'esprit, des danseuses et des guerriers, des marionnettes et des statues de divinités... »', 4, 'Butin d\'Hypostase Dendro (Niv.60+)'),
(185, 'Pierre d\'émeraude nagadus', 'Matériau d\'élévation de personnage', 'Matériau d\'élévation de personnage.\r\n« J\'ai fait un très long rêve... »\r\n« Il y avait des gens qui se tenaient la main et dansaient en cercle : des sages et des simples d\'esprit, des danseuses et des guerriers, des marionnettes et des statues de divinités... »\r\n« Ces gens qui dansaient ensemble étaient une représentation de l\'univers. Vivre a toujours été une fin, et la sagesse, le moyen d\'y parvenir. »', 5, 'Butin d\'Hypostase Dendro (Niv.75+)'),
(186, 'Éclat d\'améthyste vajrada', 'Matériau d\'élévation de personnage', 'Matériau d\'élévation de personnage.', 2, 'Butin d\'Hypostase Électro'),
(187, 'Fragment d\'améthyste vajrada', 'Matériau d\'élévation de personnage', 'Matériau d\'élévation de personnage.\r\n« Ce corps est le plus noble et le plus éminent du monde. »', 3, 'Butin d\'Hypostase Électro (Niv.40+)'),
(188, 'Morceau d\'améthyste vajrada', 'Matériau d\'élévation de personnage', 'Matériau d\'élévation de personnage.\r\n« Ce corps est le plus noble et le plus éminent du monde. »\r\n« Il devrait gouverner le monde. »', 4, 'Butin d\'Hypostase Électro (Niv.60+)'),
(189, 'Pierre d\'améthyste vajrada', 'Matériau d\'élévation de personnage', 'Matériau d\'élévation de personnage.\r\n« Ce corps est le plus noble et le plus éminent du monde. »\r\n« Il devrait gouverner le monde. »\r\n« Il a promis une fois un beau rêve à son peuple : une terre d\'éternité, immuable à jamais »', 5, 'Butin d\'Hypostase Électro (Niv.75+)'),
(190, 'Éclat de turquoise vayuda', 'Matériau d\'élévation de personnage', 'Matériau d\'élévation de personnage.', 2, 'Butin d\'Hypostase Anémo'),
(191, 'Fragment de turquoise vayuda', 'Matériau d\'élévation de personnage', 'Matériau d\'élévation de personnage.\r\n« Le vent change toujours de direction. »', 3, 'Butin d\'Hypostase Anémo (Niv.40+)'),
(192, 'Morceau de turquoise vayuda', 'Matériau d\'élévation de personnage', 'Matériau d\'élévation de personnage.\r\n« Le vent change toujours de direction. »\r\n« Un jour, il soufflera vers un meilleur avenir. »', 4, 'Butin d\'Hypostase Anémo (Niv.60+)'),
(193, 'Pierre de turquoise vayuda', 'Matériau d\'élévation de personnage', 'Matériau d\'élévation de personnage.\r\n« Le vent change toujours de direction »\r\n« Un jour, il soufflera vers un meilleur avenir. »\r\n« À partir de cet instant, je te donne ma bénédiction. »', 5, 'Butin d\'Hypostase Anémo (Niv.75+)'),
(194, 'Éclat de jade shivada', 'Matériau d\'élévation de personnage', 'Matériau d\'élévation de personnage.', 2, 'Butin d\'Hypostase Cryo'),
(195, 'Fragment de jade shivada', 'Matériau d\'élévation de personnage', 'Matériau d\'élévation de personnage.\r\n« Pardon... »', 3, 'Butin d\'Hypostase Cryo (Niv.40+)'),
(196, 'Morceau de jade shivada', 'Matériau d\'élévation de personnage', 'Matériau d\'élévation de personnage.\r\n« Pardon... »\r\n« Je vous laisse partager la tristesse du monde entier avec moi. »', 4, 'Butin d\'Hypostase Cryo (Niv.60+)'),
(197, 'Pierre de jade shivada', 'Matériau d\'élévation de personnage', 'Matériau d\'élévation de personnage.\r\n« Pardon... »\r\n« Je vous laisse partager la tristesse du monde entier avec moi. »\r\n« Puisque vous pourriez supporter mon froid glacial, vous devez avoir un fort désir au fond du cœur. »', 5, 'Butin d\'Hypostase Cryo (Niv.75+)'),
(198, 'Éclat de topaze prithiva', 'Matériau d\'élévation de personnage', 'Matériau d\'élévation de personnage.', 2, 'Butin d\'Hypostase Géo'),
(199, 'Fragment de topaze prithiva', 'Matériau d\'élévation de personnage', 'Matériau d\'élévation de personnage.\r\n« Toutes les monnaies qui circulent sur le continent font partie de mon corps. »', 3, 'Butin d\'Hypostase Géo (Niv.40+)'),
(200, 'Morceau de topaze prithiva', 'Matériau d\'élévation de personnage', 'Matériau d\'élévation de personnage.\r\n« Toutes les monnaies qui circulent sur le continent font partie de mon corps. »\r\n« Je garantis la sueur, la sagesse et l\'avenir des humains de cette manière. »', 4, 'Butin d\'Hypostase Géo (Niv.60+)'),
(201, 'Pierre de topaze prithiva', 'Matériau d\'élévation de personnage', 'Matériau d\'élévation de personnage.\r\n« Toutes les monnaies qui circulent sur le continent font partie de mon corps. »\r\n« Je garantis la sueur, la sagesse et l\'avenir des humains de cette manière. »\r\n« C\'est la confiance que je place en vous. Si vous la trahissez, vous tacherez mon sang. »', 5, 'Butin d\'Hypostase Géo (Niv.75+)'),
(202, 'Poudre d\'Azoth', 'Consommable', 'Un dispositif permettant de transmuter les matériaux d\'élévation et de modifier leur attribut élémentaire.\r\nEn dépit de ses utilisations pratiques, l\'alchimie est l\'étude de la matière et des règles qui sous-tendent ses modifications. Elle pointe vers des lois qui gouvernent les cycles de ce monde et les mouvements des lignes énergétiques.\r\nQuelqu\'un a distribué ces merveilles alchimiques un peu partout sur le continent. Bien qu\'elles soient pratiques et utiles, elles recèlent néanmoins le pouvoir de déstabiliser l\'équilibre des éléments...', 2, 'Échange Astrions');
INSERT INTO `material` (`id`, `name`, `type`, `description`, `rarity`, `source`) VALUES
(203, 'Enseignement de la Liberté', 'Matériau d\'aptitude de personnage', 'Matériau d\'amélioration d\'aptitude.\r\nLa liberté est l\'esprit du royaume du vent.\r\nLa liberté de vivre est une d\'entre elles, c\'est-à-dire, être libre de vivre en bonne santé et sans jamais se soucier du danger.', 2, 'Donjon de la maîtrise : Autel givré (Monday, Thursday, Sunday)'),
(204, 'Guide de la Liberté', 'Matériau d\'aptitude de personnage', 'Matériau d\'amélioration d\'aptitude.\r\nLa liberté est l\'esprit du royaume du vent.\r\nLa liberté de voyager est une d\'entre elles, c\'est-à-dire, être libre de circuler à travers les terres sans aucune obstruction.', 3, 'Donjon de la maîtrise : Autel givré (Monday, Thursday, Sunday)'),
(205, 'Philosophie de la Liberté', 'Matériau d\'aptitude de personnage', 'Matériau d\'amélioration d\'aptitude.\r\nLa liberté est l\'esprit du royaume du vent.\r\nLa liberté de chanter est une d\'entre elles, c\'est-à-dire, être libre de fredonner sur l\'air du vent les terres de l\'Archon Anémo.', 4, 'Donjon de la maîtrise : Autel givré (Monday, Thursday, Sunday)'),
(206, 'Enseignement de la Résistance', 'Matériau d\'aptitude de personnage', 'Matériau d\'élévation d\'aptitude.\r\nLa résistance est l\'échine du royaume du vent.\r\nElle a marqué son histoire à travers les âges. Certains se sont soulevés pour que les injustices passées ne tombent pas dans l\'oubli, comme des pousses qui jaillissent du sol et le vent qui souffle à travers les murs de pierre.', 2, 'Donjon de la maîtrise : Abîme gelé (Tuesday, Friday, Sunday)'),
(207, 'Guide de la Résistance', 'Matériau d\'aptitude de personnage', 'Matériau d\'amélioration d\'aptitude.\r\nLa résistance est l\'échine du royaume du vent.\r\nElle a marqué son histoire à travers les âges. Certains ont agi pour que le peuple jouisse de sa liberté actuelle, comme Barbatos qui a dispersé la neige et Vennessa qui s\'est rebellée.', 3, 'Donjon de la maîtrise : Abîme gelé (Tuesday, Friday, Sunday)'),
(208, 'Philosophie de la Résistance', 'Matériau d\'aptitude de personnage', 'Matériau d\'amélioration d\'aptitude.\r\nLa résistance est l\'échine du royaume du vent.\r\nElle a marqué son histoire à travers les âges. Certains se sont soulevé pour que la poésie de Mondstadt se répande aussi librement que le vent à travers les terres.', 4, 'Donjon de la maîtrise : Abîme gelé (Tuesday, Friday, Sunday)'),
(209, 'Enseignement de la Poésie', 'Matériau d\'aptitude de personnage', 'Matériau d\'amélioration d\'aptitude.\r\nLa poésie est l\'âme du royaume du vent.\r\nElle existe pour exprimer la beauté. La brise transporte les mots doux lors des beaux jours. Ils touchent le cœur des gens comme elle caresse les feuilles d\'un arbre.', 2, 'Donjon de la maîtrise : Royaume endormi (Wednesday, Saturday, Sunday)'),
(210, 'Guide de la Poésie', 'Matériau d\'aptitude de personnage', 'Matériau d\'amélioration d\'aptitude.\r\nLa poésie est l\'âme du royaume du vent.\r\nElle existe pour donner du courage à ceux qui en ont besoin. Pendant les jours sombres, les bourrasques soufflaient des mots de colère dans les rues, comme des tambours de guerre qui annoncent une tempête à venir.', 3, 'Donjon de la maîtrise : Royaume endormi (Wednesday, Saturday, Sunday)'),
(211, 'Philosophie de la Poésie', 'Matériau d\'aptitude de personnage', 'Matériau d\'amélioration d\'aptitude.\r\nLa poésie est l\'âme du royaume du vent.\r\nElle existe pour être transmise à travers les générations. Bien que rien ne soit éternel, le vent soufflera toujours ces mots doux au-delà des cieux, à travers les terres et les mers du monde entier.', 4, 'Donjon de la maîtrise : Royaume endormi (Wednesday, Saturday, Sunday)'),
(212, 'Enseignement de la Prospérité', 'Matériau d\'aptitude de personnage', 'Matériau d\'amélioration d\'aptitude.\r\nLa prospérité est l\'aspiration du pays de la roche.\r\nElle assure la paix et la sécurité de Liyue, cité bâtie conjointement par les dieux et par les hommes.', 2, 'Donjon de la maîtrise : Autel des flammes (Monday, Thursday, Sunday)'),
(213, 'Guide de la Prospérité', 'Matériau d\'aptitude de personnage', 'Matériau d\'amélioration d\'aptitude.\r\nLa prospérité est l\'aspiration du pays de la roche.\r\nRécompenser le labeur avec de l\'or, telle était la promesse faite aux enfants de Liyue.', 3, 'Donjon de la maîtrise : Autel des flammes (Monday, Thursday, Sunday)'),
(214, 'Philosophie de la Prospérité', 'Matériau d\'aptitude de personnage', 'Matériau d\'amélioration d\'aptitude.\r\nLa prospérité est l\'aspiration du pays de la roche.\r\nElle incarne le passé, le présent et le futur de Liyue. La cité possède toutes les richesses que nulle autre nation peut oser espérer obtenir.', 4, 'Donjon de la maîtrise : Autel des flammes (Monday, Thursday, Sunday)'),
(215, 'Enseignement de la Diligence', 'Matériau d\'aptitude de personnage', 'Matériau d\'amélioration d\'aptitude.\r\nLe goût du travail est l\'essence du pays de la roche.\r\nConquérir les montagnes et les mers, aspirer à la richesse et à la prospérité... Tout est possible lorsqu\'on travaille dur pour atteindre son objectif.', 2, 'Donjon de la maîtrise : Au cœur des flammes (Tuesday, Friday, Sunday)'),
(216, 'Guide de la Diligence', 'Matériau d\'aptitude de personnage', 'Matériau d\'amélioration d\'aptitude.\r\nLe goût du travail est l\'essence du pays de la roche.\r\nIl coule dans les veines des Liyuéens, qui deviennent capables de transformer la roche en or grâce à lui. La prospérité de son port commercial n\'est guère étonnante.', 3, 'Donjon de la maîtrise : Au cœur des flammes (Tuesday, Friday, Sunday)'),
(217, 'Philosophie de la Diligence', 'Matériau d\'aptitude de personnage', 'Matériau d\'amélioration d\'aptitude.\r\nLe goût du travail est l\'essence du pays de la roche.\r\nLes Liyuéens croient que chacun peut se faire une place sur la terre des dieux grâce à sa sagesse et à sa force, mais aussi à la sueur de son front.', 4, 'Donjon de la maîtrise : Au cœur des flammes (Tuesday, Friday, Sunday)'),
(218, 'Enseignement de l\'Or', 'Matériau d\'aptitude de personnage', 'Matériau d\'amélioration d\'aptitude.\r\nL\'or est le symbole du Pays de la Roche.\r\nAbondant sous ses terres, il est le nerf de la guerre de Liyue qui tient debout grâce à lui.', 2, 'Donjon de la maîtrise : Cercle brûlant (Wednesday, Saturday, Sunday)'),
(219, 'Guide de l\'Or', 'Matériau d\'aptitude de personnage', 'Matériau d\'amélioration d\'aptitude.\r\nL\'or est le symbole du Pays de la Roche.\r\nIl représente la transformation et le lien unissant le port, les monts, les terres et les hommes de Liyue : le travail se transforme en or, et ce dernier apporte la prospérité en retour.', 3, 'Donjon de la maîtrise : Cercle brûlant (Wednesday, Saturday, Sunday)'),
(220, 'Philosophie de l\'Or', 'Matériau d\'aptitude de personnage', 'Matériau d\'amélioration d\'aptitude.\r\nL\'or est le symbole du Pays de la Roche.\r\nLiyue n\'en manque pas, mais sa véritable richesse se trouve dans le cœur de ses habitants.', 4, 'Donjon de la maîtrise : Cercle brûlant (Wednesday, Saturday, Sunday)'),
(221, 'Couronne de la sagesse', 'Matériau d\'aptitude de personnage', 'Matériau d\'amélioration d\'aptitude rare.\r\nUn support utilisé dans les temps anciens pour conserver et transmettre le savoir. Bien que de nos jours les connaissances se transmettent de différentes manières, autant orales qu\'écrites, cet artéfact n\'a pas perdu la moindre once de son pouvoir d\'antan.', 5, 'Événement à durée limitée'),
(222, 'Enseignement de l\'Éphémère', 'Matériau d\'aptitude de personnage', 'Matériau d\'amélioration d\'aptitude.\r\nL\'éphémère est le rêve de la nation du tonnerre.\r\nLes gloires éphémères sont la plus haute expression de la beauté mortelle, car nous autres, mortels, sommes tels des éclairs dans la nuit. Comme un beau rêve ou une étincelle qui s\'embrase, nous laissons une empreinte magnifique sur le ciel nocturne éternel.', 2, 'Donjon de la maîtrise : Règne violet (Monday, Thursday, Sunday)'),
(223, 'Guide de l\'Éphémère', 'Matériau d\'aptitude de personnage', 'Matériau d\'amélioration d\'aptitude.\r\nL\'éphémère est le rêve de la nation du tonnerre.\r\nNous autres, mortels, trouvons les plus grandes joies dans les rêves éphémères, car la vie passe tel un éclair. Poursuivez vos rêves éthérés si vous le souhaitez et profitez du silence inattendu de la nuit à la lueur des lampes.', 3, 'Donjon de la maîtrise : Règne violet (Monday, Thursday, Sunday)'),
(224, 'Philosophie de l\'Éphémère', 'Matériau d\'aptitude de personnage', 'Matériau d\'amélioration d\'aptitude.\r\nL\'éphémère est le rêve de la nation du tonnerre.\r\nC\'est la nature éphémère de ce monde mortel qui est l\'essence de sa beauté, car nos vies en ce monde sont tels des éclairs fugaces. Et pourtant, voilà que se manifeste la puissance du divin. Qui peut donc faire face au rugissement de l\'éternité ?', 4, 'Donjon de la maîtrise : Règne violet (Monday, Thursday, Sunday)'),
(225, 'Enseignement de l\'Élégance', 'Matériau d\'aptitude de personnage', 'Matériau d\'amélioration d\'aptitude.\r\nL\'élégance est l\'apparence de la nation du tonnerre.\r\nL\'élégance n\'aime pas la flatterie et les élégants sont toujours nobles. Ils sont comme ces grands oiseaux des mers qui planent au-dessus des tempêtes retentissantes. Ils ne répondent pas à la vulgarité, comme on ne jette pas une couronne de fleurs dans la boue, sans éclabousser sa propre dignité.', 2, 'Donjon de la maîtrise : Vallée foudroyée (Tuesday, Friday, Sunday)'),
(226, 'Guide de l\'Élégance', 'Matériau d\'aptitude de personnage', 'Matériau d\'amélioration d\'aptitude.\r\nL\'élégance est l\'apparence de la nation du tonnerre.\r\nL\'élégance évite l\'arrogance. Les élégants sont toujours humbles, et ce n\'est qu\'en rejetant la vanité et la négligence que l\'on voit la véritable beauté, même dans l\'apparence la plus inesthétique.', 3, 'Donjon de la maîtrise : Vallée foudroyée (Tuesday, Friday, Sunday)'),
(227, 'Philosophie de l\'Élégance', 'Matériau d\'aptitude de personnage', 'Matériau d\'amélioration d\'aptitude.\r\nL\'élégance est l\'apparence de la nation du tonnerre.\r\nL\'élégance abhorre la vulgarité, et l\'élégant marche toujours seul. Résister aux vils maux, ne pas se laisser émouvoir par l\'amour de la médiocrité, ne jamais se réfugier dans la basse ignorance, voilà comment on perçoit l\'abîme entre l\'élégant et le méprisant.', 4, 'Donjon de la maîtrise : Vallée foudroyée (Tuesday, Friday, Sunday)'),
(228, 'Enseignement de la Lumière', 'Matériau d\'aptitude de personnage', 'Matériau d\'amélioration d\'aptitude.\r\nLa lumière est le désir de la nation du tonnerre.\r\nLe dirigeant qui prétend tout savoir pour toujours vise à accumuler la gloire céleste. Mais cette vision qui ne peut être partagée ne mène les gens qu\'à la désirer plus hardament, comme des papillons de nuit attirés par une lumière.', 2, 'Donjon de la maîtrise : Ruines de ronces (Wednesday, Saturday, Sunday)'),
(229, 'Guide de la Lumière', 'Matériau d\'aptitude de personnage', 'Matériau d\'amélioration d\'aptitude.\r\nLa lumière est le désir de la nation du tonnerre.\r\nBien que les rayons du soleil soient bloqués par des couches de nuages, il y a encore des gens au pays du dieu du tonnerre qui rêvent de percer cette mer de colère foudroyante pour poursuivre la gloire du divin, ne craignant pas d\'être cruellement frappés.', 3, 'Donjon de la maîtrise : Ruines de ronces (Wednesday, Saturday, Sunday)'),
(230, 'Philosophie de la Lumière', 'Matériau d\'aptitude de personnage', 'Matériau d\'amélioration d\'aptitude.\r\nLa lumière est le désir de la nation du tonnerre.\r\nBien qu\'elle doive vivre sur la terre, l\'humanité aspirera toujours à la lumière. Les mortels se dépasseront toujours et ils ne s\'arrêteront jamais. Une éternité scellée peut sembler magnifique, mais son essence est la mort.', 4, 'Donjon de la maîtrise : Ruines de ronces (Wednesday, Saturday, Sunday)'),
(231, 'Enseignement de l\'Admonestation', 'Matériau d\'aptitude de personnage', 'Matériau d\'amélioration d\'aptitude.\r\nL\'admonestation est la branche de la nation de la sagesse.\r\nElle provient d\'un cœur pur et seuls les mots clairs et bienveillants peuvent exprimer une extrême sagesse et libérer les germes de la connaissance des chaînes de l\'arrogance.', 2, 'Donjon de la maîtrise : Pleine lune (Monday, Thursday, Sunday)'),
(232, 'Guide de l\'Admonestation', 'Matériau d\'aptitude de personnage', 'Matériau d\'amélioration d\'aptitude.\r\nL\'admonestation est la branche de la nation de la sagesse.\r\nElle va de pair avec la pensée ingénieuse. Une réflexion consciencieuse permet de s\'exprimer clairement et humblement pour convaincre les sages et éclairer les ignorants. De telles paroles donnent la possibilité à la sagesse de couler librement, comme une rivière sans barrage.', 3, 'Donjon de la maîtrise : Pleine lune (Monday, Thursday, Sunday)'),
(233, 'Philosophie de l\'Admonestation', 'Matériau d\'aptitude de personnage', 'Matériau d\'amélioration d\'aptitude.\r\nL\'admonestation est la branche de la nation de la sagesse.\r\nElle est accomplie par de bonnes actions. Les gestes donnent un sens aux mots. Ce n\'est qu\'en tenant ses intentions, ses paroles et ses actions que des fruits charnus mûriront de l\'arbre de la sagesse.', 4, 'Donjon de la maîtrise : Pleine lune (Monday, Thursday, Sunday)'),
(234, 'Enseignement de l\'Ingénuité', 'Matériau d\'aptitude de personnage', 'Matériau d\'amélioration d\'aptitude.\r\nL\'ingénuité est la nervure de la nation de la sagesse.\r\nElle naît de la considération bienveillante. Il est souvent dit qu\'un arbre empoisonné ne porte pas de fruit. De même, les idées ingénieuses proviennent uniquement d\'esprits déterminés et attentionnés, dignes de sagesse.', 2, 'Donjon de la maîtrise : Trait d\'esprit (Tuesday, Friday, Sunday)'),
(235, 'Guide de l\'Ingénuité', 'Matériau d\'aptitude de personnage', 'Matériau d\'amélioration d\'aptitude.\r\nL\'ingénuité est la nervure de la nation de la sagesse.\r\nElle guide les paroles honnêtes. Réfléchir avec intelligence, attention et bonté permet de rester dans le droit chemin. Seuls ceux dont l\'âme est emplie de dédain et de méchanceté sont capables de paroles abjectes.', 3, 'Donjon de la maîtrise : Trait d\'esprit (Tuesday, Friday, Sunday)'),
(236, 'Philosophie de l\'Ingénuité', 'Matériau d\'aptitude de personnage', 'Matériau d\'amélioration d\'aptitude.\r\nL\'ingénuité est la nervure de la nation de la sagesse.\r\nElle est vectrice d\'une conduite harmonieuse. L\'action est complémentaire à la pensée. La sagesse s\'épanouit là où l\'esprit, les mots et les actions convergent.', 4, 'Donjon de la maîtrise : Trait d\'esprit (Tuesday, Friday, Sunday)'),
(237, 'Enseignement de l\'Usage', 'Matériau d\'aptitude de personnage', 'Matériau d\'amélioration d\'aptitude.\r\nL\'usage est la racine de la nation de la sagesse.\r\nIl naît d\'une volonté inébranlable. La sagesse se manifeste à travers la quête de la bonté. Lorsque cette dernière est absente, l\'ingéniosité et l\'éloquence le sont également.', 2, 'Donjon de la maîtrise : Discipline (Wednesday, Saturday, Sunday)'),
(238, 'Guide de l\'Usage', 'Matériau d\'aptitude de personnage', 'Matériau d\'amélioration d\'aptitude.\r\nL\'usage est la racine de la nation de la sagesse.\r\nIl corrobore les paroles sincères. Il ne suffit pas de penser et de se comporter avec bienveillance pour atteindre la sagesse. Elle émerge de certaines actions et laisse son empreinte sur les mots honnêtes.', 3, 'Donjon de la maîtrise : Discipline (Wednesday, Saturday, Sunday)'),
(239, 'Philosophie de l\'Usage', 'Matériau d\'aptitude de personnage', 'Matériau d\'amélioration d\'aptitude.\r\nL\'usage est la racine de la nation de la sagesse.\r\nIl fait pousser les fruits de la sagesse. L\'acte pratiqué se donne un sens. L\'union des actions, des paroles et des pensées est le seul moyen qu\'ont les sages d\'atteindre la perfection.', 4, 'Donjon de la maîtrise : Discipline (Wednesday, Saturday, Sunday)'),
(240, 'Enseignement de l\'Équité', 'Matériau d\'aptitude de personnage', 'Matériau d\'amélioration d\'aptitude.\r\nL\'équité est le rivage de la patrie de l\'eau.\r\nL\'équité fixe les limites des droits. Sans équité, les droits accordés par la loi seront abusés et deviendront un outil pour nuire à nos coutumes vertueuses.', 2, 'Donjon de la maîtrise : Rime rythmique (Monday, Thursday, Sunday)'),
(241, 'Guide de l\'Équité', 'Matériau d\'aptitude de personnage', 'Matériau d\'amélioration d\'aptitude.\r\nL\'équité est le rivage de la patrie de l\'eau.\r\nL\'équité est le fondement sur lequel Fontaine a été fondée. L\'équité constitue la norme de la loi. Si la loi perd l\'essence de l\'équité, elle deviendra un échiquier joué pour le profit.', 3, 'Donjon de la maîtrise : Rime rythmique (Monday, Thursday, Sunday)'),
(242, 'Philosophie de l\'Équité', 'Matériau d\'aptitude de personnage', 'Matériau d\'amélioration d\'aptitude.\r\nL\'équité est le rivage de la patrie de l\'eau.\r\nL\'équité est le fondement sur lequel Fontaine a été fondée. L\'équité exige le soutien continu et constant de la moralité et de la loi, et la moralité et la loi changent constamment sur la base du principe invariable de l\'équité.', 4, 'Donjon de la maîtrise : Rime rythmique (Monday, Thursday, Sunday)'),
(243, 'Enseignement de la Justice', 'Matériau d\'aptitude de personnage', 'Matériau d\'amélioration d\'aptitude.\r\nLa justice est l\'épée de la patrie de l\'eau.\r\nLa loi de Fontaine marche avec l\'épée de la justice à la main. Sans la direction de la justice, toute loi est réduite à des mots vides de sens, à falsifier et à massacrer au gré des caprices.', 2, 'Donjon de la maîtrise : Maxime (Tuesday, Friday, Sunday)'),
(244, 'Guide de la Justice', 'Matériau d\'aptitude de personnage', 'Matériau d\'amélioration d\'aptitude.\r\nLa justice est l\'épée de la patrie de l\'eau.\r\nLa loi de Fontaine marche avec l\'épée de la justice à la main. La justice doit être aveugle, et ceux qui manient l\'épée de la justice ne doivent pas être induits en erreur par leurs propres intérêts ou affaires.', 3, 'Donjon de la maîtrise : Maxime (Tuesday, Friday, Sunday)'),
(245, 'Philosophie de la Justice', 'Matériau d\'aptitude de personnage', 'Matériau d\'amélioration d\'aptitude.\r\nLa justice est l\'épée de la patrie de l\'eau.\r\nLa loi de Fontaine marche avec l\'épée de la justice à la main. La justice est synonyme de jugement et de réconciliation, la lame du jugement arrête les actions du mal, tandis que la réconciliation maintient l\'ordre.', 4, 'Donjon de la maîtrise : Maxime (Tuesday, Friday, Sunday)'),
(246, 'Enseignement de l\'Ordre', 'Matériau d\'aptitude de personnage', 'Matériau d\'amélioration d\'aptitude.\r\nL\'ordre est le bouclier de la patrie de l\'eau.\r\nToute loi à Fontaine sert à maintenir un ordre stable. Sans ordre, il y a chaos, et il ne peut y avoir d\'équité dans le chaos.', 2, 'Donjon de la maîtrise : Psaume (Wednesday, Saturday, Sunday)'),
(247, 'Guide de l\'Ordre', 'Matériau d\'aptitude de personnage', 'Matériau d\'amélioration d\'aptitude.\r\nL\'ordre est le bouclier de la patrie de l\'eau.\r\nToute loi à Fontaine sert à maintenir un ordre stable. Sans la garantie offerte par l\'ordre, la justice se réduira à une violence sans limites, une arme utilisée par tous contre tous.', 3, 'Donjon de la maîtrise : Psaume (Wednesday, Saturday, Sunday)'),
(248, 'Philosophie de l\'Ordre', 'Matériau d\'aptitude de personnage', 'Matériau d\'amélioration d\'aptitude.\r\nL\'ordre est le bouclier de la patrie de l\'eau.\r\nToute loi à Fontaine sert à maintenir un ordre stable. L\'équité et la justice viennent de l\'ordre et servent l\'ordre. Comme la surface immobile et sans ondulations de ses eaux, l\'ordre est la pierre angulaire de l\'existence de Fontaine.', 4, 'Donjon de la maîtrise : Psaume (Wednesday, Saturday, Sunday)'),
(249, 'Anémoculus', 'Objet d\'aventure', 'De l\'énergie Anémo accumulée au fil du temps qui a fini par durcir. Faites-en une offrande à la Statue et restaurez son pouvoir qui s\'est amenuisé avec le temps.', 1, NULL),
(250, 'Géoculus', 'Objet d\'aventure', 'De l\'énergie Géo accumulée au fil du temps qui a fini par durcir. Faites-en une offrande à la Statue et restaurez son pouvoir qui s\'est amenuisé avec le temps.', 1, NULL),
(251, 'Électroculus', 'Objet d\'aventure', 'De l\'énergie Électro accumulée au fil du temps qui a fini par durcir. Faites-en une offrande à une Statue des Sept et restaurez son pouvoir qui s\'est amenuisé avec le temps.', 1, NULL),
(252, 'Dendroculus', 'Objet d\'aventure', 'De l\'énergie Dendro accumulée au fil du temps qui a fini par durcir. Faites-en une offrande à la Statue et restaurez son pouvoir qui s\'est amenuisé avec le temps.', 1, NULL),
(253, 'Hydroculus', 'Objet d\'aventure', 'De l\'énergie Hydro accumulée au fil du temps qui a fini par durcir. Faites-en une offrande à la Statue et restaurez son pouvoir qui s\'est amenuisé avec le temps.', 1, NULL),
(254, 'Résine éphémère', 'Consommable', 'Un objet rechargeant 60 Résines originelles à l\'utilisation.\r\nUne fois obtenues, les Résines éphémères se dissipent automatiquement au bout d\'un certain moment :\r\n· Après 7 jours à compter du premier lundi suivant leur obtention, les Résines éphémères disparaissent.\r\n· La date d\'expiration des Résines éphémères obtenues lors de semaines différentes est calculée séparément pour chaque date d\'obtention.\r\n\r\nÉnergie pure qui se dissipe avec le temps – ce qui ne diffère en rien de toutes choses en ce monde. Mais saisir le moment qui se présente pour faire du monde un meilleur endroit n\'est-il pas particulièrement adapté pour celui pour qui le temps n\'est qu\'un voyage ?', 4, NULL),
(255, 'Résine fragile', 'Consommable', 'Un objet qui peut être utilisé pour régénérer 60 Résines originelles.\r\nOn peut sentir la douce énergie qui circule à l\'intérieur de ce cristal.\r\nIl contient les flux de toutes sortes d\'énergies à l\'origine de nombreuses merveilles et atrocités de ce monde.', 4, NULL),
(256, 'Agate pourpre', 'Objet d\'aventure', 'Un type de cristal formé par la fusion d\'énergie vitale concentrée et d\'une force particulière émanant des Monts Dosdragon. Il s\'agit d\'un engrais idéal pour faire pousser les Arbres permagelés.', 1, NULL),
(257, 'Farine', 'Ingrédient', 'Poudre moulue à partir de blé. Peu importe le type de plat qui l\'utilise, elle permet de remplir le ventre efficacement.', 1, 'Cuisine (préparation)'),
(258, 'Crème', 'Ingrédient', 'Produit laitier essentiel pour la pâtisserie.', 1, 'Cuisine (préparation)'),
(259, 'Volaille fumée', 'Ingrédient', 'Viande de volaille qui a subi un processus de fumage, lui conférant une texture ferme qui ne manquera pas d\'apporter du goût à votre plat.', 1, 'Cuisine (préparation)'),
(260, 'Beurre', 'Ingrédient', 'Produit laitier qui relève le goût de vos plats les plus basiques.', 1, 'Cuisine (préparation)'),
(261, 'Jambon', 'Ingrédient', 'Une cuisse d\'animal cuite pour en améliorer la conservation. Même la plus fine des tranches est pleine de saveur.', 1, 'Cuisine (préparation)'),
(262, 'Sucre', 'Ingrédient', 'Une poudre obtenue à partir de fleurs sucrantes. Procure de l\'énergie et du bonheur.', 1, 'Cuisine (préparation)'),
(263, 'Confiture', 'Ingrédient', 'Un mélange de purée de fruits et de sucre. Une cuillerée suffit pour réveiller vos papilles.', 1, 'Cuisine (préparation)'),
(264, 'Fromage', 'Ingrédient', 'Produit à base de lait fermenté. Il peut être utilisé de façons variées pour agrémenter vos plats.', 1, 'Cuisine (préparation)'),
(265, 'Bacon', 'Ingrédient', 'Lamelles de porc fumé, croustillantes et un peu grasses.', 1, 'Cuisine (préparation)'),
(266, 'Saucisse', 'Ingrédient', 'Viande hachée enfermée dans un boyau. Sa durée de conservation permet de satisfaire les besoins des férus de viande sur une longue durée.', 1, 'Cuisine (préparation)'),
(267, 'Épice', 'Ingrédient', 'Un condiment issu de la fusion de différentes saveurs que l\'on fabrique à partir de fruits harras séchés. Il apporte toute une gamme de sensations culinaires aux plats dans lesquels il est utilisé. C\'est pourquoi on dit qu\'il n\'y a pas un seul aliment qui ne lui convienne pas.', 1, 'Cuisine (préparation)'),
(268, 'Potion de résistance Pyro', 'Potion', 'Une potion mystérieuse qui permet de résister aux environnements à haute température et augmente la RÉS Pyro.\r\nSon principe d\'action consiste à adapter le corps à la chaleur plutôt que de le refroidir.', 3, 'Synthèse'),
(269, 'Potion de résistance Hydro', 'Potion', 'Une potion mystérieuse qui permet de résister aux environnements humides et augmente la RÉS Hydro.\r\nCette potion agit comme dessicateur, et est particulièrement utile pour la conservation des objets ; elle peut être ingérée pour le même effet.', 3, 'Synthèse'),
(270, 'Potion de résistance Cryo', 'Potion', 'Une potion mystérieuse qui permet de résister aux environnements à basse température et augmente la RÉS Cryo.\r\nLa première gorgée laisse une sensation de froid, qui disparaît avec l\'usage.', 3, 'Synthèse'),
(271, 'Potion de résistance Anémo', 'Potion', 'Une potion mystérieuse qui permet de résister aux vents forts et augmente la RÉS Anémo.\r\nIdéale pour les voyageurs dormant à la belle étoile.', 3, 'Synthèse'),
(272, 'Potion de résistance Électro', 'Potion', 'Une potion mystérieuse qui permet de résister aux chocs électriques et augmente la RÉS Électro.\r\nCette potion laisse une sensation de picotement lorsqu\'on l\'ingère ; son principe repose sur l\'utilisation d\'électrons chargés négativement.', 3, 'Synthèse'),
(273, 'Potion de résistance Géo', 'Potion', 'Une potion mystérieuse qui permet d\'éviter d\'être assailli par les sables ou la poussière et augmente la RÉS Géo.\r\nSon goût étrange rappelle d\'une certaine façon les aimants. Idéale pour garder le voyageur propre sur soi.', 3, 'Synthèse'),
(274, 'Potion de résistance Dendro', 'Potion', 'Une potion mystérieuse qui interrompt la croissance des plantes et augmente la RÉS Dendro.\r\nElle ne tue pas les plantes, mais provoque leur hibernation. Son ingestion est bénéfique pour le corps.', 3, 'Synthèse'),
(275, 'Huile essentielle Pyro', 'Potion', 'Un onguent qui a une forte affinité avec l\'élément Pyro et peut augmenter les DGT infligés par l\'élément Pyro.\r\nFabriquée à partir des matériaux qui ont engendré des éléments Pyro. Après l\'application, vous pouvez concentrer plus efficacement les éléments Pyro et devenez plus passionné.', 3, 'Synthèse'),
(276, 'Huile essentielle Hydro', 'Potion', 'Un onguent qui a une forte affinité avec l\'élément Hydro et peut augmenter les DGT infligés par l\'élément Hydro.\r\nC\'est un médicament humide et glissant pour l\'usage externe qui vous aidera à mieux canaliser les éléments Hydro. Elle a une odeur étrange.', 3, 'Synthèse'),
(277, 'Huile essentielle Cryo', 'Potion', 'Un onguent qui a une forte affinité avec l\'élément Cryo et peut augmenter les DGT infligés par l\'élément Cryo.\r\nAprès l\'application, vous vous sentez rafraîchi et vous transpirez moins. Elle vous aide également à garder la tête froide et rester calme.', 3, 'Synthèse'),
(278, 'Huile essentielle Anémo', 'Potion', 'Un onguent qui a une forte affinité avec l\'élément Anémo et peut augmenter les DGT infligés par l\'élément Anémo.\r\nElle dégage un parfum frais. On dit que le vent vous indique toujours la direction dans les plaines lors du voyage si vous appliquez ce médicament.', 3, 'Synthèse'),
(279, 'Huile essentielle Électro', 'Potion', 'Un onguent qui a une forte affinité avec l\'élément Électro et peut augmenter les DGT infligés par l\'élément Électro.\r\nVous vous sentez engourdi lorsque vous la touchez. Après l\'application, vous maîtrisez mieux l\'élément Électro. Cependant, on dit que cela peut provoquer des étincelles électriques et brûler vos cheveux.', 3, 'Synthèse'),
(280, 'Huile essentielle Géo', 'Potion', 'Un onguent qui a une forte affinité avec l\'élément Géo et peut augmenter les DGT infligés par l\'élément Géo.\r\nVous sentez les grains Géo lorsque vous l\'appliquez. On dit qu\'elle est également un bon médicament pour les blessures physiques.', 3, 'Synthèse'),
(281, 'Huile essentielle Dendro', 'Potion', 'Un onguent qui a une forte affinité avec l\'élément Dendro et peut augmenter les DGT infligés par l\'élément Dendro.\r\nC\'est un médicament nourrissant pour l\'usage externe. L\'appliquer sur la terre peut faire pousser des plantes et vous aidera à guider les éléments Dendro plus facilement.', 3, 'Synthèse'),
(282, 'Bave de Blob', 'Matériau d\'amélioration de personnage et d\'arme', 'Liquide épais que l\'on trouve sur les Blobs. Il s\'agit d\'un matériau commun utilisé chez les forgerons élémentaires.', 1, 'Butin de Blob'),
(283, 'Mucus de Blob', 'Matériau d\'amélioration de personnage et d\'arme', 'Substance légèrement purifiée, sécrétée par un Blob. Éviter tout contact avec la peau.', 2, 'Butin de Blob (Niv.40+)'),
(284, 'Essence de Blob', 'Matériau d\'amélioration de personnage et d\'arme', 'Concentré de Blob. Il peut se déplacer tout seul sans surveillance de votre part.', 3, 'Butin de Blob (Niv.60+)'),
(285, 'Masque endommagé', 'Matériau d\'amélioration de personnage et d\'arme', 'Un masque en os fissuré qui a appartenu à un Brutocollinus.\r\nIl est tellement endommagé qu\'on ne peut plus vraiment appeler ça un masque.', 1, 'Butin de Brutocollinus'),
(286, 'Masque sale', 'Matériau d\'amélioration de personnage et d\'arme', 'Un masque en os taché par une substance non identifiée qui dégage une odeur étrange.\r\nUn Brutocollinus le porterait même dans cet état.', 2, 'Butin de Brutocollinus (Niv.40+)'),
(287, 'Masque sinistre', 'Matériau d\'amélioration de personnage et d\'arme', 'Un masque en os d\'une grande qualité sur lequel un visage terrifiant est peint pour effrayer vos ennemis.\r\nLes Brutocollinus sont très attachés à ce genre d\'objet pour une raison mystérieuse. Certaines sources attribuent le port du masque à leur peur de voir leur propre reflet dans l\'eau.', 3, 'Butin de Brutocollinus (Niv.60+)'),
(288, 'Parchemin divinatoire', 'Matériau d\'amélioration de personnage et d\'arme', 'Un parchemin qui semble parler de magie. Il dégage une étrange chaleur.', 1, 'Butin de Brutoshaman'),
(289, 'Parchemin sigillé', 'Matériau d\'amélioration de personnage et d\'arme', 'Un ancien parchemin aux dessins étranges. En reproduisant les gestes des personnages, il est possible de recréer une certaine magie miraculeuse.', 2, 'Butin de Brutoshaman (Niv.40+)'),
(290, 'Parchemin maudit', 'Matériau d\'amélioration de personnage et d\'arme', 'Un parchemin ancien très difficile à déchiffrer. Ceux qui y sont parvenus sont tous devenus fous.', 3, 'Butin de Shaman Brutocollinus (Niv.60+)'),
(291, 'Pointe de flèche robuste', 'Matériau d\'amélioration de personnage et d\'arme', 'Une pointe de flèche rudimentaire. Bien que son état laisse à désirer, elle peut se révéler mortelle si la flèche est tirée au bon moment.', 1, 'Butin de Tireur Brutocollinus'),
(292, 'Pointe de flèche aiguisée', 'Matériau d\'amélioration de personnage et d\'arme', 'Une pointe de flèche de haute qualité. Parfaitement aiguisée, elle traverse aisément la chair.', 2, 'Butin de Tireur Brutocollinus (Niv.40+)'),
(293, 'Pointe de flèche usée', 'Matériau d\'amélioration de personnage et d\'arme', 'Une vieille pointe de flèche recouverte de sang. Elle a perdu son tranchant depuis longtemps et ne sert plus d\'arme.\r\nCependant, les chasseurs la portent fièrement à leur cou comme une amulette ou une médaille.', 3, 'Butin de Tireur Brutocollinus (Niv.60+)'),
(294, 'Corne lourde', 'Matériau d\'amélioration de personnage et d\'arme', 'Une corne utilisée par les Brutocollinus pour prévenir leurs alliés d\'un danger imminent. Bien que cet objet soit déjà bien endommagé, il n\'est pas prêt de rendre l\'âme.', 2, 'Butin de Brutoviandu'),
(295, 'Corne en bronze noir', 'Matériau d\'amélioration de personnage et d\'arme', 'Une corne de démon en métal. Elle peut souvent être obtenue des Brutocollinus musclés en raison de son poids.', 3, 'Butin de Brutoviandu (Niv.40+)'),
(296, 'Corne à cristal noir', 'Matériau d\'amélioration de personnage et d\'arme', 'Une corne en métal ornée d\'un cristal noir. Probablement utilisée par les Brutocollinus lors de rituels, elle se trouve dans un état quasiment neuf.', 4, 'Butin de Brutoviandu (Niv.60+)'),
(297, 'Statuette sinistre', 'Matériau d\'amélioration de personnage et d\'arme', 'Une statuette sculptée à l\'effigie d\'une personne inconnue.\r\nElle a été témoin du passage du temps, mais demeure intacte. Son précédent propriétaire semble l\'avoir chérie comme une icône sainte.', 2, 'Butin de Serpent noir'),
(298, 'Statuette maudite', 'Matériau d\'amélioration de personnage et d\'arme', 'Une statuette sculptée à l\'effigie d\'une personne inconnue qui ne possédait qu\'un œil.\r\nVous sentez une chaleur s\'en dégager lorsque vous la tenez, comme si c\'était l\'épaule d\'un ami cher. Peut-être que cette statuette peut donner le courage de persévérer aux gens qui comprennent son importance.', 3, 'Butin de Serpent noir (Niv.40+)'),
(299, 'Statuette maléfique', 'Matériau d\'amélioration de personnage et d\'arme', 'Une statuette à un œil qui émet une certaine énergie et dont la matière première est inconnue.\r\nEn la regardant, vous pouvez presque entendre un murmure à peine audible...\r\n« Vois, mon enfant. Tout ce qui se trouve sous le trône du ciel finira détruit dans la tourmente. L\'obscurité de la sérénité éternelle nous enveloppera tous. »', 4, 'Butin de Serpent noir (Niv.60+)'),
(300, 'Branche morte des lignes énergétiques', 'Matériau d\'amélioration de personnage et d\'arme', 'Branche fragile provenant des profondeurs de la terre. Malgré son piteux état, elle a survécu à l\'épreuve du temps et a conservé une once de ses pouvoirs.', 2, 'Butin de Mage de l\'Abîme'),
(301, 'Feuilles mortes des lignes énergétiques', 'Matériau d\'amélioration de personnage et d\'arme', 'Ces feuilles proviennent des profondeurs de la terre. Une énergie vitale émane d\'elles comme si elles étaient encore rattachées à leur arbre.', 3, 'Butin de Mage de l\'Abîme (Niv.40+)'),
(302, 'Pousse des lignes énergétiques', 'Matériau d\'amélioration de personnage et d\'arme', 'Cette pousse a été extraite à partir d\'énormes racines qui poussent sous terre. Elle déborde de vitalité comme si elle était encore rattachée à la racine mère et déploie de nouvelles feuilles.', 4, 'Butin de Mage de l\'Abîme (Niv.60+)'),
(303, 'Dispositif du chaos', 'Matériau d\'amélioration de personnage et d\'arme', 'Une pièce qui effectuait la maintenance d\'une structure antique. Son mécanisme est un plaisir pour les yeux et relève d\'une ingénierie exquise.', 2, 'Butin de Gardien des ruines'),
(304, 'Circuit du chaos', 'Matériau d\'amélioration de personnage et d\'arme', 'Un circuit qui commandait les mouvements d\'une structure antique. De nos jours, personne ne sait comment il fonctionne.', 3, 'Butin de Gardien des ruines (Niv.40+)'),
(305, 'Noyau du chaos', 'Matériau d\'amélioration de personnage et d\'arme', 'Un noyau qui faisait fonctionner une structure antique. Le monde ne serait plus le même si une telle technologie parvenait à être reproduite.', 4, 'Butin de Gardien des ruines (Niv.60+)'),
(306, 'Pollen d\'herbe brumeuse', 'Matériau d\'amélioration de personnage et d\'arme', 'De mystérieuses spores produites par l\'herbe brumeuse lorsqu\'elles sont dans un espace confiné. Il s\'agit de la nourriture préférée des lucioles.', 2, 'Butin de Mage luciole des Fatui'),
(307, 'Herbe brumeuse', 'Matériau d\'amélioration de personnage et d\'arme', 'Entièrement bien conservée. Les Lucioles Électro ont un goût prononcé pour ces herbes, ce qui permet de les contrôler.', 3, 'Butin de Mage luciole des Fatui (Niv.40+)'),
(308, 'Mèches d\'herbe brumeuse', 'Matériau d\'amélioration de personnage et d\'arme', 'Un bouquet d\'herbe brumeuse rare qui dégage une lueur blême, il risque d\'attirer les lucioles.', 4, 'Butin de Mage luciole des Fatui (Niv.60+)'),
(309, 'Dague de chasse rituelle', 'Matériau d\'amélioration de personnage et d\'arme', 'Une arme tranchante en alliage. Elle n\'a plus de propriétaire depuis longtemps, mais sa lame brille toujours d\'un éclat menaçant.', 2, 'Butin d\'Usurier des Fatui'),
(310, 'Dague rituelle de l\'Usurier', 'Matériau d\'amélioration de personnage et d\'arme', 'Une arme à la forme étrange, fabriquée grâce à une technologie de pointe snezhnayienne. Il est impossible de la manier sans avoir effectué un entraînement spécial.', 3, 'Butin d\'Usurier des Fatui (Niv.40+)'),
(311, 'Grande dague de l\'Inspecteur', 'Matériau d\'amélioration de personnage et d\'arme', 'Une arme qui a servi à « régler des comptes ». Personne n\'a les yeux derrière la tête, alors cette dague et ses techniques de maniement ont spécifiquement été conçues pour pallier cette faiblesse.', 4, 'Butin d\'Usurier des Fatui (Niv.60+)'),
(312, 'Insigne de nouvelle recrue', 'Matériau d\'amélioration de personnage et d\'arme', 'Un insigne servant à identifier les nouvelles recrues. Personne ne sait ni à qui il appartient, ni pourquoi cette personne a décidé de rejoindre l\'armée des Fatui.', 1, 'Butin de Tirailleur Fatui'),
(313, 'Insigne de sergent', 'Matériau d\'amélioration de personnage et d\'arme', 'Un insigne servant à identifier les sergents. Il est sans doute porteur d\'émotions complexes.', 2, 'Butin de Tirailleur Fatui (Niv.40+)'),
(314, 'Insigne d\'officier', 'Matériau d\'amélioration de personnage et d\'arme', 'Un insigne servant à identifier les officiers. Compte tenu de la taille de l\'armée des Fatui, ceux qui parviennent à atteindre le rang d\'officier doivent sûrement être un minimum doués.', 3, 'Butin de Tirailleur Fatui (Niv.60+)'),
(315, 'Insigne des Pilleurs', 'Matériau d\'amélioration de personnage et d\'arme', 'Un insigne porté avec fierté et qui indique le rang de son propriétaire au sein des Pilleurs de trésors. Leur soif de richesse ne pourra jamais être assouvie... Ceci dit, depuis quand peut-on se vanter d\'être un voleur ?', 1, 'Butin de Pilleur de trésors'),
(316, 'Insigne de corbeau en argent', 'Matériau d\'amélioration de personnage et d\'arme', 'Un insigne en forme de corbeau porté par les Pilleurs de trésors pour se reconnaître entre eux. Pas besoin d\'entretien pour adhérer à leur groupe, tant qu\'on est suffisamment motivé par le gain et que le courage ne nous fait pas défaut.', 2, 'Butin de Pilleur de trésors (Niv.40+)'),
(317, 'Insigne de corbeau en or', 'Matériau d\'amélioration de personnage et d\'arme', 'Un insigne qui représente la fierté des Pilleurs de trésors. Ils ne reculeront jamais devant rien pour s\'emparer du moindre trésor, qu\'il soit enfoui sous de vastes plaines ou sous l\'océan.', 3, 'Butin de Pilleur de trésors (Niv.60+)'),
(318, 'Nectar de Fleur mensongère', 'Matériau d\'amélioration de personnage et d\'arme', 'Nectar extrait du pistil d\'une Fleur mensongère. Il contient des traces d\'éléments.\r\nSon goût rappelle celui de la fleur sucrante.', 1, 'Butin de Fleur mensongère'),
(319, 'Nectar miroitant', 'Matériau d\'amélioration de personnage et d\'arme', 'Un nectar de Fleur mensongère concentré en éléments.\r\nToute la communauté scientifique s\'accorde généralement sur le fait que les Fleurs mensongères occupent une place à part parmi les plantes élémentaires, mais nombre d\'interrogations demeurent quant à ses habitudes prédatrices.', 2, 'Butin de Fleur mensongère (Niv.40+)'),
(320, 'Nectar élémentaire', 'Matériau d\'amélioration de personnage et d\'arme', 'Un nectar de Fleur mensongère épais et collant, empli d\'énergie.\r\nLa Fleur mensongère, comme son nom l\'indique, trompe sa proie pour s\'alimenter – peut-être dans le but d\'évoluer vers une forme plus puissante ?', 3, 'Butin de Fleur mensongère (Niv.60+)'),
(321, 'Fragment d\'os fragile', 'Matériau d\'amélioration de personnage et d\'arme', 'Fragment d\'os retrouvé sur le corps d\'un Élémentosaure.\r\nMalgré sa fragilité, une mystérieuse puissance semble s\'en dégager.', 2, 'Butin de Géosaure'),
(322, 'Fragment d\'os solide', 'Matériau d\'amélioration de personnage et d\'arme', 'Fragment d\'os qui semble très apprécié des Élémentosaures.\r\nL\'os doit être assez ancien. Ces créatures cousines du dragon ont beau n\'avoir pas de langage à proprement parler, elles semblent malgré tout être très attachées à ce type d\'objet.', 3, 'Butin de Géosaure (Niv.40+)'),
(323, 'Fragment d\'os fossilisé', 'Matériau d\'amélioration de personnage et d\'arme', 'Fragment d\'os obtenu parfois après avoir vaincu des Élémentosaures.\r\nTous les Élémentosaures rêvent de devenir un jour dragon, et vénèrent avec passion ce type de fossile ; peut-être pensent-ils bénéficier ainsi de la longévité et de la puissance des vrais dragons.', 4, 'Butin de Géosaure (Niv.60+)'),
(324, 'Garde-main ancien', 'Matériau d\'amélioration de personnage et d\'arme', 'Un garde-main qui a connu de nombreuses batailles et qui autrefois était monté sur une lame inazumienne.\r\nPour les épéistes expérimentés, la tsuba permet de bloquer la lame de l\'adversaire. Pour les débutants, elle permet de protéger sa propre main.', 1, 'Butin de Nobushi'),
(325, 'Garde-main kageuchi', 'Matériau d\'amélioration de personnage et d\'arme', 'Un garde-main que l\'on monte sur une épée de valeur.\r\nInazuma a une longue tradition de fabrication d\'épées. Lorsqu\'un artisan veut forger une épée, il fabrique souvent plusieurs exemplaires du même modèle pour en sélectionner le meilleur, le shinuchi, les autres étant appelés les kageuchi.', 2, 'Butin de Nobushi (Niv.40+)'),
(326, 'Garde-main célèbre', 'Matériau d\'amélioration de personnage et d\'arme', 'Un magnifique garde-main monté sur une épée célèbre.\r\nCertains croient fermement que les épées ont leur âme propre, c\'est pourquoi ils leur donnent des noms. Ceux qui montrent de l\'attachement à leur lame seront récompensés en retour. Mais ceux qui voient leur épée comme une amie ou même une amante doivent savoir que cette affection s\'abreuve de sang et de mort. Les passions de ceux qui baignent dans les effusions pourpres et dansent avec les lames se verront un jour refroidies par l\'acier même qu\'ils avaient aimé.', 3, 'Butin de Nobushi (Niv.60+)'),
(327, 'Engrenage du chaos', 'Matériau d\'amélioration de personnage et d\'arme', 'Provient d\'un ancien mécanisme des ruines désormais inactif.\r\nC\'est la source d\'énergie de ce mécanisme biomimétique.', 2, 'Butin de Sentinelle des ruines'),
(328, 'Axe du chaos', 'Matériau d\'amélioration de personnage et d\'arme', 'Provient d\'un ancien mécanisme des ruines désormais inactif.\r\nLa haute technologie et la puissance de cette machine ont été perdues quand elle n\'a pas réussi à maintenir son intégrité structurelle.', 3, 'Butin de Sentinelle des ruines (Niv.40+)'),
(329, 'Œil du chaos', 'Matériau d\'amélioration de personnage et d\'arme', 'Provient d\'un ancien mécanisme des ruines désormais inactif.\r\nLe mot « ruines » fait référence aux zones dans lesquelles ils sont les plus actifs. Cependant, les mécanismes des ruines n\'ont en réalité que peu de points communs avec les débris dans lesquels ils se trouvent habituellement.\r\nDans un passé lointain, ces ruines appartenaient à une civilisation assez puissante pour presque toucher le dôme du firmament, pendant qu\'elles restaient à attendre dans les tréfonds de la terre, l\'ordre de se soulever et de renverser tous les royaumes.', 4, 'Butin de Sentinelle des ruines (Niv.60+)'),
(330, 'Prisme sombre', 'Matériau d\'amélioration de personnage et d\'arme', 'Ce prisme pouvait autrefois polariser la lumière, mais il s\'est assombri au fil des ans.', 2, 'Butin de Suivante des Fatui'),
(331, 'Prisme de cristal', 'Matériau d\'amélioration de personnage et d\'arme', 'Un prisme qui peut dévier la lumière.\r\nIl est possible de créer des illusions à l\'aide de tels prismes, ce qui en fait, dans un sens, des appareils capables d\'influencer le cœur des hommes.', 3, 'Butin de Suivante des Fatui (Niv.40+)'),
(332, 'Prisme polarisant', 'Matériau d\'amélioration de personnage et d\'arme', 'Un prisme qui peut dévier la lumière.\r\nDans l\'obscurité, de tels prismes peuvent diffuser une faible lumière sur une zone plus large. Peut-être est-ce dû à cette caractéristique que ce genre de prismes peut apporter une certaine quiétude à ceux dont le cœur est souvent enveloppé de ténèbres.', 4, 'Butin de Suivante des Fatui (Niv.60+)'),
(333, 'Coquille spectrale', 'Matériau d\'amélioration de personnage et d\'arme', 'Un noyau d\'énergie qui permet aux Spectres de flotter. Il contient une petite quantité d\'énergie élémentaire.\r\nLa plupart de l\'énergie restante s\'est déjà dissipée.', 1, 'Butin de Spectre'),
(334, 'Cœur spectral', 'Matériau d\'amélioration de personnage et d\'arme', 'Un noyau d\'énergie qui permet aux Spectres de flotter.\r\nCes formes de vie élémentaires ont des habitudes de comportement étranges et augmenteront périodiquement leur nombre avant de disparaître.', 2, 'Butin de Spectre (Niv.40+)'),
(335, 'Noyau spectral', 'Matériau d\'amélioration de personnage et d\'arme', 'Un noyau d\'énergie intégral qui peut être ramassé après avoir vaincu un Spectre.\r\nUn débat revient souvent parmi ceux qui aiment diviser le monde entre gagnants et perdants : Lequel flotte le mieux dans les airs ? Un Anémoblob ou un Spectre ?', 3, 'Butin de Spectre (Niv.60+)'),
(336, 'Griffe dissimulée', 'Matériau d\'amélioration de personnage et d\'arme', 'Le tissu résiduel provenant du corps d\'un canidé de faille.\r\nEn toute logique, cet objet qui n\'appartient guère à ce monde n\'aurait pas dû subsister après l\'anéantissement de ces créatures anormales.', 2, 'Butin de Loup de faille'),
(337, 'Ongle dissimulé', 'Matériau d\'amélioration de personnage et d\'arme', 'Du tissu de chair provenant du corps d\'un canidé de faille.\r\nAussi étonnantes qu\'elles soient, ces créatures d\'Alfisol ne sont rien comparées à Durin, l\'Humus.', 3, 'Butin de Loup de faille (Niv.40+)'),
(338, 'Serre dissimulée', 'Matériau d\'amélioration de personnage et d\'arme', 'La griffe tranchante d\'un canidé de faille.\r\nToutes les merveilles du monde ne font pas le poids face au Cretaceus, la plus belle création de toutes.', 4, 'Butin de Loup de faille (Niv.60+)'),
(339, 'Spores de Fongus', 'Matériau d\'amélioration de personnage et d\'arme', 'Une petite quantité de poudre de spores lumineuses laissée par un Fongus. Un éternuement suffit à la faire s\'envoler sans aucune trace.', 1, 'Butin de Fongus'),
(340, 'Pollen fluorescent', 'Matériau d\'amélioration de personnage et d\'arme', 'Un petit paquet de spores fluorescentes qui provient d\'un Fongus. Il s\'agit d\'un ingrédient médicinal commun dans les pays avec des forêts tropicales. Les marchands en vendent souvent dans des sachets en papier.', 2, 'Butin de Fongus (Niv.40+)'),
(341, 'Poussière cristalline de sporange', 'Matériau d\'amélioration de personnage et d\'arme', 'Un gros amas de poudre de spores qui a été prélevé d\'un kyste complet. Si vous le placez dans un environnement adapté, celui-ci pourrait se remplir de Fongus avec le temps...', 3, 'Butin de Fongus (Niv.60+)'),
(342, 'Noyau fongique inactivé', 'Matériau d\'amélioration de personnage et d\'arme', 'Du tissu d\'un Fongus qui a été exposé à une stimulation élémentaire intense.\r\nSa texture apaisante en fait un excellent remède contre le stress. Cependant, son utilisation peut aussi facilement conduire à une infestation de Fongus, il ne convient donc pas à un usage commercial.', 2, 'Butin de Fongus activé'),
(343, 'Noyau fongique dormant', 'Matériau d\'amélioration de personnage et d\'arme', 'Du tissu organique d\'un Fongus qui a été exposé à une stimulation élémentaire intense.\r\nIl y a eu un jour un long débat entre les chercheurs de l\'Amurta pour savoir si les Fongus devaient être classés dans la catégorie des plantes ou des animaux. Finalement, ils sont arrivés à un consensus et ont définitivement clos l\'affaire : les Fongus sont considérés comme une catégorie à part, ni végétale ni animale.', 3, 'Butin de Fongus activé (Niv.40+)'),
(344, 'Noyau fongique robuste', 'Matériau d\'amélioration de personnage et d\'arme', 'Du tissu organique d\'un Fongus qui a été exposé à une stimulation élémentaire intense.\r\nOn pense généralement que les Fongus ont évolué et gagné en mobilité pour mieux disperser leurs spores.\r\nUn poète a un jour affirmé que les Fongus avaient un rêve commun dans lequel leurs spores recouvraient chaque parcelle des montagnes et des océans, et que le monde était plus paisible que jamais. Puis les Fongus se transformèrent en poissons, en oiseaux, en bêtes, en hommes et en dieux qui dépendaient tous de la pluie, de la terre et de leur propre espèce qui était revenue sur terre pour survivre.', 4, 'Butin de Fongus activé (Niv.60+)'),
(345, 'Satin rouge délavé', 'Matériau d\'amélioration de personnage et d\'arme', 'Un morceau de satin rouge légèrement déchiré utilisé pour couvrir les yeux.\r\nDe nombreux Érémites ont tendance à se couvrir les yeux avec des tissus, une pratique dont l\'origine est perdue depuis longtemps dans les sables du temps.', 1, 'Butin d\'Érémite'),
(346, 'Soie rouge brodée', 'Matériau d\'amélioration de personnage et d\'arme', 'Un morceau de soie rouge garni de décorations simples.\r\nUn guerrier aguerri peut détecter les faiblesses dans les yeux de son ennemi et en tirer ainsi profit. Les Érémites ont perdu leur maître il y a fort longtemps, et aujourd\'hui, ils ne font plus confiance qu\'à eux-mêmes. On dit qu\'ils sont plus que convaincus que toutes les choses du monde les trahissent, même leurs propres yeux.', 2, 'Butin d\'Érémite (Niv.40+)');
INSERT INTO `material` (`id`, `name`, `type`, `description`, `rarity`, `source`) VALUES
(347, 'Brocart rouge luxueux', 'Matériau d\'amélioration de personnage et d\'arme', 'Une pièce de brocart rouge avec des motifs dorés tissés à l\'intérieur.\r\nLe peuple perdu qui a vu le jour parmi les sables dorés du désert entonne parfois un chant dont la signification a été oubliée depuis longtemps pour louer le soleil brûlant qui illumine la terre comme jamais et se lamenter devant les dernières lueurs du crépuscule. La chanson se termine par la répétition de mots inconnus, qui seraient les noms de leurs anciennes divinités. Lorsque le roi du désert était en vie, ses sujets se couvraient les yeux par crainte d\'être brûlés par sa radiance semblable à celle du soleil.', 3, 'Butin d\'Érémite (Niv.60+)'),
(348, 'Conteneur du chaos', 'Matériau d\'amélioration de personnage et d\'arme', 'Provient d\'un ancien Dragon des ruines désormais inactif.\r\nLes Dragons des ruines peuvent absorber l\'énergie élémentaire et la stocker dans un composant situé à l\'intérieur de leur «cou».', 2, 'Butin de Dragon des ruines'),
(349, 'Module du chaos', 'Matériau d\'amélioration de personnage et d\'arme', 'Provient d\'un ancien Dragon des ruines désormais inactif.\r\nLes dragons étaient autrefois l\'incarnation charnelle la plus primitive et la plus ancienne des éléments suprêmes jusqu\'à ce qu\'ils soient vaincus, conquis ou assimilés.', 3, 'Butin de Dragon des ruines (Niv.40+)'),
(350, 'Vis du chaos', 'Matériau d\'amélioration de personnage et d\'arme', 'Provient d\'un ancien Dragon des ruines désormais inactif.\r\nParmi les anciens royaumes perdus, il y avait un groupe de personnes qui étaient obsédées par l\'idée de mimétisme. Qu\'il s\'agisse des éléments purs et ordonnés des Hypostases ou de l\'ancien pouvoir primitif des dragons, ces personnes pensaient qu\'ils pouvaient tous être reproduits et modifiés au point de surpasser leurs homologues naturels.\r\nAinsi, une forme corporelle supérieure et immaculée pourrait remplacer l\'ordre voué à la décomposition et à la destruction.', 4, 'Butin de Dragon des ruines (Niv.60+)'),
(351, 'Prisme endommagé', 'Matériau d\'amélioration de personnage et d\'arme', 'Un composant endommagé que vous avez obtenu après avoir vaincu une Structure primale.\r\nCes machines aux formes singulières sont les protectrices éternelles des ruines du désert et sont assez puissante pour percer les montagnes et remodeler les rochers. Les principes de fonctionnement de ces machines sont similaires aux nombreux mécanismes mystérieux à la surface et sous terre.', 2, 'Butin de Structure primale'),
(352, 'Prisme trouble', 'Matériau d\'amélioration de personnage et d\'arme', 'Un composant usé que vous avez obtenu après avoir vaincu une Structure primale.\r\nOn dit que ces machines sont différentes des mécanismes des ruines des profondeurs du désert en ce qu\'elles n\'ont pas été créées pour conquérir ou amener la destruction.', 3, 'Butin de Structure primale (Niv.40+)'),
(353, 'Prisme radieux', 'Matériau d\'amélioration de personnage et d\'arme', 'Un composant de fabrication subtile que vous avez obtenu après avoir vaincu une Structure primale.\r\nLe roi du désert a autrefois fait un rêve et a jadis formulé beaucoup de promesses.\r\nIl est dit dans les légendes du désert que ces machines avaient à l\'origine été créées pour réaliser ces promesses. Aujourd\'hui, cependant, ces promesses et ce rêve ont été oubliés et ne survivent qu\'à l\'intérieur de la mémoire mécanique de ces engins.', 4, 'Butin de Structure primale (Niv.60+)'),
(354, 'Coque desséchée', 'Matériau d\'amélioration de personnage et d\'arme', 'La coque desséchée d\'une bête sacralisée.\r\nUne preuve de la calamité qui a ravagé le monde dans le passé, et qui aujourd\'hui ne peut être trouvée que dans des endroits où personne n\'ose s\'aventurer.', 2, 'Butin de bête sacralisée'),
(355, 'Coque robuste', 'Matériau d\'amélioration de personnage et d\'arme', 'La coque solide d\'une bête sacralisée.\r\nLes restes d\'une bête née de la ruine, qui rongeait la chair et les os de grands êtres déchus. Même si ces bêtes ont reçu une once de pouvoir des divinités d\'antan, elles connaîtront la même fin que ces derniers.', 3, 'Butin de bête sacralisée (Niv.40+)'),
(356, 'Coque marquée', 'Matériau d\'amélioration de personnage et d\'arme', 'La coque d\'une bête sacralisée sur laquelle figurent des inscriptions anciennes.\r\nSur cette carapace pâle persistent faiblement les chagrins d\'un âge lointain. Le jour viendra où les os des bêtes deviendront de la poussière blanche dispersée par le vent, et alors, les défunts exaltés pourront enfin saluer leur dernier repos...', 4, 'Butin de bête sacralisée (Niv.60+)'),
(357, 'Fleur non épanouie d\'origine inconnue', 'Matériau d\'amélioration de personnage et d\'arme', 'Une fleur sauvage qu\'un Brutovéreux chérissait, cueillie avant qu\'elle ne puisse fleurir.\r\nCe type de Brutocollinus erre dans les bois sans se charger d\'objets, en dehors de ces fleurs qui n\'ont pas encore fleuri.', 2, 'Butin de Brutovéreux'),
(358, 'Fleur précieuse', 'Matériau d\'amélioration de personnage et d\'arme', 'Une fleur sauvage qu\'un Brutovéreux chérissait, dépourvue cependant de toute qualité particulière.\r\nDans de nombreuses cultures, les fleurs peuvent être utilisées comme cadeaux ou offrandes pour exprimer ses sentiments.', 3, 'Butin de Brutovéreux (Niv.40+)'),
(359, 'Fleur épanouie d\'un nomade', 'Matériau d\'amélioration de personnage et d\'arme', 'Une fleur sauvage épanouie qu\'un Brutovéreux chérissait, dépourvue cependant de toute qualité particulière.\r\nL\'étranger de l\'éternité ne demande pas de récompenses, mais seulement de voir les choses se concrétiser...', 4, 'Butin de Brutovéreux (Niv.60+)'),
(360, 'Perle transocéanique', 'Matériau d\'amélioration de personnage et d\'arme', 'Un petit cristal obtenu en vainquant un Aberrant fontemarin.\r\nLes Aberrants fontemarins viennent des mers de Fontaine et on pense qu\'ils sont une forme de vie unique née de l\'énergie mystérieuse de l\'eau.', 1, 'Butin d\'Aberrant fontemarin'),
(361, 'Morceau transocéanique', 'Matériau d\'amélioration de personnage et d\'arme', 'Un cristal obtenu en vainquant un Aberrant fontemarin, dégageant une énergie plutôt mystérieuse.\r\nBien que de nombreuses légendes parlent de grandes formes de vie résidant dans des mers éloignées hors de portée humaine, les océans ordinaires font déjà pâle figure face aux eaux vibrantes de Fontaine.', 2, 'Butin d\'Aberrant fontemarin (Niv.40+)'),
(362, 'Cristal xénochromatique', 'Matériau d\'amélioration de personnage et d\'arme', 'Un grand cristal obtenu en vainquant un Aberrant fontemarin, contenant une énergie mystérieuse.\r\nBien que les rivières et les mers prennent leur source à Fontaine, l\'eau perd ses propriétés uniques une fois hors des frontières de la nation.', 3, 'Butin d\'Aberrant fontemarin (Niv.60+)'),
(363, 'Engrenage de maillage', 'Matériau d\'amélioration de personnage et d\'arme', 'Un engrenage de Méka horlogoïde.\r\nC\'est une partie cruciale de la structure de transmission, et l\'ensemble du système fonctionnera mal si ce module est absent.', 1, 'Butin de Méka horlogoïde'),
(364, 'Engrenage à coupe droite', 'Matériau d\'amélioration de personnage et d\'arme', 'Un engrenage de Méka horlogoïde.\r\nBien que de nombreuses percées aient été réalisées dans le domaine de la cinématique des Mékas horlogoïdes, les recherches en dynamique n\'ont pas connu d\'avancées majeures depuis les fondations posées par Alain Guillotin il y a quatre cents ans.', 2, 'Butin de Méka horlogoïde (Niv.40+)'),
(365, 'Engrenage dynamique artificié', 'Matériau d\'amélioration de personnage et d\'arme', 'Un engrenage de Méka horlogoïde.\r\nLes traitements de précision et l\'avancement de la science des matériaux sont les clés du développement des Mékas horlogoïdes à Fontaine. Avec l\'aide d\'un équipement de pointe, n\'importe quel ingénieur est capable de fabriquer un méka qui surpasse les créations de l\'ère du maître-artificier Alain Guillotin.', 3, 'Butin de Méka horlogoïde (Niv.60+)'),
(366, 'Goutte d\'eau croupie', 'Matériau d\'amélioration de personnage et d\'arme', 'Une petite goutte d\'eau obtenue en vainquant un Fantasme Hydro croupi.\r\nLa force vitale qu\'elle contient ne s\'est pas encore complètement dissipée.', 2, 'Butin de Fantasme Hydro croupi'),
(367, 'Cuillère d\'eau croupie', 'Matériau d\'amélioration de personnage et d\'arme', 'De l\'eau obtenue en vainquant un Fantasme Hydro croupi.\r\nLa force vitale qu\'elle contient ne s\'est pas encore complètement dissipée.\r\nAvec la disparition actuelle des Océanides de Fontaine, d\'étranges entités élémentaires loin d\'être gracieuses sont apparues dans les eaux.', 3, 'Butin de Fantasme Hydro croupi (Niv.40+)'),
(368, 'Jeune Fantasme Hydro croupi', 'Matériau d\'amélioration de personnage et d\'arme', 'Une nouvelle forme de vie apparue après la dispersion d\'un Fantasme Hydro croupi, actuellement emprisonnée à l\'intérieur d\'une bouteille.\r\nLa vie contenue dans les eaux est intangible, et la vie intangible est née de l\'eau et emprunte sa forme.', 4, 'Butin de Fantasme Hydro croupi (Niv.60+)'),
(369, 'Noyau de faille', 'Matériau d\'amélioration de personnage et d\'arme', 'Un fragment laissé par un Primus briseur.\r\nIl ne ressemble à aucune autre forme de vie sur terre.', 2, 'Butin de Primus briseur'),
(370, 'Synapse étrangère', 'Matériau d\'amélioration de personnage et d\'arme', 'Un fragment laissé par un Primus briseur.\r\nCette étrange forme de vie tire son origine d\'Elynas et habite la région éponyme.', 3, 'Butin de Primus briseur (Niv.40+)'),
(371, 'Noyau d\'organisme d\'extramonde', 'Matériau d\'amélioration de personnage et d\'arme', 'Le noyau laissé par un Primus briseur.\r\nIl n\'y a aucun moyen d\'annihiler cette forme de vie : même réduit à néant, son noyau ressuscitera le lendemain ou dans des décennies. Heureusement, il n\'est ni agressif ni intelligent, et les Fontainois gardent simplement leurs distances.', 4, 'Butin de Primus briseur (Niv.60+)'),
(372, 'Vieille montre à gousset d\'opérateur', 'Matériau d\'amélioration de personnage et d\'arme', 'Une montre de poche standard donnée aux agents des Fatui.\r\nL\'érosion et l\'oxydation du boîtier témoignent des journées passées par son propriétaire en tant qu\'exécuteur.', 2, 'Butin d\'Opérateur Fatui'),
(373, 'Montre à gousset d\'opérateur', 'Matériau d\'amélioration de personnage et d\'arme', 'Une montre de poche standard donnée aux agents des Fatui.\r\nLa tête de la montre n\'a pas été bien conçue et le boîtier extérieur présente beaucoup trop d\'angles, ce qui peut provoquer une usure excessive des vêtements. Ainsi, certains fourreront cette montre de poche dans des boîtes plus pratiques et moins clinquantes lors de son usage.', 3, 'Butin d\'Opérateur Fatui (Niv.40+)'),
(374, 'Constance d\'opérateur', 'Matériau d\'amélioration de personnage et d\'arme', 'Un accessoire avec un œil maléfique installé dessus, source du pouvoir des opérateurs des Fatui.\r\nCela dit, le leur prendre n\'enlèverait rien au fait que lesdits opérateurs aient des capacités physiques bien au-delà de celles des humains ordinaires, forgées par de longues années d\'entraînement et d\'expérience au combat. Eux sont bien conscients qu\'ils sacrifient leur passé, leur présent et leur avenir sur l\'autel du paradis idéal des Fatui. Ainsi, les dommages qu\'ils s\'infligent en utilisant le pouvoir d\'un œil maléfique leur importent peu.', 4, 'Butin d\'Opérateur Fatui (Niv.60+)'),
(375, 'Graine d\'ouragan', 'Matériau d\'amélioration de personnage', 'La forme effondrée d\'une Hypostase Anémo après avoir été vaincue. Elle contient un pouvoir Anémo d\'une grande pureté.\r\nLa légende raconte qu\'un petit papillon fragile possède également le pouvoir de provoquer un ouragan. Ce papillon fait partie du vent pur, attendant avec patience le jour pour invoquer à nouveau l\'ouragan.', 4, 'Butin d\'Hypostase Anémo (Niv.30+)'),
(376, 'Prisme d\'éclair', 'Matériau d\'amélioration de personnage', 'Un matériel élémentaire utilisé par des Hypostases Électro pour faire converger les éléments à proximité et réparer les dommages. Il contient un pouvoir Électro d\'une grande pureté.\r\nUn prisme commun peut distinguer les couleurs qui composent la lumière blanche, tandis que un prisme d\'éclair peut faire converger les flux énergiques en un pur éclair. Bien que l\'Hypostase Électro a été vaincue, les éléments Électro continuent de converger vers ce prisme.', 4, 'Butin d\'Hypostase Électro (Niv.30+)'),
(377, 'Plume de Stormterror', 'Matériau d\'amélioration de personnage', 'Une plume provenant du Dragon de l\'Est.\r\nSoufflée par les vents pendant pas moins d\'un millénaire, elle invoquerait presque une brise pour la libérer de votre sac, mais son propriétaire vous l\'a confiée en gage de sa reconnaissance.', 5, 'Défi de Stormterror (Niv.70+)'),
(378, 'Griffe de Stormterror', 'Matériau d\'amélioration de personnage', 'Une griffe provenant du Dragon de l\'Est.\r\nElle fut contaminée par le sang de Durin, le Dragon des Ténèbres, mais grâce à vos efforts, elle n\'est plus empoisonnée.', 5, 'Défi de Stormterror (Niv.70+)'),
(379, 'Souffle de Stormterror', 'Matériau d\'amélioration de personnage', 'Un peu de pouvoir provenant des bourrasques du Dragon de l\'Est.\r\nTransparent, mais beau, il permet d\'invoquer les tornades et les alizés. Le vent est son compagnon, sa voix et sa liberté.\r\nTous ceux qui se tiennent en travers de son chemin sont ses ennemis.', 5, 'Défi de Stormterror (Niv.70+)'),
(380, 'Queue de Borée', 'Matériau d\'amélioration de personnage', 'Un fragment de la queue du Grand Roi Loup du nord.\r\nBien qu\'il soit réduit à un esprit, la puissance de Borée n\'est pas à sous-estimer. Le Roi Loup essaya de se construire un corps à l\'aide de feu et de glace afin de combattre un ennemi, mais ce ne fut pas suffisant pour contenir ses pouvoirs. Les écailles givrées qui s\'en sont détachées lors de l\'affrontement ont conservé leur énergie spirituelle.', 5, 'Défi du Loup du Nord (Niv.70+)'),
(381, 'Vertèbre de Borée', 'Matériau d\'amélioration de personnage', 'Un peu de glace provenant du corps du Grand Roi Loup du Nord.\r\nIl déclenchait des blizzards et des tempêtes de neige à la grêle si acérée qu\'elle pouvait couper la chair. Ses pouvoirs ne sont plus ceux d\'antan, mais il possède encore la puissance de recouvrir ses terres de neige.', 5, 'Défi du Loup du Nord (Niv.70+)'),
(382, 'Esprit de Borée', 'Matériau d\'amélioration de personnage', 'Un fragment d\'âme obtenu du Grand Roi Loup du Nord.\r\nCe dernier déclenchait des blizzards et des tempêtes de neige. Cependant, à la fin de sa vie, il comprit enfin que ses vents froids et violents ne pouvaient qu\'éteindre la vie, et non la nourrir. Il laissa donc ses pouvoirs se répandre sur les terres pour leur apporter bénédiction et protection éternelles.', 5, 'Défi du Loup du Nord (Niv.70+)'),
(383, 'Pilier de basalte', 'Matériau d\'amélioration de personnage', 'Un pilier formé avec une carapace d\'Hypostase Géo et que de l\'énergie Géo intense parcourt.\r\nCe monument est une preuve que l\'élément Géo est le plus lourd d\'entre tous. L\'Hypostase Géo voulait peut-être se rapprocher du ciel plutôt que de combattre ses ennemis...', 4, 'Butin d\'Hypostase Géo (Niv.30+)'),
(384, 'Duramen de glace', 'Matériau d\'amélioration de personnage', 'Duramen d\'un Arbre congelé. Une forte énergie Cryo s\'en dégage.\r\nL\'énergie et les pensées qui pullulent au fond de la terre finissent tôt ou tard par émerger à la surface. Le givre froid et endeuillé finit alors par s\'attacher aux créatures qu\'il rencontre ; et même les plantes et arbres deviennent alors gigantesques et menaçants.', 4, 'Butin d\'Arbre congelé (Niv.30+)'),
(385, 'Graine de feu', 'Matériau d\'amélioration de personnage', 'Graine qui anime tout Arbre enflammé. Une chaleur qu\'on devine prête à consumer la flore du monde semble s\'en dégager.\r\nLe noyau de la terre regorge d\'énergie sans cesse mouvante, aux désirs sans fond. S\'emparant des plantes et arbres, elle les consume sous un feu éternel, et les soumets à sa volonté de détruire tout sur son passage. Tu parles d\'un rêve.', 4, 'Butin d\'Arbre enflammé (Niv.30+)'),
(386, 'Cœur d\'eau pure', 'Matériau d\'amélioration de personnage', 'Un noyau d\'eau pure, récupéré auprès d\'une Océanide.\r\nMême tombé au milieu d\'un lac, il ne perd rien de sa pureté. Les Océanides sont des créatures d\'une extrême pureté, dont le désir de ressembler aux êtres vivant sur la terre ferme est très puissant. Elles prennent alors forme terrestre – après tout, elles n\'ont ni besoin de respirer, ni besoin de se sustenter...', 4, 'Butin d\'Océanide (Niv.30+)'),
(387, 'Corne de Monoceros Caeli', 'Matériau d\'amélioration de personnage', 'La corne d\'une baleine que vous avez obtenue en battant Tartaglia, qui avait libéré la puissance de son œil maléfique.\r\nD\'aucuns prétendent que le caractère d\'une personne est défini par la forme de sa constellation. L\'image d\'une baleine traversant le firmament est une allégorie de la destruction réduisant tout à néant sur son chemin.\r\nC\'est une bête similaire qu\'il invoqua lors de votre combat.', 5, 'Défi de Tartaglia (Niv.70+)'),
(388, 'Fragment du démon', 'Matériau d\'amélioration de personnage', 'Fragment d\'arme que vous avez obtenu en battant Tartaglia, qui avait libéré la puissance de son œil maléfique.\r\nL\'objet est couvert d\'une couleur qui n\'est pas de ce monde, sans doute due aux ténèbres sans fin que le jeune garçon a vu de ses propres yeux.\r\nPeut-être n\'a-t-il rien à voir avec la puissance d\'un œil divin, mais comme on dit : l\'arme est le reflet de celui qui la manie...', 5, 'Défi de Tartaglia (Niv.70+)'),
(389, 'Ombre du guerrier', 'Matériau d\'amélioration de personnage', 'Un fragment de pouvoir que vous avez obtenu en battant Tartaglia, qui avait libéré la puissance de son œil maléfique.\r\nCet objet est le résultat d\'une volonté de combattre qui s\'est aiguisée au cours d\'innombrables massacres.\r\nSi le destin existe pour les hommes, alors le sien a sans nul doute été affecté par ces actions. Sinon, comment expliquer qu\'il se retrouve toujours au centre de chaque conflit ?', 5, 'Défi de Tartaglia (Niv.70+)'),
(390, 'Jade juvénile', 'Matériau d\'amélioration de personnage', 'Une substance cristalline prélevée sur un Géosaure antique. En elle réside le pouvoir de se transformer en dragon.\r\nCes cristaux ternes se développent dans les corps des Élémentosaures qui sommeillent dans les montagnes. On raconte à Liyue qu\'ils finissent par prendre la place du cœur de leur hôte une fois qu\'ils ont accumulé suffisamment d\'énergie, l\'Élémentosaure d\'origine devenant alors un véritable dragon, capable d\'ébranler les montagnes et d\'ouvrir la terre.', 4, 'Butin de Géosaure antique (Niv.30+)'),
(391, 'Couronne du roi dragon', 'Matériau d\'amélioration de personnage', 'Ces cornes créées à partir de jade durci et cristallisé pendant des milliers d\'années forment la couronne du roi dragon.\r\nInfusé par les esprits et sculpté dans la roche, il est né au cœur de la montagne pour montrer la force de la terre face aux étendues de monolithes, et ses longues cornes dorées sont la marque de sa lutte contre un roi du passé.\r\nCette corne avait, pendant un temps, été brisée et avait perdu de son éclat, mais désormais, elle brille d\'une lueur froide et dorée, pendant que vous la tenez dans la paume de votre main.', 5, 'Défi d\'Azhdaha (Niv.70+)'),
(392, 'Branche de jade cramoisi', 'Matériau d\'amélioration de personnage', '\r\nNourrie par la puissance du roi dragon, cette branche osseuse scintille de mille couleurs étranges et uniques.\r\nLa haine qui parcourait les sources naturelles a embrassé le clair de lune froid, et c\'est ainsi que sont nées et qu\'ont poussé ces branches cristallines...\r\nMême si elles sont tranchées au cours de la bataille, ces branches imprégnées du sang du dragon continuent de bruisser comme si elles ne pouvaient jamais trouver la paix.', 5, 'Défi d\'Azhdaha (Niv.70+)'),
(393, 'Écaille dorée', 'Matériau d\'amélioration de personnage', 'Armure d\'écaille qui pousse naturellement sur la pierre mystique. Robustes et silencieuses, ces écailles renferment la force du roi dragon.\r\nAu fil des années, à mesure que d\'innombrables générations revenaient à la poussière de la terre, l\'or et l\'obsidienne se sont incrustés dans la chair et le sang créés par la roche avant de se répandre et de former des écailles.\r\nL\'agonie fulgurante, les pleurs silencieux... Tout cela se dissipera peut-être une fois que la rancune aura pris fin.', 5, 'Défi d\'Azhdaha (Niv.70+)'),
(394, 'Fleur cristalline', 'Matériau d\'amélioration de personnage', 'Un cristal de glace d\'une extrême pureté qui s\'est formé à partir de la floraison d\'une Hypostase Cryo.\r\nLes cristaux glacés grandissent et fleurissent selon leurs propres lois du froid, jusqu\'à ce qu\'un jour une fleur hivernale éclose et gèle tout. Peut-être est-ce le temps stagnant et ce cœur qui ne fond jamais qui sont l\'essence de cette fleur éternelle.', 4, 'Butin d\'Hypostase Cryo (Niv.30+)'),
(395, 'Solvant de rêve', 'Consommable', 'Un objet capable de modifier un matériau d\'amélioration de personnage obtenu lors de réminiscences ou d\'épreuves en ce qui est nécessaire.\r\nLes gens pensaient autrefois que l\'âme et les souvenirs avaient une forme matérielle. Si quelqu\'un rêvait de paradis avant de s\'éveiller agrippant une fleur, alors cette fleur provenait de cette rêverie. Les solvants de rêve peuvent dissoudre ce qui vient de la mémoire et le transformer en un autre rêve.\r\nLes souvenirs de lutte contre les forts pavent le chemin vers une plus grande force pour son propre compte.', 4, 'Récompense de Donjon de la conquête et du Loup du Nord'),
(396, 'Mécanisme oni', 'Matériau d\'amélioration de personnage', 'Un noyau pouvant alimenter le mécanisme de la Lame oni, et lui permettant de manier une épée tel un être autonome.\r\nLa chair se décompose, et avec elle le souvenir des arts martiaux, quels qu\'en soient la maîtrise et le raffinement.\r\nPeut-être qu\'est-ce seulement en convertissant ses quatre membres et son corps en pièces mécaniques, et en sacrifiant son cœur pour un mécanisme sophistiqué que l\'on peut transcender la chair et approcher l\'éternité...', 4, 'Butin de Lame oni (Niv.30+)'),
(397, 'Cœur perpétuel', 'Matériau d\'amélioration de personnage', 'Le noyau qui alimente les mouvements autonomes de la Matrice mécanique perpétuelle.\r\nCertains spéculent que les symboles sur ces noyaux ont été inscrits avec une intelligence et une logique d\'un langage invisible à l\'œil nu. Certains vont plus loin et pensent que ces mécanismes des ruines uniques ont décidé de manière autonome d\'abandonner leur forme biomimétique pour rechercher plus de puissance du fait de leurs noyaux particulièrement volumineux et de leurs inscriptions complexes.', 4, 'Butin de Matrice mécanique perpétuelle (Niv.30+)'),
(398, 'Perle brûlante', 'Matériau d\'amélioration de personnage', 'Le produit de la cristallisation d\'une Hypostase Pyro qui émet toujours de la chaleur.\r\nMême les flammes brûlantes seront un jour froides, et même les cendres éteintes se raviveront un jour en un feu déchaîné. Ce doit alors être la volonté du monde que les Hypostases Pyro soient constamment en train de se refroidir et de se réchauffer, un peu comme le cycle sans fin des vies de l\'univers...', 4, 'Butin d\'Hypostase Pyro (Niv.30+)'),
(399, 'Moment en fusion', 'Matériau d\'amélioration de personnage', 'Un fragment déformé qui renferme un immense pouvoir. Vous l\'avez obtenu après avoir vaincu Signora au summum de sa puissance.\r\n« J\'ai fait un long rêve.\r\nJ\'ai rêvé qu\'on se reverrait dans le monde immaculé que nous avions créé.\r\nMais j\'ai fini par me réveiller, consumée par les flammes dévastatrices depuis longtemps. Tandis que tu es revenu sur la terre avec ton sang empoisonné.\r\nRien de tout cela ne m\'importe plus. Je dois continuer ma route et incinérer tout ce que je laisse après mon passage. Je dois continuer de croire au monde pur, irréprochable et éternel dont il parlait, celui que j\'ai vu dans l\'illusion qu\'il m\'a montrée, au bout de l\'horizon, dans les rêves de Sa Majesté. »', 5, 'Défi de Signora (Niv.70+)'),
(400, 'Papillon infernal', 'Matériau d\'amélioration de personnage', 'Un fragment déformé qui renferme un immense pouvoir. Vous l\'avez obtenu après avoir vaincu Signora au summum de sa puissance.\r\nLe sceau de glace a été brisé sous la contrainte et alors que son ancienne volonté se libérait de sa prison pâle et qu\'elle se souvenait de son ancien nom, le papillon éternel exécutait une danse qui brûlait chair et sang. Elle se souvint de son passé, errant sur les terres, répandant ses flammes pour détruire le mal... ainsi que du visage de la personne qu\'elle ne pourrait plus jamais revoir.', 5, 'Défi de Signora (Niv.70+)'),
(401, 'Cœur de cendre', 'Matériau d\'amélioration de personnage', 'Un fragment déformé qui renferme un immense pouvoir. Vous l\'avez obtenu après avoir vaincu Signora au summum de sa puissance.\r\nLes larmes ne peuvent éteindre les flammes. Tristesse et rage, aussi brûlantes que la fonte, coulent dans ses veines et s\'évacuent par ses glandes lacrymales.\r\nLe premier Fatui l\'a découverte avant que son cœur ne soit détruit par les flammes éternelles. Il fit d\'elle une alliée, car il avait compris que seules une haine et une obstination plus féroces pouvaient éteindre le feu qui la dévorait.', 5, 'Défi de Signora (Niv.70+)'),
(402, 'Rosée du rejet', 'Matériau d\'amélioration de personnage', 'Une goutte d\'eau étrange que l\'Hypostase Hydro a laissée derrière elle après sa destruction.\r\nElle possède une forte volonté de réjection, comme la base de la plupart des attaques de l\'Hypostase Hydro. On raconte que l\'eau est un réceptacle des souvenirs et des volontés et que ces choses s\'intensifient quand les eaux se mélangent. Cependant, les vagues invoquées par l\'Hypostase Hydro ne peuvent pas se mêler aux eaux qui coulent au-dessus ou en dessous du sol. Même si elle convergeait vers des eaux aussi pures que celles de Petrichor, cette goutte d\'eau refuserait probablement de s\'y mélanger, tel du mercure.', 4, 'Butin d\'Hypostase Hydro (Niv.30+)'),
(403, 'Perles tempestives', 'Matériau d\'amélioration de personnage', 'Des perles parcourues par l\'élément Électro que la Manifestation du tonnerre a laissée derrière elle.\r\nOn raconte que cette dernière apparaît dans les endroits où une haine incommensurable réside. Dans les histoires chantées par les bardes, ce seraient les déceptions et les souffrances insoutenables de la vie qui les poussent à errer éternellement sur des terres brisées.', 4, 'Butin de Manifestation du tonnerre (Niv.30+)'),
(404, 'Régalia de faille', 'Matériau d\'amélioration de personnage', 'Une corne brisée obtenue après avoir vaincu le loup alpha. Cela dit, il n\'aurait dû rien rester après votre chasse.\r\nMême un seigneur des failles n\'est rien en comparaison avec des monstres plus puissants.\r\nL\'« Or » a produit en masse ce genre de meutes de canidés malformés comme par accident, un peu comme des gribouillis ratés sur des vieux morceaux de papier, et les cornes de ce dernier représentent son autorité à commander sa sombre meute à dissoudre l\'espace lui-même.', 4, 'Butin de Loup alpha doré (Niv.30+)'),
(405, 'Fausse nageoire de l\'héritier du dragon', 'Matériau d\'amélioration de personnage', 'Un morceau de tissu biologique que vous avez trouvé après avoir vaincu un Élémentosaure abyssal.\r\nBien qu\'il ressemble à une sorte de nageoire, il n\'a en fait aucune fonction.\r\nLes habitants de Byakuyakoku ont déjà mené des études approfondies sur les Élémentosaures abyssaux. Leur caractéristique la plus distinctive est la possibilité de choisir les traits qu\'ils souhaitent transmettre à leur progéniture. En raison de leur grande haine envers l\'humanité, ces Élémentosaures perdent toute acuité visuelle quand ils atteignent l\'âge adulte. En même temps, ils ont également développé un certain degré de polymorphisme élémentaire à des fins de combat.', 4, 'Butin de Harde d\'Élémentosaures abyssaux (Niv.30+)'),
(406, 'Mudra du général maléfique', 'Matériau d\'amélioration de personnage', 'Une articulation de marionnette obtenue dans le plan euthymique. Son geste représente le concept de ce que l\'on voit et de ce que l\'on fait.\r\nMême maintenant qu\'« elle » est devenue une divinité calamiteuse, elle peut toujours utiliser des gestes aussi transcendants, son épée jaillissant à la vitesse du regard.\r\nEt pourtant, alors que d\'innombrables désastres faisaient rage depuis bien trop longtemps, la carapace du général féroce dut embrasser un nouveau printemps.', 5, 'Défi de Sentinelle de l\'éternité (Niv.70+)'),
(407, 'Larmes de la divinité calamiteuse', 'Matériau d\'amélioration de personnage', 'Une articulation de marionnette obtenue dans le plan euthymique. Le geste ici a la forme du funeste œil de jugement orageux.\r\nÉtant donné qu\'elles sont capables d\'abattre la malédiction et de faire pleuvoir les catastrophes d\'un simple regard, il peut paraître naturel d\'attribuer les malheurs de la vie aux divinités calamiteuses. Cependant, si on y réfléchit d\'un peu plus près, quand notre volonté est assez forte et nos convictions suffisamment fermes...\r\nPeu importe les catastrophes, même un œil maléfique peut se laver de ses larmes.', 5, 'Défi de Sentinelle de l\'éternité (Niv.70+)'),
(408, 'Signification d\'une éternité', 'Matériau d\'amélioration de personnage', 'Un petit couvre-chef que vous avez récupéré quand le calme est revenu dans le plan euthymique.\r\nUne seule pensée peut mettre fin à toutes les calamités, et il y aura toujours des pensées pour transcender le temps.\r\nMais au milieu de toutes ces catastrophes, on peut se demander si le véritable message de Makoto a pu faire son chemin.', 5, 'Défi de Sentinelle de l\'éternité (Niv.70+)'),
(409, 'Croc runique', 'Matériau d\'amélioration de personnage', 'Un « croc » qui a permis à la mystérieuse machine antique à l\'apparence de serpent de creuser dans les profondeurs de la montagne. Il présente des gravures de runes funestes.\r\nD\'après la légende, des serpents géants se tapissent dans les entrailles du sol. Nourrissant une haine et une malveillance éternelle envers la surface, ils creusent d\'immenses tunnels sans que personne ne sache leur but sinistre.', 4, 'Butin de Serpent des ruines (Niv.30+)'),
(410, 'Bec crochu majestueux', 'Matériau d\'amélioration de personnage', 'Un bec allongé que vous avez trouvé après avoir vaincu le Champieffroi panaché.\r\nLes chercheurs considèrent toujours comme un miracle le fait que des Fongus mous aient pu évoluer pour avoir ces becs crochus durs comme la pierre. Cette espèce, que l\'on peut dignement considérer comme le roi des champignons fait tranquillement les cent pas sur la ligne d\'arrivée de l\'évolution. De temps en temps, il lance un long appel à tous les êtres vivants de la forêt tropicale : même la créature la plus impuissante et timide peut un jour devenir le monarque des bois.', 4, 'Butin de Champieffroi panaché (Niv.30+)'),
(411, 'Fruit du tonnerre', 'Matériau d\'amélioration de personnage', 'Le cœur d\'un Arbre foudroyé enveloppé de feuilles acérées tels des rasoirs. Il émet de l\'élément Électro.\r\nL\'énergie rugissante qui se déchaîne au plus profond de la terre est impatiente de déverser sa rage, souillant même les malheureuses lianes avec sa folie, les faisant gémir et s\'agiter frénétiquement. Il est difficile de dire si elle aspire à ce que d\'autres goûtent à la douleur d\'être continuellement électrocutés et carbonisés ou si elle se débat, attendant avec impatience sa mort inévitable.', 4, 'Butin d\'Arbre foudroyé (Niv.30+)'),
(412, 'Calibre perpétuel', 'Matériau d\'amélioration de personnage', 'Un mécanisme moteur que vous avez trouvé dans les Ruines mystérieuses.\r\nBien que la machine qu\'il alimentait autrefois a été détruite, il continue de tourner.\r\nUne source d\'énergie capable de se maintenir éternellement n\'est pas censée exister dans notre monde, et les résultats de la plupart des travaux de recherche sur le mouvement perpétuel ont fini par être prouvés faux. Pourtant, cet appareil est la preuve qu\'une civilisation aujourd\'hui disparue a atteint des sommets que les nations actuelles ne peuvent espérer égaler...', 4, 'Butin de Dragon cataclysmique (Niv.30+)'),
(413, 'Tétraèdre de lumière', 'Matériau d\'amélioration de personnage', 'Un composant essentiel que vous avez obtenu après avoir vaincu un mystérieux ensemble de mécanismes.\r\nJusqu\'à nos jours, ces anciennes machines n\'ont pas oublié les rêves de leurs maîtres ni leurs devoirs : construire mille palais, administrer trois mille machines, semer dix mille fleurs qui ne fleuriront plus jamais, et recréer un paradis désormais enfoui sous les sables du désert pour le bien de tous ceux qui vivent dans ce monde.\r\nBien que le roi du désert ne soit plus, que son rêve ait été depuis longtemps corrompu par des murmures impies et que tous les piliers magiques aient été brisés avant de s\'effondrer, l\'immense créature des temps anciens que le roi appréciait tant se cache toujours au sein des tempêtes de sable brûlantes.', 4, 'Butin d\'Algorithme de semi-intransition matriciaire d\'observation en néo-réseau (Niv.30+)'),
(414, 'Liane d\'oblitération', 'Matériau d\'amélioration de personnage', 'La forme finale condensée d\'une Hypostase Dendro. Des lianes robustes protègent un cristal élémentaire pur.\r\nL\'élément Dendro, dont l\'existence est la plus opposée à l\'ordre naturel des choses, mais l\'élément le plus compatible avec la vie, et une Hypostase, un être élémentaire très éloigné de toutes les autres formes de vie, n\'auraient pas dû avoir la possibilité de ne faire qu\'un dans ce monde. On raconte qu\'une Hypostase est capable d\'«apprendre» de leur environnement. C\'est pour cette raison que les rêves et les nuances de la forêt n\'ont aucun secret pour celle-ci en particulier. Sa forme actuelle est le fruit de milliers d\'années de transformation.', 4, 'Butin d\'Hypostase Dendro (Niv.30+)'),
(415, 'Fils de marionnette', 'Matériau d\'amélioration de personnage', 'Des tubes qui étaient insérés dans le dos de la marionnette. Les substances qui transitaient dedans étaient aussi bien de couleurs monotones que vives. Leur contraste était aussi impressionnant qu\'effrayant.\r\nIls alimentaient la marionnette en force et servaient également de fils pour la manipuler et l\'attacher.\r\nUne fois ces tubes rompus, elle est devenue aussi faible qu\'un nouveau-né, incapable du moindre mouvement.', 5, 'Défi de Shouki no Kami (Niv.70+)'),
(416, 'Miroir de mushin', 'Matériau d\'amélioration de personnage', 'Le miroir qui se trouvait sur la poitrine de Shouki no Kami pour le séparer du monde extérieur. Le mitsudomoe gravé dessus est un signe ostentatoire d\'autorité et de puissance.\r\nCependant, il ne représente plus l\'obéissance à la maîtresse de la foudre.\r\nCe symbole est désormais une épée pointée vers les forces qui régissent ce monde.\r\nLe miroir ne sert pas à se défendre contre le regard plein de jugement des mortels, mais il agit comme protection contre la souillure du monde.\r\nDans ce grand temple, le châtiment est infligé et l\'ignorance est purifiée.\r\nLes rêves s\'effondrent, les désirs sont arrachés et le karma est restauré.', 5, 'Défi de Shouki no Kami (Niv.70+)'),
(417, 'Cloche de daka', 'Matériau d\'amélioration de personnage', 'Une boule utilisée pour articuler deux membres mécaniques qui a la forme d\'une cloche. Sous ses airs délicats se cache une quantité d\'énergie inimaginable.\r\nSi elle est utilisée pour relier les bras, ces derniers perdront toute sensation, tandis que ce sera la force qui disparaîtra pour les jambes.\r\nSi elle relie le crâne...\r\nLa haine, la douleur, la folie et l\'orgueil demeurent intacts.\r\nQuelle chance.', 5, 'Défi de Shouki no Kami (Niv.70+)'),
(418, 'Pseudo-étamines', 'Matériau d\'amélioration de personnage', 'Les élytres de couleur verdâtre cachés dans la queue d\'un Wenut. Ils sont aussi fins que les étamines d\'une fleur.\r\nOn dit qu\'autrefois, avant même la nuit des temps, ces vers s\'enfonçaient dans la terre humide de prairies et de forêts colossales de la même manière que des oiseaux qui planent dans le ciel ou des poissons qui nagent dans l\'eau. On pense généralement que leur mimétisme des fleurs est destiné à attirer les proies perdues dans les tempêtes de sable. Mais certains disent que cette apparence de fleur est le reflet d\'une époque révolue où, au lieu du sable doré, la terre était recouverte d\'une couverture luxuriante de plantes et de fleurs.', 4, 'Butin de Wenut de Setekh (Niv.30+)'),
(419, 'Anneau de sombreur éternelle', 'Matériau d\'amélioration de personnage', 'Un catalyseur orbiculaire qui a été obtenu après avoir vaincu un émissaire à quatre bras chuchotant de lugubres évangiles.\r\nLes cercles représentant des passages, les motifs arcaniques pouvant déclencher des effets mystérieux en sont souvent composées. Ce genre d\'anneau est peut-être même un instrument pour guider le flux de l\'univers...', 4, 'Butin de Lustrateur inique (Niv.30+)'),
(420, 'Fougère du monde luxuriant', 'Matériau d\'amélioration de personnage', 'Une fougère prise dans l\'oasis d\'Apep.\r\nAvant même l\'existence du temps, le monde appartenait aux dragons. Dans le jardin du palais d\'Apep, un paradis pour la flore, les premières à pousser furent les fougères.\r\nDans les siècles qui suivirent, les fougères devinrent des arbres, des lianes, des racines et des feuilles, recouvrant le monde de leur toucher. Cependant, Apep se souvient encore du son presque inaudible que la première fougère a fait en se tordant pour atteindre le vaste ciel.', 5, 'Défi de Garde de l\'oasis d\'Apep (Niv.70+)'),
(421, 'Floraison première de l\'oasis', 'Matériau d\'amélioration de personnage', 'Une fleur de forme primordiale, obtenue de l\'oasis d\'Apep.\r\nAu fil des éons, les fleurs ont acquis une infinie variété de senteurs et de couleurs, et autant de noms qu\'il y a d\'étoiles dans le firmament nocturne.\r\nCependant, cette première fleur n\'a jamais porté de nom. Pour les dragons d\'autrefois, même si une rose perdait son nom, elle ne perdrait pas son parfum. Les appellations ne représentent que la façon dont les êtres éphémères et à l\'esprit étroit comprennent le monde. La vie, en constante évolution, n\'est quant à elle pas contrainte par de tels termes.', 5, 'Défi de Garde de l\'oasis d\'Apep (Niv.70+)'),
(422, 'Constambre', 'Matériau d\'amélioration de personnage', 'Un morceau d\'ambre pris dans l\'oasis d\'Apep. À l\'intérieur de ce dernier, un petit insecte éteint depuis longtemps reste figé dans le temps.\r\nMême si c\'est une circonstance hasardeuse qui fait qu\'une si petite vie se retrouve enveloppée par l\'ambre d\'un arbre, la continuation de son existence est le fruit du destin.\r\nSuite à la catastrophe tombée du ciel, bois, lianes, racines et toute autre flore se flétrirent et se transformèrent en poussière, et un grand nombre de formes de vie disparurent.\r\nDans le silence de l\'ambre cependant, un insecte anonyme demeure, vivant un avenir que personne d\'autre de son temps n\'a eu la chance de connaître.', 5, 'Défi de Garde de l\'oasis d\'Apep (Niv.70+)'),
(423, 'Engrenage artificié Coppelia', 'Matériau d\'amélioration de personnage', 'Un engrenage horlogoïde de rechange appartenant à Coppelia, qui sert à alimenter la danseuse artificiée.\r\nSelon la vision de son créateur, l\'histoire de la danseuse artificiée aux articulations délicates se termine par un moment joyeux. Coppelia enverra donc quiconque tentera de perturber ce moment dans un chapitre de malheur avec ses superbes compétences de combat.', 4, 'Butin de Suite cryolienne (Niv.30+)'),
(424, 'Engrenage artificié Coppelius', 'Matériau d\'amélioration de personnage', 'Un engrenage horlogoïde de rechange appartenant à Coppelius, qui sert à alimenter le danseur artificié.\r\nLe danseur artificié tourbillonne sur un air platine, interprétant une valse interminable de vent de glace. La danse de Coppelius ne s\'arrêtant pas, il continuera à tourner jusqu\'au son du glas.', 4, 'Butin de Suite cryolienne (Niv.30+)'),
(425, 'Résolution de l\'empereur', 'Matériau d\'amélioration de personnage', 'Le marteau de guerre de l\'Empereur du feu ferreux. Le puissant crabe a écrasé d\'innombrables ennemis avec cette pince, qu\'il s\'agisse de Mékas horlogoïdes, de congénères fontemarins ou de chasseurs à la curiosité mal placée.\r\nLes Aberrants fontemarins diffèrent des êtres humains pour ce qui est des concepts d\'aristocratie ou d\'héritage, qui leur sont totalement étrangers. Aucun d\'entre eux n\'est né supérieur, et celui qui survit aux prédateurs et aux adversités est capable d\'accumuler une puissance infinie tout en continuant à chasser et à se nourrir pendant des siècles. Une telle créature est souvent qualifiée de suzerain pour la peur qu\'elle inspire.', 4, 'Butin d\'Empereur du feu ferreux (Niv.30+)'),
(426, '« Appareil Tourbillon »', '', 'Le composant principal du générateur de champ expérimental.\r\nUn composant mécanique usiné avec précision qui neutralise les effets de la gravité grâce à une rotation à grande vitesse presque impossible à distinguer à l\'œil nu.\r\nAu cours de sa longue histoire, l\'Institut de recherche d\'ingénierie cinétique de Fontaine a toujours cherché un moyen de survivre contre l\'inondation qui a autrefois balayé d\'innombrables vies et pourrait encore recouvrir la terre et le ciel un jour.\r\nL\'arkhium, avec son immense potentiel énergétique et son pouvoir de briser les chaînes de la terre, était autrefois considéré comme la clé de la porte du salut.', 4, 'Butin de Générateur de champ expérimental (Niv.30+)'),
(427, 'Corne fontemarine', 'Matériau d\'amélioration de personnage', 'La section émettant de l\'électricité de l\'Hippocampe perlé millénaire, symbole de sa puissance supérieure, cassée lors d\'un affrontement contre un puissant adversaire.\r\nEn tant que plus grand prédateur descendant des hippocampes bulleux, le parcours évolutif de l\'Hippocampe perlé millénaire n\'est pas le même que celui vécu par les autres Aberrants fontemarins : il n\'a pas commencé à partir d\'une forme de base avant de devenir plus grand et plus fort, mais a plutôt développé des membres et des cornes qui n\'ont pas leur place sur un hippocampe ordinaire, et même sa musculature et son squelette ont changé. Il ressemble maintenant davantage à une bête fantastique des légendes d\'autrefois qu\'à sa forme d\'origine. Dans l\'imaginaire des poètes et des auteurs, même les hippocampes peuvent gambader sur la terre.', 4, 'Butin d\'Hippocampe perlé millénaire (Niv.30+)'),
(428, 'Eau n\'ayant pas transcendé', 'Matériau d\'amélioration de personnage', 'Ce qui reste d\'un Tulpa Hydro une fois ses parties dispersées.\r\nCette substance est très similaire aux cristaux extraits des corps des Aberrants fontemarins, mais elle contient beaucoup d\'impuretés minuscules, comme des bulles dans un verre fabriqué par un artisan maladroit.', 4, 'Butin de Tulpa Hydro (Niv.30+)'),
(429, 'Tuile cassée de Décabarian', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nL\'endroit qu\'on nomme l\'Antre de Stormterror aujourd\'hui était autrefois une cité construite par Décabarian, le Seigneur des Rafales. Ce beau rêve prospère a été créé par le pouvoir de l\'Archon, et même après sa destruction, les tuiles cassées et les fragments dispersés laissés derrière possèdent toujours une grande puissance.', 2, 'Donjon d\'entraînement : Ville de réflexions (Monday, Thursday, Sunday)'),
(430, 'Mur abattu de Décabarian', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nDécabarian, fier de son œuvre, était vénéré du haut de sa tour. Cependant, il ignorait que le peuple ne se prosternait pas ni par respect ni par admiration.', 3, 'Donjon d\'entraînement : Ville de réflexions (Monday, Thursday, Sunday)'),
(431, 'Fragment dispersé de Décabarian', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nAndrios déclara la guerre au Seigneur de la Tour, mais ne put infliger une seule égratignure aux rempats de sa cité. Hélas, la douce mélodie de la liberté mit fin au rêve du tyran en un instant.', 4, 'Donjon d\'entraînement : Ville de réflexions (Monday, Thursday, Sunday)'),
(432, 'Rêve brisé de Décabarian', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nLe peuple ne révérrait pas le Seigneur de la Tour par respect ou par admiration, mais par peur de la tempête qui s\'abattrait sur eux. Cet objet représente son ambition brisée par Barbatos.\r\nDécabarian avait déployé des moyens démesurés pour réaliser son rêve. Même brisé, il reste puissant.', 5, 'Donjon d\'entraînement : Ville de réflexions (Monday, Thursday, Sunday)'),
(433, 'Dent de lait du Loup boréal', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nFidèles vassaux d\'Andros, les loups sont bénis d\'une grande vigueur. Une simple dent de lait de louveteau renferme donc des pouvoirs considérables.\r\nLes Archons avaient pour devoir d\'aimer l\'humanité entière, mais le Roi Loup ne montrait de la considération qu\'envers les parias et les nourrissons.', 2, 'Donjon d\'entraînement : Vallée submergée (Tuesday, Friday, Sunday)'),
(434, 'Croc brisé du Loup boréal', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nLa violence fait partie du quotidien des loups. Les fractures et les blessures sont courantes.\r\nAndros perdit sa foi en l\'humanité, mais les nourrissons étaient encore innocents à ses yeux. Lorsque ces derniers sont adoptés par les loups, ils forment ensemble un lupical ; une famille que le destin leur a donné.', 3, 'Donjon d\'entraînement : Vallée submergée (Tuesday, Friday, Sunday)'),
(435, 'Croc fendu du Loup boréal', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nLes loups savent que les humains ont besoin de vivre avec les leurs. Un croc brisé est un cadeau d\'adieu qui apporte bonheur et protection.\r\nUne légende d\'un monde lointain raconte qu\'un loup adopta des jumelles. Leur foyer était surnommé la Cave aux loups ou « lupercal » ; ce mot s\'apparente à « lupical » dans ce monde.', 4, 'Donjon d\'entraînement : Vallée submergée (Tuesday, Friday, Sunday)'),
(436, 'Nostalgie du Loup boréal', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nLe Roi Loup décida de se retirer du monde, se pensant indigne de devenir le Roi des Vents. En raison de son mépris pour l\'humanité, il s\'estimait incapable de contribuer à leur bonheur, mais sa bienveillance envers les parias démontrait bien le contraire.', 5, 'Donjon d\'entraînement : Vallée submergée (Tuesday, Friday, Sunday)'),
(437, 'Entraves du Chevalier du Croc de Lion', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nLes humains aiment exagérer les exploits de leurs héros, au point de faire de simples entraves de véritables ennemis de la liberté.\r\nCelles-ci ont été donc bénies par des pouvoirs extraordinaires pour cette raison.', 2, 'Donjon d\'entraînement : Capitale assoiffée (Wednesday, Saturday, Sunday)'),
(438, 'Chaînes du Chevalier du Croc de Lion', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nDes chaînes qui ont entravé la grande Vennessa lorsqu\'elle n\'était qu\'une gladiatrice parmi les autres.', 3, 'Donjon d\'entraînement : Capitale assoiffée (Wednesday, Saturday, Sunday)'),
(439, 'Menottes du Chevalier du Croc de Lion', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nVennessa ne fut jamais véritablement entravée par ses chaînes. Contrairement à sa terre natale, Mondstadt ne possédait pas le minerai ou le feu pour forger des entraves susceptibles de l\'enchaîner indéfiniment.\r\nLa seule chose qui la retenait en ce lieu était la responsabilité qu\'elle avait envers les siens.', 4, 'Donjon d\'entraînement : Capitale assoiffée (Wednesday, Saturday, Sunday)'),
(440, 'Idéaux du Chevalier du Croc de Lion', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nLorsque Vennessa fut affranchie, ses responsabilités ne furent que plus grandes au fil du temps. Incarnation de la liberté, elle tint successivement sur ses épaules le bien-être de tribu, puis celui de Mondstadt tout entière et enfin du monde entier.\r\nSon vœu le plus cher était celui d\'un monde plus fort et indépendant.', 5, 'Donjon d\'entraînement : Capitale assoiffée (Wednesday, Saturday, Sunday)'),
(441, 'Sable lumineux de Guyun', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nLa Forêt de pierre de Guyun se dresse sur l\'endroit où la lance de Morax a transpercé ses ennemis. Avec le temps, les piliers de pierre s\'imprégnèrent de l\'esprit de ces derniers à un tel point que certains résidus maudits se déposent dessus.', 2, 'Donjon d\'entraînement : Autel du nuage d\'orage (Monday, Thursday, Sunday)'),
(442, 'Roche lustrée de Guyun', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nLa Forêt de pierre de Guyun n\'est pas un simple endroit avec vue agréable sur la mer ; il s\'agit d\'un cimetière où gît d\'anciennes divinités maléfiques de Liyue. Des miasmes envahissaient les lieux de manière récurrente, jusqu\'à l\'arrivée d\'un certain jeune homme.', 3, 'Donjon d\'entraînement : Autel du nuage d\'orage (Monday, Thursday, Sunday)'),
(443, 'Squelette sacré de Guyun', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nLa lance de pierre possède une sphère lumineuse formé par le pouvoir des ennemis du Souverain de la Roche. Il n\'était pas l\'objet de leur haine, car corrompus depuis bien longtemps, elle faisait désormais naturellement partie d\'eux.', 4, 'Donjon d\'entraînement : Autel du nuage d\'orage (Monday, Thursday, Sunday)');
INSERT INTO `material` (`id`, `name`, `type`, `description`, `rarity`, `source`) VALUES
(444, 'Corps divin de Guyun', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nLes esprits de divinités oubliées animent cette relique. Ils incarnent des idéaux pour l\'humanité qui ne virent jamais le jour, et reposent maintenant dans la pierre et les mers, sans jamais pouvoir disparaître vraiment.', 5, 'Donjon d\'entraînement : Autel du nuage d\'orage (Monday, Thursday, Sunday)'),
(445, 'Plomb karstique', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nLe Karst Jueyun est la demeure des Adeptes puissants et éclairés qui maîtrisent les arts occultes.', 2, 'Donjon d\'entraînement : Ruines grondantes (Tuesday, Friday, Sunday)'),
(446, 'Mercure karstique', '', 'Matériau d\'élévation d\'arme.\r\nGrâce aux talismans, les Adeptes peuvent insuffler une quantité de leurs pouvoirs sous forme de symboles. L\'illusion leur permet de créer une projection cosmique grâce à leur esprit. La fabrication de ce type de matière est un art secret ; n\'importe qui assez fou pour ingérer ces billes le regretterait fortement.', 3, 'Donjon d\'entraînement : Ruines grondantes (Tuesday, Friday, Sunday)'),
(447, 'Argent karstique', 'Matériau d\'élévation d\'arme', 'Une bille de métal fabriquée par les Adeptes grâce à l\'art des herbes.\r\nCette matière confère des pouvoirs exceptionnels à une arme, mais vous ne verrez jamais un artiste martial utiliser ce genre d\'objet.', 4, 'Donjon d\'entraînement : Ruines grondantes (Tuesday, Friday, Sunday)'),
(448, 'Or karstique', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nUne pilule en métal d\'une qualité exceptionnelle qui simule un état où l\'ordre et le chaos s\'entremêlent, et qui aurait été à l\'origine du monde. Hélas, les choses ne sont pas aussi simples en réalité.', 5, 'Donjon d\'entraînement : Ruines grondantes (Tuesday, Friday, Sunday)'),
(449, 'Grain d\'aérosidérite noire', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nD\'après la légende, une météorite noire tombée à travers la Mer de nuages de Liyue terrassa un monstre. Elle fut cependant souillée par le sang impur de la créature et perdit le pouvoir divin que lui avait insufflé le Souverain de la Roche.', 2, 'Donjon d\'entraînement : Terrain d\'ancien tonnerre (Wednesday, Saturday, Sunday)'),
(450, 'Éclat d\'aérosidérite noire', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nCe qu\'il se trouve au-delà de la mer est un mystère pour les Liyuéens, si bien que les rumeurs de monstres marins sont monnaie courante dans la cité portuaire. Après tout, le chaos ne peut qu\'y régner sans la protection des Sept Archons. Seule une force extérieure à Teyvat pourrait réduire les pouvoirs du Souverain de la Roche à néant.', 3, 'Donjon d\'entraînement : Terrain d\'ancien tonnerre (Wednesday, Saturday, Sunday)'),
(451, 'Fragment d\'aérosidérite noire', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\n« Outre-mer » est le surnom des terres inconnues qui s\'étendent au-delà de la côte de Teyvat. Jadis, de nombreuses anciennes divinités refusèrent de vivre sous l\'ordre des Sept Archons et se réfugièrent dans des régions lointaines et devinrent maléfiques. Bien que l\'histoire en soit ainsi, la source de leurs pouvoirs était la même que celle du Souverain de la Roche.', 4, 'Donjon d\'entraînement : Terrain d\'ancien tonnerre (Wednesday, Saturday, Sunday)'),
(452, 'Morceau d\'aérosidérite noire', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nAprès la chute de Khaenri\'ah, un hérétique créa une armée de monstres au sang noir en faisant appel à des forces maléfiques d\'un autre monde. Ceux-ci détruisirent et pillèrent tout ce qui se dressait devant eux. Durin, le dragon serpentin étaient une de ces abominations.', 5, 'Donjon d\'entraînement : Terrain d\'ancien tonnerre (Wednesday, Saturday, Sunday)'),
(453, 'Branche marine de corail', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nL\'Île de Watatsumi est la plus éloignée de l\'Île de Narukami dans la chaîne d\'îles inazumiennes, et son nom signifie « divinité des océans » dans l\'ancienne langue de l\'île. La légende raconte que lorsque le Grand serpent est apparu pour la première fois dans la nation abyssale de la nuit éternelle, son corps fluorescent était recouvert d\'une myriade de coraux colorés.', 2, 'Donjon d\'entraînement : Sables engloutis (Monday, Thursday, Sunday)'),
(454, 'Branche marine de gemme', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nCe corail ne peut être trouvé nulle part en Teyvat. C\'est un cadeau que le Grand serpent a obtenu lorsqu\'il a fait irruption dans l\'Outre-mer. Pour ce serpent abyssal, ces coraux signifiaient la puissance, et son pouvoir diminuait à mesure que ces coraux disparaissaient. En d\'autres termes, ces coraux contiennent une puissance bien au-delà de la norme.', 3, 'Donjon d\'entraînement : Sables engloutis (Monday, Thursday, Sunday)'),
(455, 'Branche marine de jade', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nEn fuyant dans l\'Outre-mer, la divinité qui avait tout perdu a rencontré les personnes abandonnées qui ne possédaient rien dans les profondeurs de l\'océan. C\'est ainsi qu\'elle a choisi de rester et de devenir leur « Orobashi no Mikoto », leur « Divinité des tréfonds ».\r\nIl est dit que la grande divinité serpent brisa un jour toutes les branches de corail de son corps pour éclairer ses enfants qui étaient recroquevillés dans l\'obscurité. On dit également qu\'elle utilisa ces branches de corail pour créer une immense échelle permettant à ces mêmes enfants de remonter à la surface pour contempler la lumière du jour.', 4, 'Donjon d\'entraînement : Sables engloutis (Monday, Thursday, Sunday)'),
(456, 'Branche marine d\'or', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nLa signification qui se cache derrière le terme de « tréfonds » représente simplement les espoirs non partagés que les habitants abandonnés des profondeurs marines fondaient sur la divinité serpent qui les dirigeait.\r\nEt c\'est également parce que cette divinité serpent avait maintenant des gens qui l\'adoraient qu\'elle est restée dans le monde qu\'elle aurait dû fuir depuis longtemps, brisant les branches de corail qui ornaient son corps, marchant sur des terres qu\'elle n\'aurait pas dû fouler, et affrontant un ennemi qu\'elle ne pourrait jamais espérer égaler... Jusqu\'à ce qu\'enfin, sa forme divine soit brisée avec les montagnes, son ichor transformé en plasma, et sa volonté et son pouvoir devinrent un venin qui ne pourrait jamais être apaisé.', 5, 'Donjon d\'entraînement : Sables engloutis (Monday, Thursday, Sunday)'),
(457, 'Sagesse de Narukami', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nPar le passé, les anciens escaladaient le sommet maintenant connu sous le nom de Mont Yougou et sculptaient du bois carbonisé par la foudre en un crochet incurvé pour l\'offrir en effigie à la force tonitruante qui illuminait les cieux et secouait la terre. Cette forme deviendra finalement le symbole du Mitsudomoe Électro, symbolisant la faveur, la sagesse et la puissance de l\'Électro, ainsi que les personnes qui représentent ces valeurs.', 2, 'Donjon d\'entraînement : Autel des sables (Tuesday, Friday, Sunday)'),
(458, 'Joie de Narukami', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nCeux qui appartiennent aux Commissions du Shogunat sont ceux qui administrent et exercent le pouvoir. Dans les contes remplis de monstres du passé, ceux à qui Son Excellence la Shogun faisait profondément confiance portaient sur eux des talismans avec ce dessin accroché, et tout comme le mot « Commission » signifie « servir joyeusement » dans l\'ancienne langue, ceux qui recevaient sa faveur la lui rendaient avec amour et loyauté. Pourtant, après un certain temps, plus rien ne fut comme avant.', 3, 'Donjon d\'entraînement : Autel des sables (Tuesday, Friday, Sunday)'),
(459, 'Affection de Narukami', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nTous les démons qui errent dans le désert ou vivent parmi les mortels sont attirés par la vue de la toute-puissante Narukami Ogosho, représentée par le Mitsudomoe Électro. Bien que leur vie puisse être plus longue que celle de n\'importe quelle créature, elle aura nécessairement une fin. Si ceux qui ont une durée de vie limitée aspirent à l\'éternité, alors ils ne peuvent que prier pour que « l\'éternité » se souvienne d\'eux. Et elle a effectivement répondu à leurs prières, se souvenant d\'eux, amis et ennemis, et leur gardant une place dans son cœur. Que ce soit les hiboux démoniaques qui résidaient dans la brume et déchiraient les cieux, le Tanuki qui osa pénétrer dans ses jardins impériaux, ou cette femelle oni, belle comme la lune et puissante au combat, qui finit par rivaliser avec elle... Que ce soit le Tengu qui prenait son envol de ses ailes sombres ou la Kitsune Saiguu qui marchait autrefois à ses côtés, mais qui finalement disparut à jamais...\r\nCes innombrables histoires sont dans son cœur, et un jour, elles brilleront à nouveau dans le paradis éternel de ses rêves.', 4, 'Donjon d\'entraînement : Autel des sables (Tuesday, Friday, Sunday)'),
(460, 'Vaillance de Narukami', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nLe trésor de la déesse du tonnerre est sa majesté, et cette majesté est incarnée par sa vaillance et sa colère. Sa colère vient de l\'amour qui persiste dans son cœur, et sa vaillance est renforcée par cette colère. Ainsi, quiconque bloquera le chemin vers l\'éternité ou lèvera la main sur le peuple d\'Inazuma deviendra son ennemi.\r\nOn raconte qu\'il existait quatre grands esprits, trois renards divins et deux grandes épées, mais que le symbole de Son Excellence, la toute-puissante Narukami Ogosho, ne pouvait être qu\'un seul coup, invincible et brillant comme une météorite.', 5, 'Donjon d\'entraînement : Autel des sables (Tuesday, Friday, Sunday)'),
(461, 'Masque du lieutenant', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nLes contes folkloriques parlent d\'un oni du nom de Torachiyo. Torachiyo était un guerrier très doué à tel point qu\'on raconte qu\'il pouvait danser au milieu de mille lames, les robes juunihitoe déchiquetées volant comme des pétales de violette, sans subir la moindre blessure. Pourtant, cet oni finira par trahir la Shogun et se révolter, se privant d\'un bras et d\'une corne dans la bataille avant de s\'enfuir et de se tuer dans un accès de folie furieuse.\r\nOn dit que les oni têtus cachent souvent de la peur et de la détresse dans leur cœur. Ils fantasment sur l\'éternité tout en craignant le moment présent, et être engloutis par la terreur ou la colère est leur juste récompense. Ce masque représente Torachiyo, les crocs dénudés.', 2, 'Donjon d\'entraînement : Enseveli dans les sables (Wednesday, Saturday, Sunday)'),
(462, 'Masque de la morsure du tigre', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\n« La lune assistant à la morsure du tigre du Mont Kinsen » dépeint le féroce oni Torachiyo brisant la naginata de la Shogun d\'un seul coup de dent, prenant ainsi l\'avantage pendant un instant. C\'est pourquoi les masques qui le représentent sont souvent pourvus de crocs finement ciselés.\r\nDans le dénouement de ce conte, l\'oni blessé s\'enfuit dans la forêt, craignant même de se montrer au froid clair de lune. On dit que le vent qui murmure à travers les ruelles et les vallées ressemble à ses cris d\'agonie silencieux, ce qu\'une expression saisonnière d\'Inazuma décrit comme étant « le vent de Torachiyo ».', 3, 'Donjon d\'entraînement : Enseveli dans les sables (Wednesday, Saturday, Sunday)'),
(463, 'Masque du cornu', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nDans les chansons oni, Torachiyo était un jeune homme élégant, fort de corps et beau de visage. Il était autrefois un lieutenant bien-aimé de la Shogun, et la suivit dans les royaumes abyssaux pour repousser le mal, gagnant la renommée d\'oni malgré leur nombre décroissant.\r\nPersonne ne chante désormais plus ces chansons, sauf le masque d\'un oni cornu, qui, même sous cette forme, possède toujours un pouvoir extraordinaire.', 4, 'Donjon d\'entraînement : Enseveli dans les sables (Wednesday, Saturday, Sunday)'),
(464, 'Masque du kijin', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nChiyo, une guerrière de la tribu oni avec l\'emblème du Mitsudomoe Électro sur le dos, a été une fois avalée entière par une bête d\'au-delà de ce monde qui avait un corps de tigre et une queue de serpent tout en retenant les forces des ténèbres. Finalement, elle a déchiré les entrailles de la créature de l\'intérieur, se libérant.\r\nC\'est l\'origine de l\'expression « Chiyo la morsure de tigre », qui sera changée au fil des années en « Torachiyo ».\r\nMais dans le ventre de la bête, elle fut souillée d\'un péché profond et vit ses camarades déchiquetés par ces dents entachées de sang. Plongée dans les ténèbres à perte de vue, elle finira par retourner son épée contre la toute-puissante Narukami Ogosho. Son bras et une corne furent tranchés, et elle s\'enfuit de la ville dans la forêt comme une bête blessée. Là, on raconte qu\'elle fut prise pour un monstre inconnu et tuée par un Tengu, ou par le Shuumatsuban, ou peut-être même par un descendant du Clan Iwakura s\'entraînant au fond des montagnes, car ses traits avaient été déformés par la haine et l\'agonie de ses blessures. Ou peut-être a-t-elle même rencontré l\'humanoïde portant une épée et un masque d\'oni près du cadavre du serpent géant, et là s\'est terminé le voyage de sa vie.\r\nPeu de ceux qui ont combattu l\'abîme à cette époque ont été épargnés par les rêves les plus sombres. Ceux qui ont tué des monstres et se sont transformés en eux n\'étaient guère la minorité. La frontière entre les mondes se fragilise, et une telle corruption n\'est peut-être pas simplement à sens unique.', 5, 'Donjon d\'entraînement : Enseveli dans les sables (Wednesday, Saturday, Sunday)'),
(465, 'Talisman en cuivre de la rosée', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nSous les instructions du Premier sage, les ancêtres du peuple de la forêt tropicale ont fabriqué ces talismans à l\'aide d\'arbres flétris pour honorer celle qui régna sur les arbres et les fleurs.\r\nLa forme du talisman s\'inspire de la rosée de la forêt. Elle symbolise l\'imprégnation de la sagesse dans les esprits des mortels.', 2, 'Donjon d\'entraînement : Nuages souillés (Monday, Thursday, Sunday)'),
(466, 'Talisman en fer de la rosée', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nPlus tard, la maîtresse des plantes enseigna les secrets de la forge du fer noir aux humains. Et c\'est ainsi que les mortels à l\'esprit innocent apprirent à se servir du feu et des lames acérées.\r\nLa maîtresse des plantes se réjouit de voir une telle chose : certes, le feu et le métal pouvaient la blesser, mais maîtriser ces deux choses allait permettre aux humains de devenir puissants.', 3, 'Donjon d\'entraînement : Nuages souillés (Monday, Thursday, Sunday)'),
(467, 'Talisman en argent de la rosée', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nDans les innombrables histoires de la forêt existe un personnage portant une cape aux rayures rouges comme le feu ardent : il est le roi des labyrinthes et le maître des eaux où se reflète la lune.\r\nCelui-ci inclina la tête pour officialiser son amitié avec la maîtresse des plantes, un acte qui fut scellé par le biais de pierres précieuses qui scintillaient comme l\'eau et de l\'argent qui brillait comme le clair de lune.\r\nEn échange d\'un tel geste, la maîtresse des plantes promit de garder les secrets du seigneur du labyrinthe et de protéger les beaux rêves de la forêt tropicale.\r\nPersonne n\'aurait pu prédire que le magnifique manoir des secrets et des rêves s\'éteindrait plus tard dans le linceul de la flamme sombre de l\'oubli, après quoi ses cendres retourneraient au sol et deviendraient des nutriments pour les forêts tropicales qui venaient de naître.', 4, 'Donjon d\'entraînement : Nuages souillés (Monday, Thursday, Sunday)'),
(468, 'Talisman en or de la rosée', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\n« Les mortels n\'aiment que trois choses : les sources éclairées par le clair de lune, la verdure pleine de vie et avoir un visage qui ne vieillit jamais... »\r\nL\'essence de la sagesse ne réside pas dans la maîtrise de toutes les connaissances ou la recherche du profit, mais dans le fait que, grâce à elle, toute créature peut atteindre la liberté qui lui permettra de se défaire des chaînes qui l\'emprisonnent.\r\nBien que le rugissement des tigres ne se fasse plus entendre dans la forêt, et que les gardiens intraitables se retirent dans les rêves des enfants... La nuit où chaque sceau d\'or secret est levé et où tous les rêves brillent comme de l\'ambre au clair de lune, peut-être qu\'elle et son peuple bien-aimé finiront par gagner véritablement leur liberté.', 5, 'Donjon d\'entraînement : Nuages souillés (Monday, Thursday, Sunday)'),
(469, 'Souvenir du jardin d\'oasis', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nLes gens avaient l\'habitude de dire que la maîtresse de la flore était la reine de la joie et du bonheur. De l\'eau douce qui jaillit de ses manches sont nées les oasis fleuries du milieu du désert. Les fleurs et les herbes suivaient ses pas, et le clair de lune lui ouvrait la voie.\r\nLorsque le ciel tournait le dos à la terre, la maîtresse de la flore étendait sa miséricorde à chacun des mortels, et ceux-ci reprirent conscience de leur propre existence et de la notion de bonheur et de la notion de plaisir, et purent ainsi retrouver une vie fastueuse et oisive.', 2, 'Donjon d\'entraînement : Obsession (Tuesday, Friday, Sunday)'),
(470, 'Gentillesse du jardin d\'oasis', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nLes textes anciens enfouis sous le sable évoquent une reine des fleurs qui est aussi la maîtresse des songes.\r\nElle prodiguait des rêves aux exilés, aux aliénés et aux simples d\'esprit, dans lesquels ils purent vivre de belles choses. Bien qu\'ils furent pauvres au point de ne posséder que le fer noir de leurs bols, les humains étaient entièrement sous le charme de la reine des fleurs. Ils se perdaient dans sa beauté et dans sa miséricorde sans limites.', 3, 'Donjon d\'entraînement : Obsession (Tuesday, Friday, Sunday)'),
(471, 'Deuil du jardin d\'oasis', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nLa reine des fleurs s\'entendait bien avec les djinns.\r\nCelle qui régnait sur les fleurs et les oasis s\'associa avec le roi du désert afin de construire Ay-Khanoum, la cité des amphithéâtres, ce qui signifiait « la cité de la fille de la lune » dans la langue ancienne des djinns.\r\nPlus tard, cependant, on dit que la reine des fleurs mourut sous la cruauté du soleil ardent et du sable, après quoi on n\'aperçut plus jamais de padisachidées violettes dans les oasis.\r\nQuand elle quitta ce monde, les tempêtes de sable se mirent à faire rage, les djinns pleurèrent, et le disque de pierres précieuses d\'une couleur argentée telle celle de la lune ne fut plus à même de prodiguer du bonheur comme il le faisait, et se mit à émettre une lueur inspirant la mélancolie.', 4, 'Donjon d\'entraînement : Obsession (Tuesday, Friday, Sunday)'),
(472, 'Vérité du jardin d\'oasis', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\n« Qui donc pourrait vénérer une divinité défunte ? »\r\nLa reine des fleurs était autrefois alliée à deux autres rois divins. En plus de la sagesse et du pouvoir, elle apporta à ses sujets joie et miséricorde.\r\nTout comme il ne peut y avoir uniquement un soleil brûlant et une pluie battante sur terre, pour le peuple de la reine des fleurs, la joie et l\'amour, le chant et la musique, le vin et l\'extravagance étaient tout aussi vitaux que la force et l\'intelligence.\r\nOn dit que les fleurs s\'épanouissent pour pouvoir se faner de la plus magnifique des manières, et c\'est la mort qui était précisément le dénouement que la reine des fleurs cherchait à obtenir, car la mort ajoute de l\'amertume à la joie disparue, et ce sentiment amer ne fait que s\'intensifier à travers des souvenirs sans fin.\r\nLe roi du désert, qui était un ignorant, ne sut jamais saisir le sens de sa pensée, il était simplement envoûté par sa tendresse et son charme incommensurables. Aussi erronée que soit cette affection sans bornes, elle faisait partie des calculs de la reine des fleurs.', 5, 'Donjon d\'entraînement : Obsession (Tuesday, Friday, Sunday)'),
(473, 'Dernier écho du pouvoir brûlant', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nJadis, le peuple du roi du désert sculpta des insignes royaux à l\'image d\'un scarabée robuste en l\'honneur du soleil rouge qui ponctue les aubes et les crépuscules ainsi que le temps qui ne cesse de s\'écouler.\r\nBien que le nom du roi ne soit plus jamais mentionné par quiconque, ces petits insignes royaux ont survécu jusqu\'à nos jours, laissant entrevoir la gloire des jours d\'autrefois.\r\nCes insignes voyagent aux quatre coins du pays avec les descendants des tribus érémites, contant silencieusement l\'histoire des temps anciens.', 2, 'Donjon d\'entraînement : Karma (Wednesday, Saturday, Sunday)'),
(474, 'Éclat subsistant du pouvoir brûlant', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nOrganiser une multitude d\'idées différentes en un tout cohérent et régner sur un nombre incalculable d\'âmes, telle est l\'essence du pouvoir.\r\nCependant, le pouvoir ne doit pas être cruel et violent, car il a un but beaucoup plus profond : créer une utopie où les gens n\'ont pas de soucis et où la persécution et l\'esclavage n\'ont pas leur place.\r\nC\'est exactement ce que le roi silencieux a vu à travers les secrets des cristaux, et exactement ce que sa partenaire lui enseigna dans le passé.\r\nLes insignes sombres en forme de scarabée et l\'intransigeance de la loi étaient les pierres fondatrices de l\'autorité du royaume.', 3, 'Donjon d\'entraînement : Karma (Wednesday, Saturday, Sunday)'),
(475, 'Rêve dominant du pouvoir brûlant', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nAutrefois, le clair de lune argenté et les sources turquoise décoraient cette belle oasis idyllique. Mais lorsque la reine des fleurs fana au milieu d\'une tempête de sable qui rendit même le soleil invisible, ce paradis doré où régnait l\'insouciance fut submergé par la mort.\r\nLes chants des tribus érémites racontent comment la lune argentée éclaira la robe écarlate d\'Al-Ahmar, le roi endeuillé, ce qui le fit sombrer dans la folie.\r\nDans une chanson triste, le roi solitaire, qui ne parvient pas à se défaire de ses souvenirs du passé, érige un rêve immortel au milieu du désert. C\'est un endroit où l\'eau des sources pures converge sans être perturbée, un lieu éclairé par la lumière d\'un soleil couchant éternel.', 4, 'Donjon d\'entraînement : Karma (Wednesday, Saturday, Sunday)'),
(476, 'Passé glorieux du pouvoir brûlant', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nÀ l\'époque où régnait l\'ignorant roi du désert, le pouvoir de ce dernier s\'étendait jusqu\'à chaque parcelle de terre, telle la lumière du soleil brûlant.\r\nD\'anciennes tablettes de pierre rapportent qu\'il transforma son pouvoir en sept rayons de lumière aveuglante ou sept piliers qu\'il accorda ensuite de manière égale et miséricordieuse à tous. Personne ne fut négligé, personne ne fut mécontent.\r\nC\'était un âge d\'or oublié, une époque où la reine des fleurs s\'était fondue dans les sables et où la maîtresse des plantes était déterminée à partir. Seuls les descendants du sable décidèrent de suivre leur divinité. Même si l\'ombre sinistre de la calamité se profilait, la puissance en laquelle ils avaient foi brillait comme l\'ambre et l\'or.\r\nMalgré tout, la calamité finit par s\'abattre, et les insignes de scarabée dorés furent ensevelis dans les profondeurs des sanctuaires et des palais, sous des piliers effondrés gravés d\'images d\'aigles et de taureaux.', 5, 'Donjon d\'entraînement : Karma (Wednesday, Saturday, Sunday)'),
(477, 'Fragment d\'un accord ancien', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nUne lyre aux touches pourries. Sa voix est bloquée, et elle ne peut pas être jouée ni entendue.\r\nOn dit que les descendants de la musique oubliée ont jadis laqué une lyre mécanique en bois avec de l\'or afin de faire revivre les magnifiques vestiges de l\'ancienne dynastie, essayant en vain de l\'associer aux golems silencieux...\r\nUn long moment passa, et la lyre souillée resta dans l\'étreinte du géant silencieux, caressée par les flots, ne jouant qu\'un air douloureux.', 2, 'Donjon d\'entraînement : Ruse robotique (Monday, Thursday, Sunday)'),
(478, 'Chapitre d\'un accord ancien', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nLes légendes disent que les survivants de cette ancienne dynastie ont jadis coulé une lyre en fer noir pour commémorer et faire le deuil.\r\nUne lyre moulée en fer noir ne peut pas être jouée. Pour faire le deuil de cette ancienne dynastie qui était autrefois si fière de sa musique dorée, ceux qui sont restés ont forgé cet instrument lourd au silence encore plus lourd.\r\nMais la lyre de fer noir reste aussi silencieuse que cette époque perdue.', 3, 'Donjon d\'entraînement : Ruse robotique (Monday, Thursday, Sunday)'),
(479, 'Mouvement d\'un accord ancien', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nDans ce grand désastre, le palais doré a coulé avec son trône, la musique a été brisée, les notes se sont transformées en silence...\r\nMais les vestiges de cet empire n\'ont jamais abandonné leur désir de salut et de surpasser le destin.\r\nPour sauver leur civilisation déclinante et leur désir de transcendance, la Troupe dorée a joué les mélodies les plus merveilleuses, incitant les enfants à offrir leurs précieuses âmes aux golems endormis.', 3, 'Donjon d\'entraînement : Ruse robotique (Monday, Thursday, Sunday)'),
(480, 'Écho d\'un accord ancien', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nLa grande symphonie du roi divin est composée d\'innombrables mélodies liées entre elles, mais sa thèse principale est submergée par le destin commun de la patrie de l\'eau.\r\nLes harmonies de ces années insouciantes et les sonates étaient autrefois la plus grande représentation de la civilisation et de l\'ordre, mais lorsque l\'empire prospère est descendu dans un terrain de jeu pour les tyrans, les despotes brutaux ont déchiré le lien avec la source sous le couvert de manteaux dorés, et la discorde s\'est finalement répandue à travers l\'empire...\r\nLa chanson originale du destin a continué à résonner sur une partition d\'or qui s\'estompe, imperturbable pour tous... jusqu\'à ce que tout soit dissous et que tout retourne au chaos.', 5, 'Donjon d\'entraînement : Ruse robotique (Monday, Thursday, Sunday)'),
(481, 'Résidu de rosée sacrée pure', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nLes légendes disent qu\'avant la naissance de la vie, il existait une mer primordiale dense. C\'est l\'origine la plus pure de toutes choses, et un jour, elle engloutira également toute vie... Et les choses qui n\'en sont pas issues seront filtrées, ne devenant que des détritus flottants.\r\n« L\'existence de détritus a aussi un sens... Quel genre de chose serait rejetée par la source de toutes choses qui avale toute vie ? », demanda le jeune accordeur au grand usurpateur, réfléchissant à cette énigme.', 2, 'Donjon d\'entraînement : Artisanat (Tuesday, Friday, Sunday)'),
(482, 'Sublimation de rosée sacrée pure', 'Matériau d\'élévation d\'arme', '\r\nMatériau d\'élévation d\'arme.\r\n« La vie qui est venue de la mer primordiale est inévitablement destinée à y retourner. », dit l\'accordeur Boethius. « Mais les humains ont d\'infinies possibilités de transcender, et même les eaux de la mer primordiale ne peuvent pas les dissoudre complètement...»\r\nL\'usurpateur arrogant a autrefois tenté de mélanger l\'ichor insoluble avec de l\'eau pure, afin de contenir la sagesse et les souvenirs, ce qui permettrait ainsi à ses sujets et serviteurs de se débarrasser de leur corps physique et d\'obtenir des vies indépendantes et éternelles.\r\nMais sectionner l\'âme du corps est une douleur qui ne peut être supportée par aucune vie ordinaire, et l\'ordre de l\'usurpateur déchira et brisa leurs âmes...\r\nL\'ichor fut alors teint en noir par les pleurs et les hurlements de tant d\'âmes, et il perdit son harmonie et sa sagesse, ne laissant que le chaos et la folie.', 3, 'Donjon d\'entraînement : Artisanat (Tuesday, Friday, Sunday)'),
(483, 'Source de rosée sacrée pure', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nC\'est une relique d\'une époque où les chevaliers de source erraient encore... Ces derniers disaient qu\'il y avait encore des esprits fidèles dans les sources et les vallées des montagnes où personne ne marchait, gardant ces sources pures et non polluées...\r\nCependant, le créateur de l\'ichor artificiel ne pouvait tolérer aucune discorde dans sa grande symphonie. On dit que partout où Boethius l\'accordeur est allé, le nectar naturel a été enterré, les aqueducs de l\'empire ont été construits et les esprits nés de l\'eau pure ont disparu sans laisser de trace...', 4, 'Donjon d\'entraînement : Artisanat (Tuesday, Friday, Sunday)'),
(484, 'Essence de rosée sacrée pure', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nUne solution dense et pure, dérivée et raffinée des gouttes de la vaste mer primordiale. L\'ancien roi divin croyait autrefois que cela apporterait la vie éternelle et une sagesse infinie, mais en écoutant attentivement... Il prophétisait doucement, faiblement, la fin ultime où toute chose se dissoudrait en une seule...\r\nC\'était autrefois la grande symphonie inachevée du roi divin... Un ichor doré sans limite et sans source, remplaçant les eaux primitives sauvages, et les glorieux sages immortels apportant une illumination semblable à un nectar dans les coins ignorants du monde.\r\nMais les illusions du roi divin ont finalement sombré dans les profondeurs de l\'abîme noir, et avec les ravages du temps, l\'ichor doré a finalement été corrodé par la soupe primordiale...\r\nL\'accordeur a fermé les yeux sur les mauvais présages et, soutenant son corps brisé, a continué à donner la vie à ses compatriotes selon les plans autrefois établis, afin que l\'ancien ordre puisse un jour revenir...\r\nAujourd\'hui encore, de nombreux poètes et peintres fous de Fontaine voient dans l\'apocalypse vécue par Boethius un rappel du cycle inéluctable du monde.', 5, 'Donjon d\'entraînement : Artisanat (Tuesday, Friday, Sunday)'),
(485, 'Gobelet brisé de la mer immaculée', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nLa légende dit que lorsque le premier usurpateur est venu à la mer primordiale, le premier souverain lui a donné un gobelet d\'eau.\r\nPlus tard, il devint le roi divin incontesté, et utilisa ce gobelet d\'eau pour affiner les mystérieuses gouttes de rosée, établissant un empire splendide et puissant.\r\nMais c\'est désormais une histoire ancienne, dont les personnages principaux dorment dans la phosphorescence des bancs de poissons des profondeurs.', 2, 'Donjon d\'entraînement : Artifice (Wednesday, Saturday, Sunday)'),
(486, 'Gobelet à vin de la mer immaculée', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nQuel genre d\'empire peut durer mille ans ? Après son retour du royaume des dragons, l\'usurpateur réfléchit à cette question.\r\nCombinant une pierre immortelle avec l\'essence d\'ichor extraite de l\'eau primordiale et sculptant le tout en une race aussi noire que le fer... Avec de la lithos arcanique pour peau et de l\'ichor pour sang, ne craignant plus jamais la malédiction du retour d\'un passé primitif...\r\nLe golem silencieux rêve encore du rêve de l\'empire... Sans savoir que cela fait plus de mille ans depuis sa chute...', 3, 'Donjon d\'entraînement : Artifice (Wednesday, Saturday, Sunday)'),
(487, 'Gobelet d\'argent de la mer immaculée', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nDans les années où le linceul doré couvrait la terre, l\'eau de source pure devait être dissimulée dans des ruisseaux de montagne cachés, et les chevaliers de source sont nés dans ces sanctuaires.\r\nLes chevaliers juraient sur leur armure d\'argent, afin de protéger les esprits nés de l\'eau pure, afin de protéger leurs semblables qui gardaient également foi en la seule source véritable, résistant au pouvoir doré omniprésent qui avait effacé le ciel et éclipsé le soleil.\r\nMais c\'est une légende ancienne... Peu de temps après l\'effondrement et l\'écroulement de l\'empire, les chevaliers qui résistaient à l\'usurpateur ont perdu leur armure, et leurs histoires ont été oubliées dans un enchevêtrement de légendes et de ballades.', 4, 'Donjon d\'entraînement : Artifice (Wednesday, Saturday, Sunday)'),
(488, 'Gobelet d\'or de la mer immaculée', 'Matériau d\'élévation d\'arme', 'Matériau d\'élévation d\'arme.\r\nLorsque l\'usurpateur de la ville engloutie a joué le dernier mouvement de la symphonie dorée, son musicien préféré a volé le gobelet d\'or débordant d\'ichor.\r\nChoqué par la trahison téméraire du roi divin, le musicien convoqua les derniers gardes et utilisa l\'insoluble ichor pour sceller le dragon sous la haute tour, avant de le faire sombrer dans l\'abîme avec la capitale.\r\nPar la suite, ce noble voleur a disparu silencieusement de l\'histoire, et seule la Troupe dorée se souvient fidèlement de sa prophétie.', 5, 'Donjon d\'entraînement : Artifice (Wednesday, Saturday, Sunday)'),
(489, 'Kusava flétri', 'Objet de quête', 'Le kusava vidé de son énergie. Il a l\'air flétri. Pourtant, la graine pleine de souvenirs précieux qu\'il contient est toujours vivante.', 1, ''),
(490, 'Brindille d\'argent', 'Objet de quête', 'Une brindille qui a été extraite du centre du monde.\r\nElle faisait partie d\'un arbre géant dont les racines s\'étendaient dans le monde entier. Bien qu\'elle ait été arrachée de celui-ci, leur lien demeure aussi fort qu\'autrefois. Tant que l\'arbre blanc géant qui symbolise le centre de ce monde ne dépérit pas, cette brindille conservera sa vitalité.\r\n\r\nLes arbres symbolisent la vie. Une histoire raconte que quelqu\'un a confié sa conscience aux branches de l\'arbre blanc avant son trépas pour que sa vie et ses espoirs ne soient pas perdus : les branches pousseraient et bourgeonneraient avec le soleil et la pluie pour finalement porter de nouveaux fruits.\r\nLes arbres symbolisent également la sagesse. Une légende raconte qu\'un sage suspendu à l\'envers à un arbre avait obtenu le savoir nécessaire pour écrire des runes et contrôler les mots sacrés. En parcourant le royaume qui se trouvait le long des racines, il a pu entrevoir le secret du cosmos.\r\nL\'ensemble des connaissances, tous souvenirs et expériences du monde circulent à travers les racines du grand arbre, comme des ruisseaux qui convergent pour former une rivière qui se jette dans la mer et dont l\'eau tombe des nuages. Ce cycle est celui de la vie.', 5, ''),
(491, 'Pierre de résonance Anémoculus', 'Consommable', 'Outil permettant de détecter les Anémoculus proches à Mondstadt.\r\nUn disque de pierre fabriqué par l\'homme, dont les caractéristiques se rapprochent de celles des Statues des Sept et capable de résonner avec les Anémoculus.\r\nCe type de pierre résonnant avec les Oculus est un dérivé maladroit des Statues des Sept construites autrefois à travers les terres. C\'est précisément cette infériorité qui explique cette soif de résonance vis-à-vis des statues représentant les Archons, en faisant un ustensile prisé des aventuriers et des croyants pour la collecte d\'Oculus.', 3, 'Synthèse'),
(492, 'Pierre de résonance Géoculus', 'Consommable', 'Outil permettant de détecter les Géoculus proches à Liyue.\r\nUn disque de pierre travaillé, dont le caractère mystérieux rappelle celui de la statue de l\'Archon Géo, et qui a le pouvoir d\'indiquer la présence de Géoculus. Les Oculus sont l\'incarnation de la force perdue de la statue ; les lui rendre a peut-être une signification importante sur ces terres qui vénèrent les Archons.', 3, 'Synthèse'),
(493, 'Pierre de résonance Électroculus', 'Consommable', 'Outil permettant de détecter les Électroculus proches à Inazuma.\r\nCet étrange appareil a un pouvoir qui résonne avec les Statues des Sept Électro. C\'est grâce à cela qu\'il peut détecter la présence d\'Électroculus.\r\nAu cours des années interminables, même les villes et les sculptures les plus majestueuses sont érodées par le vent et la pluie, et même le pouvoir des Statues des Sept se dissipe progressivement. Mais la puissance qui s\'en échappe se fond une fois de plus dans les Oculus, qui appellent en silence leurs Statues.', 3, 'Synthèse'),
(494, 'Pierre de résonance Dendroculus', 'Consommable', 'Utilisez cet objet à Sumeru pour localiser les Dendroculus proches de vous.\r\nUne amulette de protection qui produit des effets similaires aux Statues des Sept Dendro, ce qui explique qu\'elle soit attirée par les Dendroculus.\r\nDes plantes, des arbres, des lianes, ou encore des fleurs... Les formes de vie qui dépendent de l\'élément Dendro se présentent sous diverses formes. Bien qu\'elles diffèrent par leur apparence et divers autres aspects, elles sont toutes nées de la même terre et finiront toutes par y retourner.', 3, 'Synthèse'),
(495, 'Pierre de résonance Hydroculus', 'Consommable', 'Outil permettant de détecter les Hydroculus proches à Fontaine.\r\nCe talisman étrange qui possède des propriétés similaires aux Statues des Sept Hydro semble être capable de détecter le flux élémentaire des Hydroculus.\r\nLes oculus sont par nature des fragments de puissance dispersés appartenant aux Statues des Sept. Les ramener aux Statues est donc la chose raisonnable, juste et légale à faire.', 3, 'Synthèse'),
(496, 'Détecteur de trésors Anémo', 'Gadget', 'Outil permettant de détecter les coffres proches à Mondstadt.\r\nL\'aiguille indiquant la direction de ceux-ci ne fonctionne pas comme une aiguille traditionnelle : au lieu de réagir au champ magnétique, elle suit les flux des lignes énergétiques.', 4, 'Synthèse'),
(497, 'Détecteur de trésors Géo', 'Gadget', 'Outil permettant de détecter les coffres proches à Liyue.\r\nDe nombreux coffres sont éparpillés à travers les monts et vallées du riche Pays de la Roche.', 4, 'Synthèse'),
(498, 'Détecteur de trésors Électro', 'Gadget', 'Outil permettant de détecter les coffres proches à Inazuma.\r\nLe pays de l\'archipel du tonnerre a subi de nombreuses blessures au cours du temps. Les nombreux coffres au trésor qui parsèment les îles sont un symbole de sa renaissance.', 4, 'Synthèse'),
(499, 'Détecteur de trésors Dendro', 'Gadget', 'Outil permettant de détecter les coffres proches à Sumeru.\r\nPour certaines personnes, la vie est faite pour l\'exploration et les découvertes. Des coffres pleins à craquer les attendent à chacune de leurs destinations.', 4, 'Synthèse'),
(500, 'Détecteur de trésors Hydro', 'Gadget', 'Outil permettant de détecter les coffres proches à Fontaine.\r\nCes coffres dispersés ne sont la propriété légale d\'aucun individu ou organisation. Dans la plupart des cas, il est légal de revendiquer le contenu qui s\'y trouve.', 4, 'Synthèse'),
(501, 'Résine condensée', 'Consommable', 'Vous pouvez en détenir jusqu\'à 5 simultanément.\r\nRésine contenant une importante quantité d\'énergie, pouvant être utilisée à la place de la Résine originelle pour activer les Fleurs des lignes énergétiques et les Arbres pétrifiés afin d\'obtenir d\'importantes récompenses.\r\nLes anciens arbres argentés, au contact des lignes énergétiques, libèrent l\'énergie contenue dans leur résine, qui vient ensuite circuler à travers les canaux énergétiques, raffinant les matières qui y reposent et produisant parfois ce type de matière.', 4, 'Synthèse'),
(502, 'Sérénithéière', 'Gadget', 'Une théière offerte par Madame Ping qui semble servir de porte d\'entrée vers un autre monde...', 5, '');
